    }
  });

  // ÿ•ŸÉŸÖÿßŸÑ ÿ•ÿ¨ÿ±ÿßÿ° ÿ™ÿµÿ≠Ÿäÿ≠Ÿä
  router.post('/:id/complete', async (req, res) => {
    try {
      const actionId = parseInt(req.params.id);
      const { notes } = req.body;
      const userId = (req as any).user?.id || 1;

      const action = await storage.completeCorrectiveAction(actionId, userId, notes);
      res.json(action);
    } catch (error: any) {
      console.error('Error completing corrective action:', error);
      res.status(500).json({ message: 'ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÉŸÖÿßŸÑ ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ° ÿßŸÑÿ™ÿµÿ≠Ÿäÿ≠Ÿä' });
    }
  });

  return router;
}

export function createDataValidationRouter(storage: IStorage) {
  const router = Router();
  const dataValidator = getDataValidator(storage);

  // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿµÿ≠ÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
  router.post('/validate', async (req, res) => {
    try {
      const { table, data, isUpdate = false } = req.body;

      if (!table || !data) {
        return res.status(400).json({ message: 'ÿßÿ≥ŸÖ ÿßŸÑÿ¨ÿØŸàŸÑ ŸàÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÖÿ∑ŸÑŸàÿ®ÿ©' });
      }

      const result = await dataValidator.validateData(table, data, isUpdate);
      res.json(result);
    } catch (error: any) {
      console.error('Error validating data:', error);
      res.status(500).json({ message: 'ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™' });
    }
  });

  // ŸÅÿ≠ÿµ ÿ≥ŸÑÿßŸÖÿ© ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
  router.get('/database-integrity', async (req, res) => {
    try {
      const result = await dataValidator.validateDatabaseIntegrity();
      res.json(result);
    } catch (error: any) {
      console.error('Error checking database integrity:', error);
      res.status(500).json({ message: 'ÿÆÿ∑ÿ£ ŸÅŸä ŸÅÿ≠ÿµ ÿ≥ŸÑÿßŸÖÿ© ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™' });
    }
  });

  return router;
}

