  /**
   * ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿßŸÇÿ™ÿ±ÿßÿ≠ ŸÑŸÑÿ≠ŸÇŸÑ
   */
  private getSuggestion(rule: ValidationRule, value: any): string {
    switch (rule.rule_type) {
      case 'min':
        return `Value should be at least ${rule.parameters.min}`;
      case 'max':
        return `Value should be at most ${rule.parameters.max}`;
      case 'range':
        return `Value should be between ${rule.parameters.min} and ${rule.parameters.max}`;
      case 'pattern':
        return 'Please check the format';
      default:
        return 'Please review the value';
    }
  }

  /**
   * ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿßŸÇÿ™ÿ±ÿßÿ≠ ÿ®ÿßŸÑŸÑÿ∫ÿ© ÿßŸÑÿπÿ±ÿ®Ÿäÿ©
   */
  private getSuggestionAr(rule: ValidationRule, value: any): string {
    switch (rule.rule_type) {
      case 'min':
        return `Ÿäÿ¨ÿ® ÿ£ŸÜ ÿ™ŸÉŸàŸÜ ÿßŸÑŸÇŸäŸÖÿ© ÿπŸÑŸâ ÿßŸÑÿ£ŸÇŸÑ ${rule.parameters.min}`;
      case 'max':
        return `Ÿäÿ¨ÿ® ÿ£ŸÜ ÿ™ŸÉŸàŸÜ ÿßŸÑŸÇŸäŸÖÿ© ÿπŸÑŸâ ÿßŸÑÿ£ŸÉÿ´ÿ± ${rule.parameters.max}`;
      case 'range':
        return `Ÿäÿ¨ÿ® ÿ£ŸÜ ÿ™ŸÉŸàŸÜ ÿßŸÑŸÇŸäŸÖÿ© ÿ®ŸäŸÜ ${rule.parameters.min} Ÿà ${rule.parameters.max}`;
      case 'pattern':
        return 'Ÿäÿ±ÿ¨Ÿâ ŸÖÿ±ÿßÿ¨ÿπÿ© ÿßŸÑÿ™ŸÜÿ≥ŸäŸÇ';
      default:
        return 'Ÿäÿ±ÿ¨Ÿâ ŸÖÿ±ÿßÿ¨ÿπÿ© ÿßŸÑŸÇŸäŸÖÿ©';
    }
  }

  /**
   * ÿ•ŸÜÿ¥ÿßÿ° ÿ™ÿ≠ÿ∞Ÿäÿ± ŸÑŸÑÿ£ÿÆÿ∑ÿßÿ° ŸÅŸä ÿßŸÑÿ™ÿ≠ŸÇŸÇ
   */
  private async createValidationAlert(tableName: string, errors: ValidationError[]): Promise<void> {
    try {
      const criticalErrors = errors.filter(e => e.severity === 'critical');
      if (criticalErrors.length === 0) return;

      const alertManager = getAlertManager(this.storage);

      await alertManager.createAlert({
        title: `Data Validation Errors in ${tableName}`,
        title_ar: `ÿ£ÿÆÿ∑ÿßÿ° ŸÅŸä ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ®ŸäÿßŸÜÿßÿ™ ${tableName}`,
        message: `Found ${criticalErrors.length} critical validation errors`,
        message_ar: `ÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ${criticalErrors.length} ÿÆÿ∑ÿ£ ÿ≠ÿ±ÿ¨ ŸÅŸä ÿßŸÑÿ™ÿ≠ŸÇŸÇ`,
        type: 'system',
        category: 'error',
        severity: 'high',
        source: 'data_validator',
        source_id: tableName,
        context_data: { errors: criticalErrors },
        requires_action: true,
        target_roles: [1, 2] // ÿßŸÑÿ£ÿØŸÖŸÜ ŸàÿßŸÑŸÖÿØŸäÿ±ŸäŸÜ
      });

    } catch (error) {
      console.error('[DataValidator] ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿ™ÿ≠ÿ∞Ÿäÿ± ÿßŸÑÿ™ÿ≠ŸÇŸÇ:', error);
    }
  }

  /**
   * Validate roll creation against business invariants
   * INVARIANT B: Sum of roll weights ‚â§ ProductionOrder.final_quantity_kg + tolerance
   */
  async validateRollCreation(rollData: any): Promise<ValidationResult> {
    const errors: ValidationError[] = [];
    const warnings: ValidationWarning[] = [];

    try {
      // Get production order
      const productionOrder = await this.storage.getProductionOrderById(rollData.production_order_id);
      if (!productionOrder) {
        errors.push({
          field: 'production_order_id',
          message: 'Production order not found',
          message_ar: 'ÿ£ŸÖÿ± ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨ ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ',
          severity: 'high',
          rule_id: 'production_order_exists'
        });
        return { isValid: false, errors, warnings };
      }

      // Get existing rolls for this production order
      const existingRolls = await this.storage.getRollsByProductionOrder(rollData.production_order_id);
      const existingTotalWeight = existingRolls.reduce((sum, roll) => 
        sum + parseFloat(roll.weight_kg || '0'), 0);

      // Calculate remaining capacity
      const finalQuantity = parseFloat(productionOrder.final_quantity_kg || '0');
      const proposedWeight = parseFloat(rollData.weight_kg || '0');
      const newTotalWeight = existingTotalWeight + proposedWeight;

      // Get production settings for tolerance
      const settings = await this.storage.getProductionSettings();
      const tolerance = parseFloat(settings.overrun_tolerance_percent || '3') / 100;
      const maxAllowedWeight = finalQuantity * (1 + tolerance);

      // INVARIANT B: Check weight constraint
      if (newTotalWeight > maxAllowedWeight) {
        errors.push({
          field: 'weight_kg',
          message: `Roll weight exceeds production order limits. Current: ${existingTotalWeight}kg, Proposed: ${proposedWeight}kg, Max allowed: ${maxAllowedWeight}kg`,
          message_ar: `Ÿàÿ≤ŸÜ ÿßŸÑÿ±ŸàŸÑ Ÿäÿ™ÿ¨ÿßŸàÿ≤ ÿ≠ÿØŸàÿØ ÿ£ŸÖÿ± ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨. ÿßŸÑÿ≠ÿßŸÑŸä: ${existingTotalWeight} ŸÉŸäŸÑŸàÿå ÿßŸÑŸÖŸÇÿ™ÿ±ÿ≠: ${proposedWeight} ŸÉŸäŸÑŸàÿå ÿßŸÑÿ≠ÿØ ÿßŸÑÿ£ŸÇÿµŸâ: ${maxAllowedWeight} ŸÉŸäŸÑŸà`,
          severity: 'high',
          rule_id: 'roll_weight_constraint'
        });
      }

      // Warning if approaching limit (90% of max)
      if (newTotalWeight > maxAllowedWeight * 0.9 && errors.length === 0) {
        warnings.push({
          field: 'weight_kg',
          message: 'Roll weight approaching production order limits',
          message_ar: 'Ÿàÿ≤ŸÜ ÿßŸÑÿ±ŸàŸÑ ŸäŸÇÿ™ÿ±ÿ® ŸÖŸÜ ÿ≠ÿØŸàÿØ ÿ£ŸÖÿ± ÿßŸÑÿ•ŸÜÿ™ÿßÿ¨',
          suggestion: 'Consider reducing weight or creating additional production orders',
          suggestion_ar: 'ÿßŸÜÿ∏ÿ± ŸÅŸä ÿ™ŸÇŸÑŸäŸÑ ÿßŸÑŸàÿ≤ŸÜ ÿ£Ÿà ÿ•ŸÜÿ¥ÿßÿ° ÿ£ŸàÿßŸÖÿ± ÿ•ŸÜÿ™ÿßÿ¨ ÿ•ÿ∂ÿßŸÅŸäÿ©'
        });
      }

      return { isValid: errors.length === 0, errors, warnings };

    } catch (error) {
      console.error('[DataValidator] Error validating roll creation:', error);
      errors.push({
        field: 'system',
        message: 'System validation error',
        message_ar: 'ÿÆÿ∑ÿ£ ŸÅŸä ŸÜÿ∏ÿßŸÖ ÿßŸÑÿ™ÿ≠ŸÇŸÇ',
        severity: 'critical',
        rule_id: 'system_error'
      });
      return { isValid: false, errors, warnings };
    }
  }

  /**
   * ŸÅÿ≠ÿµ ÿ≥ŸÑÿßŸÖÿ© ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
   */
  async validateDatabaseIntegrity(): Promise<{
    isHealthy: boolean;
    issues: string[];
    recommendations: string[];
  }> {
    try {
      const issues: string[] = [];
      const recommendations: string[] = [];

      // ŸÅÿ≠ÿµ ÿßŸÑŸÖÿ±ÿßÿ¨ÿπ ÿßŸÑŸÖÿπÿ∑ŸÑÿ©
      // ÿ≥ŸÜÿ≠ÿ™ÿßÿ¨ ŸÑÿ•ÿ∂ÿßŸÅÿ© ÿßÿ≥ÿ™ÿπŸÑÿßŸÖÿßÿ™ ŸÑŸÑŸÅÿ≠ÿµ

      // ŸÅÿ≠ÿµ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖŸÉÿ±ÿ±ÿ©
      // ÿ≥ŸÜÿ≠ÿ™ÿßÿ¨ ŸÑÿ•ÿ∂ÿßŸÅÿ© ÿßÿ≥ÿ™ÿπŸÑÿßŸÖÿßÿ™ ŸÑŸÑŸÅÿ≠ÿµ

      // ŸÅÿ≠ÿµ ÿßŸÑŸÇŸäŸÖ ÿßŸÑŸÖŸÅŸÇŸàÿØÿ© ŸÅŸä ÿßŸÑÿ≠ŸÇŸàŸÑ ÿßŸÑŸÖÿ∑ŸÑŸàÿ®ÿ©
      // ÿ≥ŸÜÿ≠ÿ™ÿßÿ¨ ŸÑÿ•ÿ∂ÿßŸÅÿ© ÿßÿ≥ÿ™ÿπŸÑÿßŸÖÿßÿ™ ŸÑŸÑŸÅÿ≠ÿµ

      return {
        isHealthy: issues.length === 0,
        issues,
        recommendations
      };

    } catch (error) {
      console.error('[DataValidator] ÿÆÿ∑ÿ£ ŸÅŸä ŸÅÿ≠ÿµ ÿ≥ŸÑÿßŸÖÿ© ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™:', error);
      return {
        isHealthy: false,
        issues: ['ÿÆÿ∑ÿ£ ŸÅŸä ŸÅÿ≠ÿµ ÿ≥ŸÑÿßŸÖÿ© ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™'],
        recommendations: ['Ÿäÿ±ÿ¨Ÿâ ŸÖÿ±ÿßÿ¨ÿπÿ© ÿ≥ÿ¨ŸÑÿßÿ™ ÿßŸÑŸÜÿ∏ÿßŸÖ']
      };
    }
  }

  /**
   * ÿ•ÿ∂ÿßŸÅÿ© ŸÇÿßÿπÿØÿ© ÿ™ÿ≠ŸÇŸÇ ŸÖÿÆÿµÿµÿ©
   */
  addCustomRule(rule: ValidationRule): void {
    if (!this.validationRules.has(rule.table)) {
      this.validationRules.set(rule.table, []);
    }
    this.validationRules.get(rule.table)?.push(rule);
    console.log(`[DataValidator] ÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ŸÇÿßÿπÿØÿ© ÿ™ÿ≠ŸÇŸÇ ŸÖÿÆÿµÿµÿ©: ${rule.name_ar}`);
  }

  /**
   * ÿ•ÿ∂ÿßŸÅÿ© ŸÖÿØŸÇŸÇ ŸÖÿÆÿµÿµ
   */
  addCustomValidator(name: string, validator: Function): void {
    this.customValidators.set(name, validator);
    console.log(`[DataValidator] ÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ŸÖÿØŸÇŸÇ ŸÖÿÆÿµÿµ: ${name}`);
  }

  /**
   * CRITICAL: validateEntity - Main validation entry point for all database writes
   * This method MUST be called before every database insert/update operation
   * Enforces business rules, invariants, and data integrity constraints
   */
  async validateEntity(tableName: string, data: Record<string, any>, isUpdate: boolean = false): Promise<ValidationResult> {
    console.log(`[DataValidator] üîí Validating ${tableName} entity:`, { tableName, isUpdate, dataKeys: Object.keys(data) });

    try {
      // Call the main validation method with enhanced logging
      const result = await this.validateData(tableName, data, isUpdate);

      // Enhanced error logging for critical failures
      if (!result.isValid) {
        console.error(`[DataValidator] ‚ùå VALIDATION FAILED for ${tableName}:`, {
          errors: result.errors,
          warnings: result.warnings,
          data: data
        });
      } else {
        console.log(`[DataValidator] ‚úÖ Validation passed for ${tableName}`);
      }

      return result;
    } catch (error) {
      console.error(`[DataValidator] CRITICAL ERROR during ${tableName} validation:`, error);
      return {
        isValid: false,
        errors: [{
          field: '_system',
          message: 'Validation system error',
          message_ar: 'ÿÆÿ∑ÿ£ ŸÅŸä ŸÜÿ∏ÿßŸÖ ÿßŸÑÿ™ÿ≠ŸÇŸÇ',
          severity: 'critical',
          rule_id: 'system_error',
          value: error
        }],
        warnings: []
      };
    }
  }

  /**
   * CRITICAL: validateStatusTransition - Enforces valid state transitions
   * Prevents invalid status changes that could corrupt business workflow
   */
  async validateStatusTransition(tableName: string, currentStatus: string, newStatus: string, entityId: number): Promise<ValidationResult> {
    console.log(`[DataValidator] üîÑ Validating status transition for ${tableName}:`, { 
      entityId, currentStatus, newStatus 
    });

    const errors: ValidationError[] = [];
    const warnings: ValidationWarning[] = [];

    try {
      // Define valid status transitions by table
      const validTransitions: Record<string, Record<string, string[]>> = {
        orders: {
          'waiting': ['in_production', 'cancelled'],
          'in_production': ['completed', 'paused', 'cancelled'],
          'paused': ['in_production', 'cancelled'],
          'completed': [], // No transitions allowed from completed
          'cancelled': [] // No transitions allowed from cancelled
        },
        production_orders: {
          'pending': ['active', 'cancelled'],
          'active': ['completed', 'cancelled'],
          'completed': [], // No transitions allowed from completed
          'cancelled': [] // No transitions allowed from cancelled
        },
        rolls: {
          'film': ['printing', 'cutting'], // Can skip printing if not needed
          'printing': ['cutting'],
          'cutting': ['done'],
          'done': [] // No transitions allowed from done
        }
      };

      // Check if table has defined transitions
      const tableTransitions = validTransitions[tableName];
      if (!tableTransitions) {
        warnings.push({
          field: 'status',
          message: `No status transition rules defined for ${tableName}`,
          message_ar: `ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÇŸàÿßÿπÿØ ÿßŸÜÿ™ŸÇÿßŸÑ ÿ≠ÿßŸÑÿ© ŸÖÿ≠ÿØÿØÿ© ŸÑŸÄ ${tableName}`
        });
        return { isValid: true, errors, warnings };
      }

      // Check if current status exists
      const allowedFromCurrent = tableTransitions[currentStatus];
      if (!allowedFromCurrent) {
        errors.push({
          field: 'status',
          message: `Invalid current status: ${currentStatus}`,
          message_ar: `ÿ≠ÿßŸÑÿ© ÿ≠ÿßŸÑŸäÿ© ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©: ${currentStatus}`,
          severity: 'high',
          rule_id: 'invalid_current_status'
        });
        return { isValid: false, errors, warnings };
      }

      // Check if transition is allowed
      if (!allowedFromCurrent.includes(newStatus)) {
        errors.push({
          field: 'status',
          message: `Invalid status transition: ${currentStatus} ‚Üí ${newStatus}`,
          message_ar: `ÿßŸÜÿ™ŸÇÿßŸÑ ÿ≠ÿßŸÑÿ© ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠: ${currentStatus} ‚Üê ${newStatus}`,
          severity: 'high',
          rule_id: 'invalid_status_transition',
          value: { from: currentStatus, to: newStatus, allowed: allowedFromCurrent }
        });
        return { isValid: false, errors, warnings };
      }

      console.log(`[DataValidator] ‚úÖ Valid status transition: ${currentStatus} ‚Üí ${newStatus}`);
      return { isValid: true, errors, warnings };

    } catch (error) {
      console.error('[DataValidator] Error validating status transition:', error);
      errors.push({
        field: 'status',
        message: 'Error validating status transition',
        message_ar: 'ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÜÿ™ŸÇÿßŸÑ ÿßŸÑÿ≠ÿßŸÑÿ©',
        severity: 'critical',
        rule_id: 'transition_validation_error'
      });
      return { isValid: false, errors, warnings };
    }
  }
}

// ÿ•ŸÜÿ¥ÿßÿ° ŸÖÿ´ŸäŸÑ ŸÖÿ¥ÿ™ÿ±ŸÉ
let dataValidator: DataValidator | null = null;

export function getDataValidator(storage: IStorage): DataValidator {
  if (!dataValidator) {
    dataValidator = new DataValidator(storage);
  }
  return dataValidator;
}

export default DataValidator;

