  });

  const editForm = useForm<ConsumablePartFormData>({
    resolver: zodResolver(consumablePartSchema),
  });

  const transactionForm = useForm<BarcodeTransactionFormData>({
    resolver: zodResolver(barcodeTransactionSchema),
    defaultValues: {
