      part.code?.toLowerCase().includes(searchLower) ||
      part.type?.toLowerCase().includes(searchLower) ||
      String(part.part_id || '').toLowerCase().includes(searchLower) ||
      part.barcode?.toLowerCase().includes(searchLower)
