  const handleEdit = (part: any) => {
    setEditingPart(part);
    editForm.reset({
      code: part.code || "",
      type: part.type || "",
      status: part.status || "active",
      notes: part.notes || "",
      location: part.location || "",
      unit: part.unit || "ŸÇÿ∑ÿπÿ©",
