                    <div className="animate-pulse">جاري التحليل...</div>
                  ) : (
                    <>
                      {anomalies?.isAnomaly ? (
                        <XCircle className="h-16 w-16 text-red-500 mx-auto mb-2" />
                      ) : (
                        <CheckCircle className="h-16 w-16 text-green-500 mx-auto mb-2" />
                      )}
                      <p className="font-medium">
                        {anomalies?.isAnomaly
                          ? "تم اكتشاف شذوذ"
                          : "لا يوجد شذوذ"}
                      </p>
                      {anomalies?.isAnomaly && (
                        <Badge
                          variant={getSeverityColor(anomalies.severity)}
                          className="mt-2"
                        >
                          {anomalies.severity === "high"
                            ? "عالي"
                            : anomalies.severity === "medium"
                              ? "متوسط"
                              : "منخفض"}
                        </Badge>
                      )}
                    </>
                  )}
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle>درجة الشذوذ</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-3xl font-bold text-center">
                  {anomalyLoading ? "..." : anomalies?.anomalyScore.toFixed(2)}
                </div>
                <p className="text-center text-muted-foreground">من أصل 5.0</p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle>المقاييس المتأثرة</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-1">
                  {anomalies?.affectedMetrics?.map(
                    (metric: string, index: number) => (
                      <Badge
                        key={index}
                        variant="outline"
                        className="block text-center"
                      >
                        {metric}
                      </Badge>
                    ),
                  )}
                  {(!anomalies?.affectedMetrics ||
                    anomalies?.affectedMetrics?.length === 0) && (
                    <p className="text-center text-muted-foreground">لا يوجد</p>
                  )}
                </div>
              </CardContent>
            </Card>
          </div>

          {anomalies?.isAnomaly && (
            <Card>
              <CardHeader>
                <CardTitle>إجراءات مقترحة</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-2">
                  {anomalies?.recommendations?.map(
                    (rec: string, index: number) => (
                      <div
                        key={index}
                        className="flex items-center gap-2 p-2 bg-red-50 rounded-md"
                      >
                        <AlertTriangle className="h-4 w-4 text-red-600" />
                        <span className="text-sm">{rec}</span>
                      </div>
                    ),
                  )}
                </div>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        <TabsContent value="patterns" className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <Card>
              <CardHeader>
                <CardTitle>ساعات الذروة</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-6 gap-2">
                  {patterns?.peakHours?.map((hour: number) => (
                    <Badge key={hour} variant="default" className="text-center">
                      {hour}:00
                    </Badge>
                  ))}
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle>أفضل نوبات العمل</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-2">
                  {patterns?.optimalShifts?.map(
                    (shift: string, index: number) => (
                      <Badge
                        key={index}
                        variant="secondary"
                        className="block text-center"
                      >
                        النوبة {shift}
                      </Badge>
                    ),
                  )}
                </div>
              </CardContent>
            </Card>
          </div>

          <Card>
            <CardHeader>
              <CardTitle>رؤى الكفاءة</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                {patterns?.efficiencyInsights?.map(
                  (insight: string, index: number) => (
                    <div
                      key={index}
                      className="flex items-center gap-2 p-3 bg-green-50 rounded-md"
                    >
                      <BarChart3 className="h-4 w-4 text-green-600" />
                      <span className="text-sm">{insight}</span>
                    </div>
                  ),
                )}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="optimization" className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            <Card>
              <CardHeader>
                <CardTitle className="text-sm">السرعة المقترحة</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">
                  {optimizationLoading
                    ? "..."
                    : `${optimization?.recommendedSpeed}%`}
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle className="text-sm">درجة الحرارة</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">
                  {optimizationLoading
                    ? "..."
                    : `${optimization?.recommendedTemperature}°C`}
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle className="text-sm">الضغط المقترح</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">
                  {optimizationLoading
                    ? "..."
                    : `${optimization?.recommendedPressure} بار`}
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle className="text-sm">التحسن المتوقع</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-green-600">
                  {optimizationLoading
                    ? "..."
                    : `+${optimization?.expectedImprovement.toFixed(1)}%`}
                </div>
              </CardContent>
            </Card>
          </div>

          <Card>
            <CardHeader>
              <CardTitle>تطبيق التحسينات</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="flex items-center justify-between p-3 border rounded-md">
                  <span>مستوى الثقة في التحسينات</span>
                  <Badge variant="secondary">
                    {optimization
                      ? `${(optimization.confidence * 100).toFixed(0)}%`
                      : "..."}
                  </Badge>
                </div>

                <Button
                  onClick={() =>
                    optimization &&
                    applyOptimizationMutation.mutate(optimization)
                  }
                  disabled={
                    applyOptimizationMutation.isPending || !optimization
                  }
                  className="w-full gap-2"
                >
                  <Target className="h-4 w-4" />
                  تطبيق التحسينات المقترحة
                </Button>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/components/dashboard/Dashboar.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "../ui/card";
import { Badge } from "../ui/badge";
import { formatNumber, formatPercentage } from "../../lib/formatNumber";
import ErrorBoundary from "../ErrorBoundary";
import {
  ShoppingCart,
  Package,
  Users,
  AlertTriangle,
  TrendingUp,
  TrendingDown,
  Activity,
  Clock,
} from "lucide-react";

interface DashboardStat {
  label: string;
  value: string | number;
  change?: string;
  trend?: "up" | "down" | "neutral";
  icon: React.ReactNode;
  color: string;
}

function DashboardStatsContent() {
  const {
    data: stats = {},
    isLoading,
    error,
  } = useQuery({
    queryKey: ["/api/dashboard/stats"],
  });

  if (isLoading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
        {[...Array(4)].map((_, i) => (
          <Card key={i}>
            <CardContent className="p-6">
              <div className="animate-pulse">
                <div className="h-4 bg-gray-200 rounded mb-2"></div>
                <div className="h-8 bg-gray-200 rounded mb-2"></div>
                <div className="h-3 bg-gray-200 rounded"></div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  const dashboardStats: DashboardStat[] = [
    {
      label: "الطلبات النشطة",
      value: formatNumber((stats as any)?.activeOrders || 12),
      change: "+12% من الأسبوع الماضي",
      trend: "up",
      icon: <ShoppingCart className="w-6 h-6" />,
      color: "text-blue-600",
    },
    {
      label: "معدل الإنتاج",
      value: formatPercentage((stats as any)?.productionRate || 85),
      change:
        ((stats as any)?.productionRate || 85) >= 85
          ? "أداء ممتاز"
          : ((stats as any)?.productionRate || 85) >= 70
            ? "أداء جيد"
            : "يحتاج تحسين",
      trend:
        ((stats as any)?.productionRate || 85) >= 85
          ? "up"
          : ((stats as any)?.productionRate || 85) >= 70
            ? "neutral"
            : "down",
      icon: <TrendingUp className="w-6 h-6" />,
      color:
        ((stats as any)?.productionRate || 85) >= 85
          ? "text-green-600"
          : ((stats as any)?.productionRate || 85) >= 70
            ? "text-yellow-600"
            : "text-red-600",
    },
    {
      label: "العمال الحاضرين",
      value: `${formatNumber((stats as any)?.presentEmployees || 18)}/${formatNumber((stats as any)?.totalEmployees || 22)}`,
      change: `${formatPercentage(Math.round((((stats as any)?.presentEmployees || 18) / ((stats as any)?.totalEmployees || 22)) * 100))} معدل الحضور`,
      trend: "neutral",
      icon: <Users className="w-6 h-6" />,
      color: "text-purple-600",
    },
    {
      label: "تنبيهات الصيانة",
      value: formatNumber((stats as any)?.maintenanceAlerts || 2),
      change:
        ((stats as any)?.maintenanceAlerts || 2) > 0
          ? "يتطلب انتباه"
          : "جميع المكائن تعمل بشكل طبيعي",
      trend: ((stats as any)?.maintenanceAlerts || 2) > 0 ? "down" : "up",
      icon: <AlertTriangle className="w-6 h-6" />,
      color:
        ((stats as any)?.maintenanceAlerts || 2) > 0
          ? "text-red-600"
          : "text-green-600",
    },
  ];

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
      {dashboardStats.map((stat, index) => (
        <Card
          key={index}
          className="hover:shadow-md transition-shadow"
          data-testid={`stat-card-${index}`}
        >
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div className="flex-1">
                <p
                  className="text-sm font-medium text-gray-600 mb-1"
                  data-testid={`stat-label-${index}`}
                >
                  {stat.label}
                </p>
                <p
                  className={`text-2xl font-bold ${stat.color} mb-1`}
                  data-testid={`stat-value-${index}`}
                >
                  {stat.value}
                </p>
                <div className="flex items-center gap-1">
                  {stat.trend === "up" && (
                    <TrendingUp className="w-3 h-3 text-green-500" />
                  )}
                  {stat.trend === "down" && (
                    <TrendingDown className="w-3 h-3 text-red-500" />
                  )}
                  {stat.trend === "neutral" && (
                    <Activity className="w-3 h-3 text-gray-500" />
                  )}
                  <p
                    className="text-xs text-gray-500"
                    data-testid={`stat-change-${index}`}
                  >
                    {stat.change}
                  </p>
                </div>
              </div>
              <div className={`${stat.color} opacity-20`}>{stat.icon}</div>
            </div>

            {/* Additional indicators */}
            <div className="mt-3 flex justify-between items-center">
              <Badge
                variant={
                  stat.trend === "up"
                    ? "default"
                    : stat.trend === "down"
                      ? "destructive"
                      : "secondary"
                }
                className="text-xs"
                data-testid={`stat-badge-${index}`}
              >
                {stat.trend === "up"
                  ? "ممتاز"
                  : stat.trend === "down"
                    ? "يحتاج انتباه"
                    : "مستقر"}
              </Badge>
              <Clock className="w-3 h-3 text-gray-400" />
            </div>
          </CardContent>
        </Card>
      ))}
    </div>
  );
}

export default function DashboardStats() {
  return (
    <ErrorBoundary
      fallback="component"
      title="خطأ في تحميل الإحصائيات"
      description="تعذر تحميل إحصائيات لوحة التحكم. يرجى المحاولة مرة أخرى."
      onError={(error, errorInfo) => {
        console.error("Dashboard stats error:", error, errorInfo);
      }}
    >
      <DashboardStatsContent />
    </ErrorBoundary>
  );
}

