    if (!Array.isArray(data)) return [];

    return data
      .filter((item) => {
        // Status filter
        const statusMatch =
          statusFilter === "all" ||
          (statusFilter === "active" &&
            (item.status === "active" || item.status === "operational")) ||
          (statusFilter === "inactive" &&
            (item.status === "inactive" ||
              item.status === "down" ||
              item.status === "maintenance"));

