        const searchMatch =
          !quickSearch ||
          searchFields.some((field) => {
            const value = item[field];
            if (value === null || value === undefined) return false;
            return value
              .toString()
              .toLowerCase()
              .includes(quickSearch.toLowerCase());
          });

        return statusMatch && searchMatch;
      })
      .sort((a, b) => {
