  // جلب الإجراءات التصحيحية
  router.get("/", async (req, res) => {
    try {
      const alertId = req.query.alert_id
        ? parseInt(req.query.alert_id as string)
        : undefined;
      const actions = await storage.getCorrectiveActions(alertId);
      res.json(actions);
