      const action = await storage.completeCorrectiveAction(
        actionId,
        userId,
        notes,
      );
      res.json(action);
    } catch (error: any) {
      console.error("Error completing corrective action:", error);
      res.status(500).json({ message: "خطأ في إكمال الإجراء التصحيحي" });
    }
  });

  return router;
