      type: part.type || "",
      status: part.status || "active",
      notes: part.notes || "",
      location: part.location || "",
      unit: part.unit || "قطعة",
      current_quantity: part.current_quantity || 0,
      min_quantity: part.min_quantity || undefined,
      max_quantity: part.max_quantity || undefined,
      barcode: part.barcode || "",
