  };

  const handleDelete = (id: number) => {
    if (window.confirm("هل أنت متأكد من حذف هذه القطعة؟")) {
      deleteMutation.mutate(id);
    }
  };

  const onAddSubmit = (data: ConsumablePartFormData) => {
    createMutation.mutate(data);
