import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "../ui/card";
import { Button } from "../ui/button";
import { Input } from "../ui/input";
import { Label } from "../ui/label";
import { Badge } from "../ui/badge";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "../ui/dialog";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../ui/select";
import { Package, Scissors, Archive, Plus, QrCode, Play } from "lucide-react";
import { useToast } from "../../hooks/use-toast";

interface ProductionOrder {
  id: number;
  production_order: any; // Add proper type based on your schema
}

interface ProgressData {
  production_order: any;
  progress: {
    printing?: { completed: number; total: number };
    lamination?: { completed: number; total: number };
    cutting?: { completed: number; total: number };
    packaging?: { completed: number; total: number };
    film_weight?: number;
    film_percentage?: number;
    printed_weight?: number;
    printed_percentage?: number;
    cut_weight?: number;
    cut_percentage?: number;
    warehouse_weight?: number;
    warehouse_percentage?: number;
  };
  rolls: any[];
  warehouse_receipts: any[];
}

interface AvailableCut {
  id: string;
  name: string;
}

export default function OrderProgress() {
  const [selectedProductionOrderId, setSelectedProductionOrderId] = useState<
    number | null
  >(null);
  const [warehouseDialogOpen, setWarehouseDialogOpen] = useState(false);
  const [receiptData, setReceiptData] = useState({
    production_order_id: 0,
    cut_id: "",
    received_weight_kg: "",
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch all production orders
  const { data: productionOrders = [] } = useQuery<ProductionOrder[]>({
    queryKey: ["/api/production-orders"],
    refetchInterval: false, // Disabled polling - use manual refresh
    staleTime: 5 * 60 * 1000, // 5 minutes stale time
    gcTime: 10 * 60 * 1000, // 10 minutes garbage collection
    refetchOnWindowFocus: false, // Prevent unnecessary refetches
  });

  // Fetch progress for selected production order
  const { data: progress, isLoading: progressLoading } = useQuery<ProgressData>(
    {
      queryKey: ["/api/production/order-progress", selectedProductionOrderId],
      enabled: !!selectedProductionOrderId,
      refetchInterval: false, // Disabled polling - use manual refresh
      staleTime: 3 * 60 * 1000, // 3 minutes stale time
      gcTime: 5 * 60 * 1000, // 5 minutes garbage collection
      refetchOnWindowFocus: false, // Prevent unnecessary refetches
    },
  );

  // Fetch available cuts for warehouse receipt
  const { data: availableCuts = [] } = useQuery<AvailableCut[]>({
    queryKey: ["/api/cuts/available"],
    enabled: warehouseDialogOpen,
  });

  const warehouseReceiptMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await fetch("/api/warehouse/receipts", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "فشل في تسجيل الاستلام");
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "تم الاستلام بنجاح",
        description: "تم تسجيل استلام المستودع",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/warehouse/receipts"] });
      queryClient.invalidateQueries({
        queryKey: ["/api/production/order-progress"],
      });
      setWarehouseDialogOpen(false);
      setReceiptData({
        production_order_id: 0,
        cut_id: "",
        received_weight_kg: "",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "خطأ",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleWarehouseReceipt = () => {
    if (!receiptData.production_order_id || !receiptData.received_weight_kg) {
      toast({
        title: "خطأ",
        description: "يرجى ملء جميع الحقول المطلوبة",
        variant: "destructive",
      });
      return;
    }

    warehouseReceiptMutation.mutate({
      production_order_id: receiptData.production_order_id,
      cut_id: receiptData.cut_id ? parseInt(receiptData.cut_id) : null,
      received_weight_kg: parseFloat(receiptData.received_weight_kg),
    });
  };

  return (
    <div className="space-y-6">
      {/* Job Order Selection */}
      <Card>
        <CardHeader>
          <CardTitle>اختيار أمر الإنتاج</CardTitle>
        </CardHeader>
        <CardContent>
          <Select
            value={selectedProductionOrderId?.toString() ?? ""}
            onValueChange={(value) =>
              setSelectedProductionOrderId(parseInt(value))
            }
          >
            <SelectTrigger data-testid="select-job-order">
              <SelectValue placeholder="اختر أمر الإنتاج لمتابعة التقدم" />
            </SelectTrigger>
            <SelectContent>
              {productionOrders
                .filter((order: any) => order.status === "in_production")
                .map((order: any) => (
                  <SelectItem key={order.id} value={order.id.toString()}>
                    {order.production_order_number} - {order.quantity_required}{" "}
                    كجم
                  </SelectItem>
                ))}
            </SelectContent>
          </Select>
        </CardContent>
      </Card>

      {/* Progress Display */}
      {selectedProductionOrderId && progress && (
        <div className="space-y-4">
          {/* Progress Summary */}
          <Card>
            <CardHeader>
              <CardTitle>
                تقدم الطلب -{" "}
                {progress.production_order?.production_order_number}
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div className="text-center">
                  <Package className="h-8 w-8 mx-auto mb-2 text-blue-500" />
                  <p className="text-sm text-gray-600">فيلم</p>
                  <p className="font-bold text-lg">
                    {progress.progress?.film_weight?.toFixed(2) || 0} كجم
                  </p>
                  <div className="w-full bg-gray-200 rounded-full h-2 mt-2">
                    <div
                      className="bg-blue-500 h-2 rounded-full transition-all duration-300"
                      style={{
                        width: `${Math.min(progress.progress?.film_percentage || 0, 100)}%`,
                      }}
                    ></div>
                  </div>
                </div>

                <div className="text-center">
                  <Play className="h-8 w-8 mx-auto mb-2 text-green-500" />
                  <p className="text-sm text-gray-600">طباعة</p>
                  <p className="font-bold text-lg">
                    {progress.progress?.printed_weight?.toFixed(2) || 0} كجم
                  </p>
                  <div className="w-full bg-gray-200 rounded-full h-2 mt-2">
                    <div
                      className="bg-green-500 h-2 rounded-full transition-all duration-300"
                      style={{
                        width: `${Math.min(progress.progress?.printed_percentage || 0, 100)}%`,
                      }}
                    ></div>
                  </div>
                </div>

                <div className="text-center">
                  <Scissors className="h-8 w-8 mx-auto mb-2 text-orange-500" />
                  <p className="text-sm text-gray-600">تقطيع</p>
                  <p className="font-bold text-lg">
                    {progress.progress?.cut_weight?.toFixed(2) || 0} كجم
                  </p>
                  <div className="w-full bg-gray-200 rounded-full h-2 mt-2">
                    <div
                      className="bg-orange-500 h-2 rounded-full transition-all duration-300"
                      style={{
                        width: `${Math.min(progress.progress?.cut_percentage || 0, 100)}%`,
                      }}
                    ></div>
                  </div>
                </div>

                <div className="text-center">
                  <Archive className="h-8 w-8 mx-auto mb-2 text-purple-500" />
                  <p className="text-sm text-gray-600">مستودع</p>
                  <p className="font-bold text-lg">
                    {progress.progress?.warehouse_weight?.toFixed(2) || 0} كجم
                  </p>
                  <div className="w-full bg-gray-200 rounded-full h-2 mt-2">
                    <div
                      className="bg-purple-500 h-2 rounded-full transition-all duration-300"
                      style={{
                        width: `${Math.min(progress.progress?.warehouse_percentage || 0, 100)}%`,
                      }}
                    ></div>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Rolls Details */}
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle>الرولات</CardTitle>
                <Dialog
                  open={warehouseDialogOpen}
                  onOpenChange={setWarehouseDialogOpen}
                >
                  <DialogTrigger asChild>
                    <Button
                      onClick={() =>
                        setReceiptData({
                          ...receiptData,
                          production_order_id: selectedProductionOrderId,
                        })
                      }
                    >
                      <Plus className="h-4 w-4 mr-2" />
                      استلام مستودع
                    </Button>
                  </DialogTrigger>
                  <DialogContent className="max-w-md">
                    <DialogHeader>
                      <DialogTitle>تسجيل استلام المستودع</DialogTitle>
                      <DialogDescription>
                        تسجيل استلام المواد في المستودع مع تحديد الوزن المستلم
                      </DialogDescription>
                    </DialogHeader>
                    <div className="space-y-4">
                      <div className="space-y-2">
                        <Label>الوزن المستلم (كجم) *</Label>
                        <Input
                          type="number"
                          step="0.1"
                          value={receiptData.received_weight_kg}
                          onChange={(e) =>
                            setReceiptData({
                              ...receiptData,
                              received_weight_kg: e.target.value,
                            })
                          }
                          placeholder="45.2"
                          className="text-right"
                          data-testid="input-received-weight"
                        />
                      </div>

                      <div className="flex justify-end space-x-3 space-x-reverse pt-4">
                        <Button
                          type="button"
                          variant="outline"
                          onClick={() => setWarehouseDialogOpen(false)}
                          disabled={warehouseReceiptMutation.isPending}
                        >
                          إلغاء
                        </Button>
                        <Button
                          onClick={handleWarehouseReceipt}
                          disabled={warehouseReceiptMutation.isPending}
                          data-testid="button-confirm-receipt"
                        >
                          {warehouseReceiptMutation.isPending
                            ? "جاري التسجيل..."
                            : "تسجيل الاستلام"}
                        </Button>
                      </div>
                    </div>
                  </DialogContent>
                </Dialog>
              </div>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                {progress.rolls?.map((roll: any) => (
                  <div
                    key={roll.id}
                    className="flex items-center justify-between p-3 border rounded-lg"
                  >
                    <div className="flex items-center space-x-3 space-x-reverse">
                      <QrCode className="h-5 w-5 text-gray-400" />
                      <div>
                        <p
                          className="font-medium"
                          data-testid={`text-roll-${roll.id}`}
                        >
                          {roll.roll_number}
                        </p>
                        <p className="text-sm text-gray-500">
                          {roll.weight_kg?.toFixed(2)} كجم - {roll.machine_id}
                        </p>
                      </div>
                    </div>

                    <div className="flex items-center space-x-2 space-x-reverse">
                      <Badge
                        variant={
                          roll.stage === "film"
                            ? "secondary"
                            : roll.stage === "printing"
                              ? "default"
                              : "outline"
                        }
                      >
                        {roll.stage === "film"
                          ? "فيلم"
                          : roll.stage === "printing"
                            ? "مطبوع"
                            : "مقطع"}
                      </Badge>

                      {roll.printed_at && (
                        <span className="text-xs text-gray-400">
                          طُبع:{" "}
                          {new Date(roll.printed_at).toLocaleDateString("ar")}
                        </span>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>

          {/* Warehouse Receipts */}
          {progress.warehouse_receipts?.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle>إيصالات المستودع</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  {progress.warehouse_receipts.map((receipt: any) => (
                    <div
                      key={receipt.id}
                      className="flex items-center justify-between p-3 border rounded-lg"
                    >
                      <div>
                        <p className="font-medium">استلام #{receipt.id}</p>
                        <p className="text-sm text-gray-500">
                          {receipt.received_weight_kg?.toFixed(2)} كجم
                        </p>
                      </div>
                      <div className="text-xs text-gray-400">
                        {new Date(receipt.created_at).toLocaleDateString("ar")}
                      </div>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          )}
        </div>
      )}

      {progressLoading && (
        <Card>
          <CardContent className="p-6 text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto"></div>
            <p className="mt-2 text-sm text-gray-500">جاري تحميل التقدم...</p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/hr/PerformanceRevi.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardHeader, CardTitle, CardContent } from "../ui/card";
import { Button } from "../ui/button";
import { Badge } from "../ui/badge";
import { Progress } from "../ui/progress";
import { formatNumber, formatPercentage } from "../../lib/formatNumber";
import {
  Target,
  Star,
  TrendingUp,
  Calendar,
  User,
  Plus,
  BarChart3,
  Award,
} from "lucide-react";

interface PerformanceReview {
  id: number;
  employee_id: number;
  review_period_start: string;
  review_period_end: string;
  reviewer_id: number;
  review_type: "annual" | "quarterly" | "project_based" | "probation";
  status: "draft" | "in_progress" | "completed" | "approved";
  overall_score?: number;
  overall_rating?:
    | "excellent"
    | "very_good"
    | "good"
    | "needs_improvement"
    | "unsatisfactory";
  goals_for_next_period?: string;
  development_plan?: string;
  reviewer_comments?: string;
  employee_comments?: string;
  created_at: string;
  updated_at?: string;
}

interface PerformanceCriteria {
  id: number;
  name: string;
  name_ar: string;
  description?: string;
  description_ar?: string;
  weight: number;
  is_active: boolean;
  category: string;
}

export default function PerformanceReviews() {
  const [selectedReview, setSelectedReview] = useState<number | null>(null);

  const { data: reviews = [], isLoading: reviewsLoading } = useQuery<
    PerformanceReview[]
  >({
    queryKey: ["/api/hr/performance-reviews"],
    initialData: [],
  });

  const { data: criteria = [], isLoading: criteriaLoading } = useQuery<
    PerformanceCriteria[]
  >({
    queryKey: ["/api/hr/performance-criteria"],
    initialData: [],
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case "completed":
        return "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200";
      case "in_progress":
        return "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200";
      case "draft":
        return "bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200";
      case "approved":
        return "bg-emerald-100 text-emerald-800 dark:bg-emerald-900 dark:text-emerald-200";
      default:
        return "bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200";
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case "completed":
        return "مكتمل";
      case "in_progress":
        return "قيد المراجعة";
      case "draft":
        return "مسودة";
      case "approved":
        return "معتمد";
      default:
        return status;
    }
  };

  const getRatingColor = (rating: string) => {
    switch (rating) {
      case "excellent":
        return "text-green-600 dark:text-green-400";
      case "very_good":
        return "text-blue-600 dark:text-blue-400";
      case "good":
        return "text-yellow-600 dark:text-yellow-400";
      case "needs_improvement":
        return "text-orange-600 dark:text-orange-400";
      case "unsatisfactory":
        return "text-red-600 dark:text-red-400";
      default:
        return "text-gray-600 dark:text-gray-400";
    }
  };

  const getRatingText = (rating: string) => {
    switch (rating) {
      case "excellent":
        return "ممتاز";
      case "very_good":
        return "جيد جداً";
      case "good":
        return "جيد";
      case "needs_improvement":
        return "يحتاج تحسين";
      case "unsatisfactory":
        return "غير مرضي";
      default:
        return rating;
    }
  };

  const getReviewTypeText = (type: string) => {
    switch (type) {
      case "annual":
        return "تقييم سنوي";
      case "quarterly":
        return "تقييم ربع سنوي";
      case "project_based":
        return "تقييم مشروع";
      case "probation":
        return "تقييم فترة تجريبية";
      default:
        return type;
    }
  };

  const getScoreColor = (score: number) => {
    if (score >= 90) return "text-green-600 dark:text-green-400";
    if (score >= 80) return "text-blue-600 dark:text-blue-400";
    if (score >= 70) return "text-yellow-600 dark:text-yellow-400";
    if (score >= 60) return "text-orange-600 dark:text-orange-400";
    return "text-red-600 dark:text-red-400";
  };

  const averageScore =
    reviews.length > 0
      ? parseFloat(
          (
            reviews
              .filter((r) => r.overall_score)
              .reduce((sum, r) => sum + (r.overall_score || 0), 0) /
            reviews.filter((r) => r.overall_score).length
          ).toFixed(1),
        )
      : 0;

  if (reviewsLoading || criteriaLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
          <p className="text-gray-600 dark:text-gray-400 mt-2">
            جاري تحميل تقييمات الأداء...
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
            نظام تقييم الأداء
          </h2>
          <p className="text-gray-600 dark:text-gray-300">
            متابعة وتقييم أداء الموظفين بشكل دوري ومنهجي
          </p>
        </div>
        <Button className="bg-green-600 hover:bg-green-700 text-white">
          <Plus className="w-4 h-4 ml-2" />
          تقييم جديد
        </Button>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 dark:text-gray-400">
                  إجمالي التقييمات
                </p>
                <p className="text-2xl font-bold text-gray-900 dark:text-white">
                  {formatNumber(reviews.length)}
                </p>
              </div>
              <Target className="w-8 h-8 text-blue-600" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 dark:text-gray-400">
                  المتوسط العام
                </p>
                <p
                  className={`text-2xl font-bold ${getScoreColor(averageScore)}`}
                >
                  {formatPercentage(averageScore)}
                </p>
              </div>
              <BarChart3 className="w-8 h-8 text-green-600" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 dark:text-gray-400">
                  التقييمات المكتملة
                </p>
                <p className="text-2xl font-bold text-gray-900 dark:text-white">
                  {formatNumber(
                    reviews.filter(
                      (r) =>
                        r.status === "completed" || r.status === "approved",
                    ).length,
                  )}
                </p>
              </div>
              <Award className="w-8 h-8 text-purple-600" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600 dark:text-gray-400">
                  قيد المراجعة
                </p>
                <p className="text-2xl font-bold text-gray-900 dark:text-white">
                  {formatNumber(
                    reviews.filter((r) => r.status === "in_progress").length,
                  )}
                </p>
              </div>
              <TrendingUp className="w-8 h-8 text-orange-600" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Performance Criteria */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Star className="w-5 h-5" />
            معايير التقييم النشطة
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {criteria.map((criterion) => (
              <div
                key={criterion.id}
                className="p-4 border rounded-lg bg-gray-50 dark:bg-gray-800"
              >
                <div className="flex justify-between items-start mb-2">
                  <h4 className="font-medium">
                    {criterion.name_ar || criterion.name}
                  </h4>
                  <Badge variant="outline">
                    {formatPercentage(criterion.weight)}
                  </Badge>
                </div>
                <p className="text-sm text-gray-600 dark:text-gray-400">
                  {criterion.description_ar ||
                    criterion.description ||
                    "لا يوجد وصف"}
                </p>
                <div className="mt-2">
                  <Badge variant="secondary" className="text-xs">
                    {criterion.category}
                  </Badge>
                </div>
              </div>
            ))}
          </div>
          {criteria.length === 0 && (
            <div className="text-center py-8">
              <Star className="w-12 h-12 text-gray-400 mx-auto mb-3" />
              <p className="text-gray-600 dark:text-gray-400">
                لا توجد معايير تقييم محددة
              </p>
              <Button variant="outline" className="mt-2">
                إضافة معايير التقييم
              </Button>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Reviews List */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {reviews.map((review) => (
          <Card key={review.id} className="hover:shadow-lg transition-shadow">
            <CardHeader>
              <div className="flex justify-between items-start">
                <div>
                  <CardTitle className="text-lg mb-1">
                    {getReviewTypeText(review.review_type)}
                  </CardTitle>
                  <div className="flex items-center gap-2 text-sm text-gray-600 dark:text-gray-400">
                    <User className="w-4 h-4" />
                    <span>موظف رقم {review.employee_id}</span>
                  </div>
                </div>
                <Badge className={getStatusColor(review.status)}>
                  {getStatusText(review.status)}
                </Badge>
              </div>
            </CardHeader>

            <CardContent className="space-y-4">
              <div className="grid grid-cols-2 gap-4 text-sm">
                <div>
                  <p className="text-gray-600 dark:text-gray-400">
                    فترة التقييم
                  </p>
                  <p className="font-medium">
                    {new Date(review.review_period_start).toLocaleDateString(
                      "ar",
                    )}{" "}
                    -
                    {new Date(review.review_period_end).toLocaleDateString(
                      "ar",
                    )}
                  </p>
                </div>
                <div>
                  <p className="text-gray-600 dark:text-gray-400">المقيم</p>
                  <p className="font-medium">مدير رقم {review.reviewer_id}</p>
                </div>
              </div>

              {review.overall_score && (
                <div className="space-y-2">
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-600 dark:text-gray-400">
                      النتيجة الإجمالية
                    </span>
                    <span
                      className={`font-bold ${getScoreColor(review.overall_score)}`}
                    >
                      {formatPercentage(review.overall_score)}
                    </span>
                  </div>
                  <Progress value={review.overall_score} className="h-2" />
                </div>
              )}

              {review.overall_rating && (
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600 dark:text-gray-400">
                    التقدير العام
                  </span>
                  <Badge
                    className={`${getRatingColor(review.overall_rating)} bg-transparent border`}
                  >
                    {getRatingText(review.overall_rating)}
                  </Badge>
                </div>
              )}

              <div className="flex items-center gap-2 text-xs text-gray-500 dark:text-gray-400">
                <Calendar className="w-3 h-3" />
                <span>
                  تم الإنشاء:{" "}
                  {new Date(review.created_at).toLocaleDateString("ar")}
                </span>
              </div>

              <div className="flex gap-2 pt-2">
                <Button
                  size="sm"
                  className="flex-1"
                  onClick={() => setSelectedReview(review.id)}
                >
                  عرض التفاصيل
                </Button>
                <Button size="sm" variant="outline" className="flex-1">
                  تحرير
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Empty State */}
      {reviews.length === 0 && (
        <Card>
          <CardContent className="p-12 text-center">
            <Target className="w-16 h-16 text-gray-400 mx-auto mb-4" />
            <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
              لا توجد تقييمات أداء
            </h3>
            <p className="text-gray-600 dark:text-gray-400 mb-4">
              ابدأ بإنشاء تقييم أداء جديد لتتبع أداء فريقك
            </p>
            <Button className="bg-green-600 hover:bg-green-700 text-white">
              <Plus className="w-4 h-4 ml-2" />
              إنشاء تقييم جديد
            </Button>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/ai/AIAssistant.tsx.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "../ui/card";
import { Button } from "../ui/button";
import { Input } from "../ui/input";
import { ScrollArea } from "../ui/scroll-area";
import { Badge } from "../ui/badge";
import { useToast } from "../../hooks/use-toast";
import { useAuth } from "../../hooks/use-auth";
import { apiRequest } from "../../lib/queryClient";
import ErrorBoundary from "../ErrorBoundary";
import {
  Bot,
  User,
  Send,
  Mic,
  Volume2,
  Bell,
  TrendingUp,
  Settings,
} from "lucide-react";
import { generateMessageId } from "../../../../shared/id-generator";

interface Message {
  id: string;
  type: "user" | "assistant";
  content: string;
  timestamp: Date;
}

export default function AIAssistant() {
  const [messages, setMessages] = useState<Message[]>([
    {
      id: "1",
      type: "assistant",
      content: `👋 **مرحباً! أنا مساعدك الذكي المطور**`,
      timestamp: new Date(),
    },
  ]);
  const [inputValue, setInputValue] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const { toast } = useToast();
  const { user } = useAuth();

  // إجراءات سريعة للمساعد الذكي
  const quickActions = [
    {
      label: "سجل عميل",
      icon: User,
      command: "سجل عميل جديد",
      description: "إضافة عميل جديد",
    },
    {
      label: "أضف منتج",
      icon: TrendingUp,
      command: "أضف منتج جديد",
      description: "إضافة منتج للعميل",
    },
    {
      label: "حالة الإنتاج",
      icon: Settings,
      command: "ما حالة الإنتاج؟",
      description: "إحصائيات الإنتاج",
    },
    {
      label: "مساعدة",
      icon: Bell,
      command: "مساعدة",
      description: "دليل الاستخدام",
    },
  ];

  const sendMessageMutation = useMutation({
    mutationFn: async (message: string) => {
      if (!user?.id) {
        throw new Error("يجب تسجيل الدخول لاستخدام المساعد الذكي");
      }

      setIsTyping(true);

      const response = await fetch("/api/ai/chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          message,
          context: "factory_operations",
          userId: user.id,
        }),
      });
      return response.json();
    },
    onSuccess: (response: any) => {
      setIsTyping(false);

      // Extract the message from different possible response fields
      const content = 
        response.reply || 
        response.message || 
        response.clarificationQuestion || 
        response.confirmationMessage || 
        "عذراً، لم أستطع معالجة طلبك في الوقت الحالي.";

      const assistantMessage: Message = {
        id: generateMessageId(),
        type: "assistant",
        content,
        timestamp: new Date(),
      };
      setMessages((prev) => [...prev, assistantMessage]);
    },
    onError: () => {
      setIsTyping(false);
      const errorMessage: Message = {
        id: generateMessageId(),
        type: "assistant",
        content: "عذراً، حدث خطأ في الاتصال. يرجى المحاولة مرة أخرى.",
        timestamp: new Date(),
      };
      setMessages((prev) => [...prev, errorMessage]);
      toast({
        title: "خطأ في المساعد الذكي",
        description: "لا يمكن الوصول لخدمة المساعد الذكي حالياً",
        variant: "destructive",
      });
    },
  });

  const handleSendMessage = (message?: string) => {
    const messageToSend = message || inputValue.trim();
    if (!messageToSend) return;

    const userMessage: Message = {
      id: generateMessageId(),
      type: "user",
      content: messageToSend,
      timestamp: new Date(),
    };

    setMessages((prev) => [...prev, userMessage]);
    sendMessageMutation.mutate(messageToSend);
    setInputValue("");
  };

  const handleQuickAction = (command: string) => {
    handleSendMessage(command);
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const toggleVoiceInput = () => {
    toast({
      title: "الإدخال الصوتي",
      description: "استخدم المساعد الصوتي في الصفحة الرئيسية للإدخال الصوتي المتقدم",
    });
  };

  const speakMessage = (content: string) => {
    if ("speechSynthesis" in window) {
      const utterance = new SpeechSynthesisUtterance(content);
      utterance.lang = "ar-SA";
      speechSynthesis.speak(utterance);
    }
  };

  return (
    <Card className="h-96">
      <CardHeader className="pb-3">
        <CardTitle className="flex items-center gap-2">
          <Bot className="w-5 h-5 text-blue-500" />
          المساعد الذكي
          <Badge variant="secondary" className="mr-auto">
            نشط
          </Badge>
        </CardTitle>
      </CardHeader>
      <CardContent className="p-0">
        <ScrollArea className="h-64 p-4">
          <div className="space-y-4">
            {messages.map((message) => (
              <div
                key={message.id}
                className={`flex gap-3 ${message.type === "user" ? "justify-end" : "justify-start"}`}
              >
                <div
                  className={`flex gap-2 max-w-[80%] ${message.type === "user" ? "flex-row-reverse" : "flex-row"}`}
                >
                  <div
                    className={`w-8 h-8 rounded-full flex items-center justify-center ${
                      message.type === "user" ? "bg-blue-100" : "bg-green-100"
                    }`}
                  >
                    {message.type === "user" ? (
                      <User className="w-4 h-4 text-blue-600" />
                    ) : (
                      <Bot className="w-4 h-4 text-green-600" />
                    )}
                  </div>
                  <div
                    className={`rounded-lg p-3 ${
                      message.type === "user"
                        ? "bg-blue-500 text-white"
                        : "bg-gray-100 text-gray-900"
                    }`}
                  >
                    <p className="text-sm whitespace-pre-wrap break-words">{message.content}</p>
                    {message.type === "assistant" && (
                      <Button
                        variant="ghost"
                        size="sm"
                        className="mt-1 h-6 p-1 text-gray-500 hover:text-gray-700"
                        onClick={() => speakMessage(message.content)}
                      >
                        <Volume2 className="w-3 h-3" />
                      </Button>
                    )}
                  </div>
                </div>
              </div>
            ))}
            {isTyping && (
              <div className="flex justify-start">
                <div className="flex gap-2">
                  <div className="w-8 h-8 rounded-full bg-green-100 flex items-center justify-center animate-pulse">
                    <Bot className="w-4 h-4 text-green-600" />
                  </div>
                  <div className="bg-gray-100 rounded-lg p-3">
                    <div className="flex gap-1 items-center">
                      <div className="w-2 h-2 bg-green-500 rounded-full animate-bounce"></div>
                      <div
                        className="w-2 h-2 bg-green-500 rounded-full animate-bounce"
                        style={{ animationDelay: "0.15s" }}
                      ></div>
                      <div
                        className="w-2 h-2 bg-green-500 rounded-full animate-bounce"
                        style={{ animationDelay: "0.3s" }}
                      ></div>
                      <span className="text-xs text-gray-500 mr-2">جاري التفكير...</span>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        </ScrollArea>

        <div className="p-4 border-t">
          <div className="flex gap-2">
            <Input
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              onKeyDown={handleKeyDown}
              placeholder="اكتب رسالتك هنا..."
              className="flex-1"
              disabled={sendMessageMutation.isPending}
              data-testid="input-ai-message"
            />
            <Button
              variant="outline"
              size="sm"
              onClick={toggleVoiceInput}
              data-testid="button-ai-voice"
              title="الإدخال الصوتي"
            >
              <Mic className="w-4 h-4" />
            </Button>
            <Button
              onClick={() => handleSendMessage()}
              disabled={!inputValue.trim() || sendMessageMutation.isPending}
              data-testid="button-ai-send"
              title="إرسال"
            >
              <Send className="w-4 h-4" />
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

