import { CheckCircle, AlertCircle, Info, XCircle, X } from "lucide-react";
import { Alert, AlertDescription } from "./alert";
import { Button } from "./button";

interface NotificationProps {
  type: "success" | "error" | "warning" | "info";
  title?: string;
  message: string;
  onClose?: () => void;
  autoClose?: boolean;
  duration?: number;
}

const iconMap = {
  success: CheckCircle,
  error: XCircle,
  warning: AlertCircle,
  info: Info,
};

const colorMap = {
  success: "border-success bg-success/10 text-success",
  error: "border-danger bg-danger/10 text-danger",
  warning: "border-warning bg-warning/10 text-warning",
  info: "border-primary bg-primary/10 text-primary",
};

export function Notification({
  type,
  title,
  message,
  onClose,
  autoClose = true,
  duration = 5000,
}: NotificationProps) {
  const Icon = iconMap[type];

  return (
    <Alert
      className={`fixed top-4 left-4 right-4 lg:left-auto lg:right-4 lg:w-96 z-50 ${colorMap[type]}`}
    >
      <div className="flex items-center">
        <Icon className="h-4 w-4 flex-shrink-0" />
        <div className="mr-3 flex-1">
          {title && <p className="font-medium text-sm">{title}</p>}
          <AlertDescription className="text-sm">{message}</AlertDescription>
        </div>
        {onClose && (
          <Button
            variant="ghost"
            size="sm"
            onClick={onClose}
            className="mr-auto p-1 h-auto"
          >
            <X className="h-4 w-4" />
          </Button>
        )}
      </div>
    </Alert>
  );
}

client/src/components/hr/LeaveManagement.content:
import React, { useState, useMemo } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardHeader, CardTitle, CardContent } from "../ui/card";
import { Button } from "../ui/button";
import { Badge } from "../ui/badge";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogDescription,
} from "../ui/dialog";
import { Textarea } from "../ui/textarea";
import { Label } from "../ui/label";
import { useToast } from "../../hooks/use-toast";
import { apiRequest } from "../../lib/queryClient";
import {
  Calendar,
  Clock,
  CheckCircle,
  XCircle,
  AlertCircle,
  User,
  CalendarDays,
  Eye,
  Check,
  X,
} from "lucide-react";

interface UserRequest {
  id: number;
  user_id: number;
  type: string;
  title: string;
  description: string;
  status: string;
  priority: string;
  start_date?: string;
  end_date?: string;
  requested_amount?: number;
  manager_comments?: string;
  created_at: string;
  updated_at?: string;
  user?: {
    id: number;
    username: string;
    display_name?: string;
    display_name_ar?: string;
  };
}

export default function LeaveManagement() {
  const [selectedRequest, setSelectedRequest] = useState<UserRequest | null>(
    null
  );
  const [isViewDialogOpen, setIsViewDialogOpen] = useState(false);
  const [isApprovalDialogOpen, setIsApprovalDialogOpen] = useState(false);
  const [approvalComments, setApprovalComments] = useState("");
  const [approvalAction, setApprovalAction] = useState<"approve" | "reject">(
    "approve"
  );

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const {
    data: userRequests = [],
    isLoading: requestsLoading,
    error: requestsError,
    refetch: refetchRequests,
    isFetching,
  } = useQuery<UserRequest[]>({
    queryKey: ["/api/user-requests"],
    initialData: [],
    refetchOnWindowFocus: false,
    staleTime: 0,
    retry: 3,
    enabled: true,
    onError: (err: any) => {
      toast({
        title: "خطأ",
        description:
          err instanceof Error ? err.message : "فشل في تحميل طلبات المستخدمين",
        variant: "destructive",
      });
    },
  });

  const { data: users = [], isLoading: usersLoading } = useQuery<any[]>({
    queryKey: ["/api/users"],
    initialData: [],
  });

  const updateRequestMutation = useMutation({
    mutationFn: async ({
      id,
      status,
      comments,
    }: {
      id: number;
      status: string;
      comments: string;
    }) => {
      return await apiRequest(`/api/user-requests/${id}`, {
        method: "PATCH",
        body: JSON.stringify({
          status,
          manager_comments: comments,
        }),
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/user-requests"] });
      setIsApprovalDialogOpen(false);
      setApprovalComments("");
      setSelectedRequest(null);
      toast({
        title: "تم تحديث الطلب بنجاح",
        description: "تم حفظ قرار الموافقة/الرفض",
      });
    },
    onError: () => {
      toast({
        title: "خطأ في تحديث الطلب",
        description: "حدث خطأ أثناء تحديث حالة الطلب",
        variant: "destructive",
      });
    },
  });

  const getStatusColor = (status: string) => {
    if (!status)
      return "bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200";
    const lowerStatus = status.toLowerCase();
    if (lowerStatus === "approved" || status === "موافق عليه" || status === "موافق") {
      return "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200";
    }
    if (lowerStatus === "rejected" || status === "مرفوض") {
      return "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200";
    }
    if (lowerStatus === "pending" || status === "معلق" || status === "قيد المراجعة") {
      return "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200";
    }
    return "bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200";
  };

  const getStatusText = (status: string) => {
    if (!status) return status;
    const lowerStatus = status.toLowerCase();
    if (lowerStatus === "approved" || status === "موافق عليه" || status === "موافق")
      return "موافق عليه";
    if (lowerStatus === "rejected" || status === "مرفوض") return "مرفوض";
    if (lowerStatus === "pending" || status === "معلق" || status === "قيد المراجعة")
      return "قيد المراجعة";
    return status;
  };

  const getStatusIcon = (status: string) => {
    if (!status) return <Clock className="w-4 h-4" />;
    const lowerStatus = status.toLowerCase();
    if (lowerStatus === "approved" || status === "موافق عليه" || status === "موافق")
      return <CheckCircle className="w-4 h-4" />;
    if (lowerStatus === "rejected" || status === "مرفوض") return <XCircle className="w-4 h-4" />;
    if (
      lowerStatus === "pending" ||
      status === "معلق" ||
      status === "قيد المراجعة"
    )
      return <AlertCircle className="w-4 h-4" />;
    return <Clock className="w-4 h-4" />;
  };

  const getPriorityColor = (priority: string) => {
    switch (priority.toLowerCase()) {
      case "high":
      case "عالية":
        return "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200";
      case "medium":
      case "متوسطة":
      case "عادي":
      case "عادية":
        return "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200";
      case "low":
      case "منخفضة":
        return "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200";
      default:
        return "bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200";
    }
  };

  const getPriorityText = (priority: string) => {
    switch (priority.toLowerCase()) {
      case "high":
      case "عالية":
        return "عالية";
      case "medium":
      case "متوسطة":
      case "عادي":
      case "عادية":
        return "متوسطة";
      case "low":
      case "منخفضة":
        return "منخفضة";
      default:
        return priority;
    }
  };

  const handleApproval = (
    request: UserRequest,
    action: "approve" | "reject"
  ) => {
    setSelectedRequest(request);
    setApprovalAction(action);
    setIsApprovalDialogOpen(true);
  };

  const handleSubmitApproval = () => {
    if (!selectedRequest) return;

    updateRequestMutation.mutate({
      id: selectedRequest.id,
      status: approvalAction,
      comments: approvalComments,
    });
  };

  const getUserDisplayName = (userId: number) => {
    if (!Array.isArray(users) || users.length === 0) return `المستخدم ${userId}`;
    const user = users.find((u: any) => u.id === userId);
    return user ? user.display_name_ar || user.display_name || user.username : `المستخدم ${userId}`;
  };

  // memoize the request groups
  const pendingRequests = useMemo(
    () =>
      Array.isArray(userRequests)
        ? userRequests.filter((req: any) => {
            const status = String(req.status || "").toLowerCase();
            return status === "pending" || req.status === "معلق" || req.status === "قيد المراجعة";
          })
        : [],
    [userRequests]
  );

  const approvedRequests = useMemo(
    () =>
      Array.isArray(userRequests)
        ? userRequests.filter((req: any) => {
            const status = String(req.status || "").toLowerCase();
            return status === "approved" || req.status === "موافق عليه" || req.status === "موافق";
          })
        : [],
    [userRequests]
  );

  const rejectedRequests = useMemo(
    () =>
      Array.isArray(userRequests)
        ? userRequests.filter((req: any) => {
            const status = String(req.status || "").toLowerCase();
            return status === "rejected" || req.status === "مرفوض";
          })
        : [],
    [userRequests]
  );

  if (requestsLoading || usersLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
          <p className="text-gray-600 dark:text-gray-400 mt-2">
            جاري تحميل طلبات المستخدمين...
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
            إدارة طلبات المستخدمين
          </h2>
          <p className="text-gray-600 dark:text-gray-300">
            مراجعة والموافقة على طلبات المستخدمين
          </p>
        </div>
        <div className="flex gap-2">
          <Button onClick={() => refetchRequests()} variant="outline" className="text-sm">
            إعادة تحميل البيانات
          </Button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600 dark:text-gray-400">
                  إجمالي الطلبات
                </p>
                <p className="text-2xl font-bold text-blue-600">{Array.isArray(userRequests) ? userRequests.length : 0}</p>
              </div>
              <CalendarDays className="w-8 h-8 text-blue-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600 dark:text-gray-400">قيد المراجعة</p>
                <p className="text-2xl font-bold text-yellow-600">{pendingRequests.length}</p>
              </div>
              <Clock className="w-8 h-8 text-yellow-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600 dark:text-gray-400">موافق عليها</p>
                <p className="text-2xl font-bold text-green-600">{approvedRequests.length}</p>
              </div>
              <CheckCircle className="w-8 h-8 text-green-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600 dark:text-gray-400">مرفوضة</p>
                <p className="text-2xl font-bold text-red-600">{rejectedRequests.length}</p>
              </div>
              <XCircle className="w-8 h-8 text-red-500" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Requests Table */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Calendar className="w-5 h-5" />
            قائمة طلبات المستخدمين
          </CardTitle>
        </CardHeader>
        <CardContent>
          {requestsLoading ? (
            <div className="text-center py-8">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
              <p className="text-gray-600 dark:text-gray-400 mt-2">جاري تحميل الطلبات...</p>
            </div>
          ) : !Array.isArray(userRequests) || userRequests.length === 0 ? (
            <div className="text-center py-8 text-gray-500 dark:text-gray-400">
              <Calendar className="w-12 h-12 mx-auto mb-4 opacity-50" />
              <p>لا توجد طلبات مسجلة</p>
              {requestsError && <p className="text-red-500 mt-2">خطأ في تحميل البيانات: {String(requestsError)}</p>}
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="w-full border-collapse">
                <thead>
                  <tr className="border-b bg-gray-50 dark:bg-gray-800">
                    <th className="text-right p-3 font-semibold">المستخدم</th>
                    <th className="text-right p-3 font-semibold">نوع الطلب</th>
                    <th className="text-right p-3 font-semibold">العنوان</th>
                    <th className="text-right p-3 font-semibold">الأولوية</th>
                    <th className="text-right p-3 font-semibold">الحالة</th>
                    <th className="text-right p-3 font-semibold">تاريخ الطلب</th>
                    <th className="text-center p-3 font-semibold">الإجراءات</th>
                  </tr>
                </thead>
                <tbody>
                  {Array.isArray(userRequests) &&
                    userRequests.map((request: any) => (
                      <tr key={request.id} className="border-b hover:bg-gray-50 dark:hover:bg-gray-800">
                        <td className="p-3">
                          <div className="flex items-center gap-2">
                            <User className="w-4 h-4 text-gray-400" />
                            <span className="font-medium">{getUserDisplayName(request.user_id)}</span>
                          </div>
                        </td>
                        <td className="p-3">
                          <Badge variant="outline" className="bg-blue-50 text-blue-700 dark:bg-blue-900 dark:text-blue-300">
                            {request.type}
                          </Badge>
                        </td>
                        <td className="p-3">
                          <span className="text-sm font-medium">{request.title}</span>
                        </td>
                        <td className="p-3">
                          <Badge className={getPriorityColor(request.priority)}>
                            {getPriorityText(request.priority)}
                          </Badge>
                        </td>
                        <td className="p-3">
                          <Badge className={getStatusColor(request.status)}>
                            <span className="flex items-center gap-1">
                              {getStatusIcon(request.status)}
                              {getStatusText(request.status)}
                            </span>
                          </Badge>
                        </td>
                        <td className="p-3 text-sm text-gray-600 dark:text-gray-400">
                          {new Date(request.created_at).toLocaleDateString("ar")}
                        </td>
                        <td className="p-3">
                          <div className="flex justify-center gap-2">
                            <Button
                              size="sm"
                              variant="outline"
                              className="bg-blue-50 text-blue-600 hover:bg-blue-100 dark:bg-blue-900 dark:text-blue-300"
                              onClick={() => {
                                setSelectedRequest(request);
                                setIsViewDialogOpen(true);
                              }}
                            >
                              <Eye className="w-4 h-4 mr-1" />
                              عرض
                            </Button>
                            {(request.status?.toLowerCase() === "pending" ||
                              request.status === "معلق" ||
                              request.status === "قيد المراجعة") && (
                              <>
                                <Button size="sm" className="bg-green-600 hover:bg-green-700 text-white" onClick={() => handleApproval(request, "approve")}>
                                  <Check className="w-4 h-4 mr-1" />
                                  موافقة
                                </Button>
                                <Button size="sm" className="bg-red-600 hover:bg-red-700 text-white" onClick={() => handleApproval(request, "reject")}>
                                  <X className="w-4 h-4 mr-1" />
                                  رفض
                                </Button>
                              </>
                            )}
                          </div>
                        </td>
                      </tr>
                    ))}
                </tbody>
              </table>
            </div>
          )}
        </CardContent>
      </Card>

      {/* View Request Dialog */}
      <Dialog open={isViewDialogOpen} onOpenChange={setIsViewDialogOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>تفاصيل الطلب</DialogTitle>
            <DialogDescription>عرض تفاصيل طلب الإجازة وحالة المراجعة</DialogDescription>
          </DialogHeader>

          {selectedRequest && (
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label className="font-semibold">المستخدم:</Label>
                  <p className="text-sm text-gray-600 dark:text-gray-400">
                    {getUserDisplayName(selectedRequest.user_id)}
                  </p>
                </div>
                <div>
                  <Label className="font-semibold">نوع الطلب:</Label>
                  <p className="text-sm text-gray-600 dark:text-gray-400">{selectedRequest.type}</p>
                </div>
                <div>
                  <Label className="font-semibold">العنوان:</Label>
                  <p className="text-sm text-gray-600 dark:text-gray-400">{selectedRequest.title}</p>
                </div>
                <div>
                  <Label className="font-semibold">الأولوية:</Label>
                  <Badge className={getPriorityColor(selectedRequest.priority)}>
                    {getPriorityText(selectedRequest.priority)}
                  </Badge>
                </div>
                {selectedRequest.start_date && (
                  <div>
                    <Label className="font-semibold">تاريخ البداية:</Label>
                    <p className="text-sm text-gray-600 dark:text-gray-400">
                      {new Date(selectedRequest.start_date).toLocaleDateString("ar")}
                    </p>
                  </div>
                )}
                {selectedRequest.end_date && (
                  <div>
                    <Label className="font-semibold">تاريخ النهاية:</Label>
                    <p className="text-sm text-gray-600 dark:text-gray-400">
                      {new Date(selectedRequest.end_date).toLocaleDateString("ar")}
                    </p>
                  </div>
                )}
                {selectedRequest.requested_amount && (
                  <div>
                    <Label className="font-semibold">المبلغ المطلوب:</Label>
                    <p className="text-sm text-gray-600 dark:text-gray-400">
                      {selectedRequest.requested_amount} ريال
                    </p>
                  </div>
                )}
                <div>
                  <Label className="font-semibold">الحالة:</Label>
                  <Badge className={getStatusColor(selectedRequest.status)}>
                    {getStatusText(selectedRequest.status)}
                  </Badge>
                </div>
              </div>

              <div>
                <Label className="font-semibold">الوصف:</Label>
                <p className="text-sm text-gray-600 dark:text-gray-400 mt-1 p-3 bg-gray-50 dark:bg-gray-800 rounded-lg">
                  {selectedRequest.description}
                </p>
              </div>

              {selectedRequest.manager_comments && (
                <div>
                  <Label className="font-semibold">تعليقات المدير:</Label>
                  <p className="text-sm text-gray-600 dark:text-gray-400 mt-1 p-3 bg-gray-50 dark:bg-gray-800 rounded-lg">
                    {selectedRequest.manager_comments}
                  </p>
                </div>
              )}
            </div>
          )}
        </DialogContent>
      </Dialog>

      {/* Approval Dialog */}
      <Dialog open={isApprovalDialogOpen} onOpenChange={setIsApprovalDialogOpen}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>
              {approvalAction === "approve" ? "موافقة على الطلب" : "رفض الطلب"}
            </DialogTitle>
            <DialogDescription>
              {approvalAction === "approve"
                ? "تأكيد الموافقة على الطلب مع إمكانية إضافة تعليقات"
                : "رفض الطلب وإضافة تعليقات حول أسباب الرفض"}
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-4">
            <div>
              <Label htmlFor="comments">تعليقات (اختياري):</Label>
              <Textarea
                id="comments"
                placeholder="أضف تعليقات حول قرارك..."
                value={approvalComments}
                onChange={(e) => setApprovalComments(e.target.value)}
                className="mt-1"
              />
            </div>
            <div className="flex justify-end gap-2">
              <Button variant="outline" onClick={() => setIsApprovalDialogOpen(false)}>
                إلغاء
              </Button>
              <Button
                onClick={handleSubmitApproval}
                disabled={updateRequestMutation.isPending}
                className={
                  approvalAction === "approve" ? "bg-green-600 hover:bg-green-700" : "bg-red-600 hover:bg-red-700"
                }
              >
                {updateRequestMutation.isPending ? (
                  <div className="flex items-center gap-2">
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                    جاري المعالجة...
                  </div>
                ) : approvalAction === "approve" ? (
                  <>
                    <Check className="w-4 h-4 mr-1" />
                    موافقة
                  </>
                ) : (
                  <>
                    <X className="w-4 h-4 mr-1" />
                    رفض
                  </>
                )}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

server/services/meta-whatsapp.ts.content:
import type { IStorage } from "../storage";
import { logger } from "../lib/logger";

export interface MetaWhatsAppConfig {
  accessToken: string;
  phoneNumberId: string;
  businessAccountId: string;
  apiVersion: string;
}

export interface WhatsAppTemplateMessage {
  to: string;
  type: "template";
  template: {
    name: string;
    language: {
      code: string;
    };
    components?: Array<{
      type: string;
      parameters?: Array<{
        type: string;
        text: string;
      }>;
    }>;
  };
}

export interface WhatsAppTextMessage {
  to: string;
  type: "text";
  text: {
    body: string;
  };
}

export class MetaWhatsAppService {
  private config: MetaWhatsAppConfig;
  private storage: IStorage;
  private baseUrl: string;

  constructor(storage: IStorage, config?: Partial<MetaWhatsAppConfig>) {
    this.storage = storage;

    this.config = {
      accessToken: process.env.META_ACCESS_TOKEN || "",
      phoneNumberId: process.env.META_PHONE_NUMBER_ID || "",
      businessAccountId:
        process.env.META_BUSINESS_ACCOUNT_ID || "795259496521200",
      apiVersion: "v21.0",
      ...config,
    };

    this.baseUrl = `https://graph.facebook.com/${this.config.apiVersion}`;

    if (!this.config.accessToken || !this.config.phoneNumberId) {
      logger.warn(
        "⚠️ Meta WhatsApp API credentials not configured. Set META_ACCESS_TOKEN and META_PHONE_NUMBER_ID environment variables."
      );
    } else {
      logger.info("✅ Meta WhatsApp API service initialized successfully");
    }
  }

  /**
   * إرسال رسالة نصية مباشرة
   */
  async sendTextMessage(
    to: string,
    message: string,
    options?: {
      title?: string;
      priority?: string;
      context_type?: string;
      context_id?: string;
    },
  ): Promise<{ success: boolean; messageId?: string; error?: string }> {
    try {
      if (!this.config.accessToken || !this.config.phoneNumberId) {
        throw new Error("Meta WhatsApp API غير مُعد بشكل صحيح");
      }

      // تنسيق رقم الهاتف (إزالة + وwhatsapp: إن وجدت)
      const formattedPhone = to
        .replace(/[\+\s\-\(\)]/g, "")
        .replace("whatsapp:", "");

      const messageData: WhatsAppTextMessage = {
        to: formattedPhone,
        type: "text",
        text: {
          body: message,
        },
      };

      const response = await fetch(
        `${this.baseUrl}/${this.config.phoneNumberId}/messages`,
        {
          method: "POST",
          headers: {
            Authorization: `Bearer ${this.config.accessToken}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify(messageData),
        },
      );

      const result = await response.json();

      if (!response.ok) {
        throw new Error(
          result.error?.message ||
            `HTTP ${response.status}: ${response.statusText}`,
        );
      }

      // حفظ الإشعار في قاعدة البيانات
      const notificationData = {
        title: options?.title || "رسالة واتس اب",
        message: message,
        type: "whatsapp" as const,
        priority: options?.priority || "normal",
        recipient_type: "user" as const,
        phone_number: to,
        status: "sent" as const,
        external_id: result.messages?.[0]?.id,
        external_status: "sent",
        sent_at: new Date(),
        context_type: options?.context_type,
        context_id: options?.context_id,
      };

      await this.storage.createNotification(notificationData);

      logger.info(
        `📱 تم إرسال رسالة واتس اب مباشرة إلى ${to} - ID ${result.messages?.[0]?.id}`
      );

      return {
        success: true,
        messageId: result.messages?.[0]?.id,
      };
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : "خطأ غير معروف";
      logger.error("خطأ في إرسال رسالة واتس اب عبر Meta API", error);

      const notificationData = {
        title: options?.title || "رسالة واتس اب",
        message: message,
        type: "whatsapp" as const,
        priority: options?.priority || "normal",
        recipient_type: "user" as const,
        phone_number: to,
        status: "failed" as const,
        error_message: errorMessage,
        context_type: options?.context_type,
        context_id: options?.context_id,
      };

      await this.storage.createNotification(notificationData);

      return {
        success: false,
        error: errorMessage,
      };
    }
  }

  /**
   * إرسال رسالة باستخدام قالب Meta مُوافق عليه
   */
  async sendTemplateMessage(
    to: string,
    templateName: string,
    language: string = "ar",
    variables: string[] = [],
    options?: {
      title?: string;
      priority?: string;
      context_type?: string;
      context_id?: string;
    },
  ): Promise<{ success: boolean; messageId?: string; error?: string }> {
    try {
      if (!this.config.accessToken || !this.config.phoneNumberId) {
        throw new Error("Meta WhatsApp API غير مُعد بشكل صحيح");
      }

      // تنسيق رقم الهاتف
      const formattedPhone = to
        .replace(/[\+\s\-\(\)]/g, "")
        .replace("whatsapp:", "");

      const messageData: WhatsAppTemplateMessage = {
        to: formattedPhone,
        type: "template",
        template: {
          name: templateName,
          language: {
            code: language,
          },
        },
      };

      // إضافة متغيرات القالب إن وجدت
      if (variables && variables.length > 0) {
        messageData.template.components = [
          {
            type: "body",
            parameters: variables.map((variable) => ({
              type: "text",
              text: variable,
            })),
          },
        ];
      }

      const response = await fetch(
        `${this.baseUrl}/${this.config.phoneNumberId}/messages`,
        {
          method: "POST",
          headers: {
            Authorization: `Bearer ${this.config.accessToken}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify(messageData),
        },
      );

      const result = await response.json();

      if (!response.ok) {
        throw new Error(
          result.error?.message ||
            `HTTP ${response.status}: ${response.statusText}`,
        );
      }

      // حفظ الإشعار في قاعدة البيانات
      const notificationData = {
        title: options?.title || "رسالة واتس اب (قالب)",
        message: `قالب: ${templateName} - متغيرات: ${variables.join(", ")}`,
        type: "whatsapp" as const,
        priority: options?.priority || "normal",
        recipient_type: "user" as const,
        phone_number: to,
        status: "sent" as const,
        external_id: result.messages?.[0]?.id,
        external_status: "sent",
        sent_at: new Date(),
        context_type: options?.context_type,
        context_id: options?.context_id,
      };

      await this.storage.createNotification(notificationData);

      logger.info(
        `📱 تم إرسال رسالة واتس اب (قالب Meta) إلى ${to} - ID ${result.messages?.[0]?.id}`
      );

      return {
        success: true,
        messageId: result.messages?.[0]?.id,
      };
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : "خطأ غير معروف";
      logger.error("خطأ في إرسال رسالة واتس اب (قالب Meta)", error);

      const notificationData = {
        title: options?.title || "رسالة واتس اب (قالب)",
        message: `قالب: ${templateName} - خطأ: ${errorMessage}`,
        type: "whatsapp" as const,
        priority: options?.priority || "normal",
        recipient_type: "user" as const,
        phone_number: to,
        status: "failed" as const,
        error_message: errorMessage,
        context_type: options?.context_type,
        context_id: options?.context_id,
      };

      await this.storage.createNotification(notificationData);

      return {
        success: false,
        error: errorMessage,
      };
    }
  }

  /**
   * الحصول على معلومات رقم الهاتف التجاري
   */
  async getPhoneNumberInfo(): Promise<any> {
    try {
      if (!this.config.accessToken || !this.config.phoneNumberId) {
        throw new Error("Meta WhatsApp API غير مُعد بشكل صحيح");
      }

      const response = await fetch(
        `${this.baseUrl}/${this.config.phoneNumberId}?fields=display_phone_number,verified_name,quality_rating`,
        {
          headers: {
            Authorization: `Bearer ${this.config.accessToken}`,
          },
        },
      );

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error?.message || `HTTP ${response.status}`);
      }

      return result;
    } catch (error) {
      logger.error("خطأ في الحصول على معلومات الرقم", error);
      throw error;
    }
  }

  /**
   * الحصول على قائمة القوالب المُوافقة
   */
  async getApprovedTemplates(): Promise<any[]> {
    try {
      if (!this.config.accessToken || !this.config.businessAccountId) {
        throw new Error("Meta WhatsApp API غير مُعد بشكل صحيح");
      }

      const response = await fetch(
        `${this.baseUrl}/${this.config.businessAccountId}/message_templates?fields=name,status,language,components`,
        {
          headers: {
            Authorization: `Bearer ${this.config.accessToken}`,
          },
        },
      );

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error?.message || `HTTP ${response.status}`);
      }

      // فلترة القوالب المُوافقة فقط
      return (
        result.data?.filter(
          (template: any) => template.status === "APPROVED",
        ) || []
      );
    } catch (error) {
      logger.error("خطأ في الحصول على القوالب", error);
      throw error;
    }
  }

  /**
   * التحقق من صحة الإعداد
   */
  async testConnection(): Promise<{
    success: boolean;
    error?: string;
    data?: any;
  }> {
    try {
      const phoneInfo = await this.getPhoneNumberInfo();
      return {
        success: true,
        data: phoneInfo,
      };
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : "فشل اختبار الاتصال";
      return {
        success: false,
        error: errorMessage,
      };
    }
  }

  /**
   * معالجة Webhook الواردة من Meta
   */
  async handleWebhook(body: any): Promise<void> {
    try {
      // معالجة تحديثات حالة الرسائل
      if (body.entry && body.entry[0] && body.entry[0].changes) {
        for (const change of body.entry[0].changes) {
          if (change.field === "messages") {
            const value = change.value;

            // تحديث حالة الرسائل
            if (value.statuses) {
              for (const status of value.statuses) {
                await this.updateMessageStatus(status.id, status.status);
              }
            }

            // معالجة الرسائل الواردة
            if (value.messages) {
              for (const message of value.messages) {
                await this.handleIncomingMessage(message);
              }
            }
          }
        }
      }
    } catch (error) {
      logger.error("خطأ في معالجة Webhook", error);
    }
  }

  /**
   * تحديث حالة الرسالة
   */
  private async updateMessageStatus(
    messageId: string,
    status: string,
  ): Promise<void> {
    try {
      // تحديث حالة الرسالة في قاعدة البيانات
      const notifications = await this.storage.getNotifications();
      const notification = notifications.find(
        (n) =>
          n.twilio_sid === messageId || (n as any).external_id === messageId,
      );

      if (notification) {
        // تحديث الحالة
        const updatedNotification = {
          ...notification,
          external_status: status,
          delivered_at:
            status === "delivered" ? new Date() : notification.delivered_at,
          read_at: status === "read" ? new Date() : notification.read_at,
        };

        logger.info(`📊 تم تحديث حالة الرسالة ${messageId} ${status}`);
      }
    } catch (error) {
      logger.error("خطأ في تحديث حالة الرسالة", error);
    }
  }

  /**
   * معالجة الرسائل الواردة
   */
  private async handleIncomingMessage(message: any): Promise<void> {
    try {
      logger.info("📨 رسالة واردة", {
        from: message.from,
        type: message.type,
        text: message.text?.body || "غير نصية",
      });

      // حفظ الرسالة الواردة في قاعدة البيانات
      const notificationData = {
        title: "رسالة واردة",
        message: message.text?.body || "رسالة غير نصية",
        type: "whatsapp" as const,
        priority: "normal",
        recipient_type: "system" as const,
        phone_number: message.from,
        status: "received" as const,
        external_id: message.id,
        external_status: "received",
        received_at: new Date(),
        context_type: "incoming_message",
      };

      await this.storage.createNotification(notificationData);
    } catch (error) {
      logger.error("خطأ في معالجة الرسالة الواردة", error);
    }
  }
}

