import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import Header from "../components/layout/Header";
import Sidebar from "../components/layout/Sidebar";
import MobileNav from "../components/layout/MobileNav";
import {
  Card,
  CardContent,
  CardHeader,
  CardTitle,
} from "../components/ui/card";
import { Button } from "../components/ui/button";
import { Badge } from "../components/ui/badge";
import {
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
} from "../components/ui/tabs";
import {
  formatNumber,
  formatPercentage,
  formatNumberWithCommas,
} from "../lib/formatNumber";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../components/ui/select";
import { Calendar } from "../components/ui/calendar";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "../components/ui/popover";
import {
  BarChart3,
  PieChart,
  TrendingUp,
  Download,
  Filter,
  Calendar as CalendarIcon,
  FileText,
  Users,
  Settings,
  Package,
  AlertTriangle,
  CheckCircle2,
  Activity,
  Zap,
  Clock,
  Target,
} from "lucide-react";
import { format } from "date-fns";
import { ar } from "date-fns/locale";
import {
  InteractiveBarChart,
  InteractiveLineChart,
  InteractivePieChart,
  InteractiveAreaChart,
  ComboChart,
  MetricsGrid,
} from "../components/charts";

export default function Reports() {
  const [dateRange, setDateRange] = useState<{ from?: Date; to?: Date }>({});
  const [selectedPeriod, setSelectedPeriod] = useState("month");
  const [reportType, setReportType] = useState("production");

  // Get date range for API calls
  const getDateRange = () => {
    const now = new Date();
    let from: string, to: string;

    switch (selectedPeriod) {
      case "week":
        from = format(
          new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000),
          "yyyy-MM-dd",
        );
        to = format(now, "yyyy-MM-dd");
        break;
      case "quarter":
        from = format(
          new Date(now.getFullYear(), Math.floor(now.getMonth() / 3) * 3, 1),
          "yyyy-MM-dd",
        );
        to = format(now, "yyyy-MM-dd");
        break;
      case "year":
        from = format(new Date(now.getFullYear(), 0, 1), "yyyy-MM-dd");
        to = format(now, "yyyy-MM-dd");
        break;
      case "custom":
        from = dateRange.from
          ? format(dateRange.from, "yyyy-MM-dd")
          : format(
              new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000),
              "yyyy-MM-dd",
            );
        to = dateRange.to
          ? format(dateRange.to, "yyyy-MM-dd")
          : format(now, "yyyy-MM-dd");
        break;
      default: // month
        from = format(
          new Date(now.getFullYear(), now.getMonth(), 1),
          "yyyy-MM-dd",
        );
        to = format(now, "yyyy-MM-dd");
    }

    return { from, to };
  };

  const { from, to } = getDateRange();

  // Fetch comprehensive dashboard data
  const { data: dashboardData, isLoading: isDashboardLoading } = useQuery({
    queryKey: ["/api/reports/dashboard", from, to],
    queryFn: () =>
      fetch(`/api/reports/dashboard?date_from=${from}&date_to=${to}`).then(
        (res) => res.json(),
      ),
  });

  // Fetch order reports
  const { data: orderReports, isLoading: isOrdersLoading } = useQuery({
    queryKey: ["/api/reports/orders", from, to],
    queryFn: () =>
      fetch(`/api/reports/orders?date_from=${from}&date_to=${to}`).then((res) =>
        res.json(),
      ),
  });

  // Fetch advanced metrics
  const { data: advancedMetrics, isLoading: isMetricsLoading } = useQuery({
    queryKey: ["/api/reports/advanced-metrics", from, to],
    queryFn: () =>
      fetch(
        `/api/reports/advanced-metrics?date_from=${from}&date_to=${to}`,
      ).then((res) => res.json()),
  });

  // Fetch HR reports
  const { data: hrReports, isLoading: isHRLoading } = useQuery({
    queryKey: ["/api/reports/hr", from, to],
    queryFn: () =>
