      if (isNaN(assignedToId) || assignedToId <= 0) {
        return res.status(400).json({ message: "معرف المستخدم المعين غير صحيح" });
      }

      // Validate note_type
      const validNoteTypes = ['order', 'design', 'statement', 'quote', 'delivery', 'call_customer', 'other'];
