  // Get mixing batches by production order
  app.get("/api/mixing-batches/production-order/:productionOrderId", requireAuth, async (req, res) => {
    try {
      const productionOrderId = parseInt(req.params.productionOrderId);
      const batches = await storage.getMixingBatchesByProductionOrder(productionOrderId);
      res.json({ data: batches });
    } catch (error: any) {
      console.error("Error getting production order batches:", error);
      res.status(500).json({ message: "خطأ في جلب عمليات الخلط لأمر الإنتاج", error: error.message });
    }
  });

  // Create mixing batch
  app.post("/api/mixing-batches", requireAuth, async (req, res) => {
    try {
      const { batch, ingredients } = req.body;

      if (!batch || !ingredients || !Array.isArray(ingredients) || ingredients.length === 0) {
        return res.status(400).json({ message: "بيانات عملية الخلط أو المكونات ناقصة" });
      }

      // Validate screw_assignment
      if (!batch.screw_assignment || !['A', 'B'].includes(batch.screw_assignment)) {
        return res.status(400).json({ message: "يجب تحديد البريمة (A أو B)" });
      }

      // Validate machine_id is provided
      if (!batch.machine_id) {
        return res.status(400).json({ message: "يجب تحديد الماكينة" });
      }

      // Ensure batch_number is auto-generated (remove if user provided it)
      const { batch_number, formula_id, roll_id, ...cleanBatchData } = batch;

      const batchData = {
        ...cleanBatchData,
        operator_id: req.user!.id,
        status: "in_progress",
        started_at: new Date(),
      };

      const newBatch = await storage.createMixingBatch(batchData, ingredients);
      res.status(201).json(newBatch);
    } catch (error: any) {
      console.error("Error creating mixing batch:", error);
      res.status(500).json({ message: "خطأ في إنشاء عملية الخلط", error: error.message });
    }
  });

  // Update mixing batch
  app.put("/api/mixing-batches/:id", requireAuth, async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const updates = req.body;

      const updatedBatch = await storage.updateMixingBatch(id, updates);
      res.json(updatedBatch);
    } catch (error: any) {
      console.error("Error updating mixing batch:", error);
      res.status(500).json({ message: "خطأ في تحديث عملية الخلط", error: error.message });
    }
  });

  // Update batch ingredient actuals
  app.put("/api/mixing-batches/:id/ingredients", requireAuth, async (req, res) => {
    try {
      const batchId = parseInt(req.params.id);
      const { ingredientUpdates } = req.body;

      if (!ingredientUpdates || !Array.isArray(ingredientUpdates)) {
        return res.status(400).json({ message: "بيانات المكونات ناقصة" });
      }

      await storage.updateBatchIngredientActuals(batchId, ingredientUpdates);
      const updatedBatch = await storage.getMixingBatchById(batchId);
      res.json(updatedBatch);
    } catch (error: any) {
      console.error("Error updating batch ingredients:", error);
      res.status(500).json({ message: "خطأ في تحديث الكميات الفعلية", error: error.message });
    }
  });

  // Complete mixing batch
  app.post("/api/mixing-batches/:id/complete", requireAuth, async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const completedBatch = await storage.completeMixingBatch(id);
      res.json(completedBatch);
    } catch (error: any) {
      console.error("Error completing mixing batch:", error);
      res.status(500).json({ message: "خطأ في إتمام عملية الخلط", error: error.message });
    }
  });

  // Record material consumption from mixing batch
  app.post("/api/inventory/consumption", requireAuth, async (req: AuthRequest, res) => {
    try {
      const { batchId, consumptions } = req.body;
      const userId = req.session.userId;

      if (!userId) {
        return res.status(401).json({ message: "غير مصرح" });
      }

      if (!batchId || !consumptions || !Array.isArray(consumptions)) {
        return res.status(400).json({ message: "بيانات الاستهلاك غير مكتملة" });
      }

      // Get batch details
      const batch = await storage.getMixingBatchById(batchId);
      if (!batch) {
        return res.status(404).json({ message: "دفعة الخلط غير موجودة" });
      }

      // Record consumption for each ingredient
      const results = [];
      for (const consumption of consumptions) {
        const { item_id, quantity_consumed, cost_at_consumption } = consumption;

        // Get inventory item
        const inventoryItem = await storage.getInventoryByItemId(item_id);
        if (!inventoryItem) {
          throw new Error(`الصنف ${item_id} غير موجود في المخزون`);
        }

        // Create inventory movement (out)
        const movement = await storage.createInventoryMovement({
          inventory_id: inventoryItem.id,
          movement_type: "out",
          quantity: quantity_consumed.toString(),
          unit_cost: cost_at_consumption?.toString(),
          total_cost: cost_at_consumption 
            ? (parseFloat(quantity_consumed) * parseFloat(cost_at_consumption)).toString()
            : undefined,
          reference_number: `BATCH-${batch.batch_number}`,
          reference_type: "production",
          notes: `استهلاك من دفعة خلط ${batch.batch_number}`,
          created_by: userId,
        });

        results.push({
          item_id,
          quantity_consumed,
          movement_id: movement.id,
          new_quantity: parseFloat(inventoryItem.current_stock) - parseFloat(quantity_consumed),
        });
      }

      res.json({
        success: true,
        message: "تم تسجيل استهلاك المواد بنجاح",
        results,
      });
    } catch (error: any) {
      console.error("Error recording material consumption:", error);
      res.status(500).json({ 
        message: "خطأ في تسجيل استهلاك المواد", 
        error: error.message 
      });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

scripts/hash-passwords.js.content:
#!/usr/bin/env node

/**
 * Password Hashing Script for Production Deployment
 *
 * This script identifies and hashes any plaintext passwords in the database
 * to ensure security compliance before production deployment.
 *
 * Usage:
 *   node scripts/hash-passwords.js
 *
 * Environment Variables:
 *   DATABASE_URL - Required: PostgreSQL connection string
 *   DRY_RUN - Optional: Set to 'true' to preview changes without applying them
 */

import { drizzle } from "drizzle-orm/neon-serverless";
import { Pool } from "@neondatabase/serverless";
import { eq } from "drizzle-orm";
import bcrypt from "bcrypt";

// Configuration
const SALT_ROUNDS = 12; // Strong salt rounds for production
const DRY_RUN = process.env.DRY_RUN === "true";

async function hashPasswords() {
  console.log("🔐 Password Hashing Script Started");
  console.log("===================================");

  if (!process.env.DATABASE_URL) {
    console.error("❌ DATABASE_URL environment variable is not set");
    process.exit(1);
  }

  if (DRY_RUN) {
    console.log("🧪 DRY RUN MODE: No changes will be made to the database");
  }

  console.log("✅ DATABASE_URL is configured");

  try {
    // Create database connection
    const pool = new Pool({ connectionString: process.env.DATABASE_URL });
    const db = drizzle({ client: pool });

    console.log("🔌 Connected to database");

    // Fetch all users using raw SQL to avoid schema dependencies
    console.log("📋 Fetching user accounts...");
    const usersResult = await db.execute(
      "SELECT id, username, password FROM users ORDER BY id",
    );
    const allUsers = usersResult.rows;
    console.log(`📊 Found ${allUsers.length} user accounts`);

    let plaintextPasswordsFound = 0;
    let passwordsHashed = 0;
    let usersWithNoPassword = 0;
    const hashedUsers = [];

    console.log("\n🔍 Analyzing password security...");

    for (const user of allUsers) {
      if (!user.password) {
        usersWithNoPassword++;
        console.warn(
          `⚠️  User ${user.id} (${user.username}) has no password set`,
        );
        continue;
      }

      // Check if password is already hashed (bcrypt hashes start with $2a$, $2b$, or $2y$)
      const isHashedPassword =
        user.password.startsWith("$2a$") ||
        user.password.startsWith("$2b$") ||
        user.password.startsWith("$2y$");

      if (!isHashedPassword) {
        plaintextPasswordsFound++;
        console.error(
          `🚨 PLAINTEXT PASSWORD FOUND: User ${user.id} (${user.username})`,
        );

        if (!DRY_RUN) {
          try {
            // Hash the plaintext password
            const hashedPassword = await bcrypt.hash(
              user.password,
              SALT_ROUNDS,
            );

            // Update user with hashed password using raw SQL
            await db.execute("UPDATE users SET password = $1 WHERE id = $2", [
              hashedPassword,
              user.id,
            ]);

            passwordsHashed++;
            hashedUsers.push({
              id: user.id,
              username: user.username,
              originalLength: user.password.length,
              hashedLength: hashedPassword.length,
            });

            console.log(
              `✅ Hashed password for user ${user.id} (${user.username})`,
            );
          } catch (hashError) {
            console.error(
              `❌ Failed to hash password for user ${user.id}:`,
              hashError,
            );
          }
        } else {
          console.log(
            `   → Would hash password for user ${user.id} (${user.username})`,
          );
        }
      }
    }

    console.log("\n📊 PASSWORD SECURITY ANALYSIS COMPLETE");
    console.log("=====================================");
    console.log(`👥 Total users analyzed: ${allUsers.length}`);
    console.log(
      `🔓 Users with plaintext passwords: ${plaintextPasswordsFound}`,
    );
    console.log(`🔐 Passwords hashed in this run: ${passwordsHashed}`);
    console.log(`⚠️  Users with no password: ${usersWithNoPassword}`);
    console.log(
      `✅ Users with properly hashed passwords: ${allUsers.length - plaintextPasswordsFound - usersWithNoPassword}`,
    );

    if (passwordsHashed > 0 && !DRY_RUN) {
      console.log("\n🔐 PASSWORDS SUCCESSFULLY HASHED:");
      hashedUsers.forEach((user) => {
        console.log(
          `   ✅ User ${user.id} (${user.username}): ${user.originalLength} chars → ${user.hashedLength} chars`,
        );
      });
    }

    if (DRY_RUN && plaintextPasswordsFound > 0) {
      console.log("\n🧪 DRY RUN SUMMARY:");
      console.log(`   ${plaintextPasswordsFound} password(s) would be hashed`);
      console.log("   Run without DRY_RUN=true to apply changes");
    }

    if (plaintextPasswordsFound === 0) {
      console.log("\n🎉 EXCELLENT! All user passwords are properly hashed.");
      console.log("   Your database is secure for production deployment.");
    } else if (passwordsHashed > 0 && !DRY_RUN) {
      console.log("\n✅ PASSWORD HASHING COMPLETE");
      console.log(
        "   All plaintext passwords have been converted to secure hashes.",
      );
      console.log("   Your database is now secure for production deployment.");
      console.log("\n⚠️  NEXT STEPS:");
      console.log("   1. Remove SKIP_SECURITY_CHECK environment variable");
      console.log(
        "   2. Test application startup to verify security check passes",
      );
      console.log("   3. Proceed with production deployment");
    }

    // Close the connection
    await pool.end();
    console.log("\n🔚 Database connection closed");

    // Exit with appropriate code
    if (DRY_RUN || plaintextPasswordsFound === 0 || passwordsHashed > 0) {
      console.log("✅ Script completed successfully");
      process.exit(0);
    } else {
      console.log("❌ Script completed with issues");
      process.exit(1);
    }
  } catch (error) {
    console.error("❌ Password hashing failed:", error);
    process.exit(1);
  }
}

// Run the script
hashPasswords().catch((error) => {
  console.error("💥 Unexpected error:", error);
  process.exit(1);
});

