                  <label className="text-sm font-medium">رقم الهاتف *</label>
                  <div className="flex gap-2">
                    <Input
                      placeholder="+966501234567"
                      value={phoneNumber}
                      onChange={(e) => setPhoneNumber(e.target.value)}
                      className="flex-1"
