  }

  .text-success-600 {
    color: hsl(var(--success-600));
  }

  .text-warning-600 {
    color: hsl(var(--warning-600));
  }

  .text-danger-600 {
    color: hsl(var(--danger-600));
  }

  /* Border utilities */
