  // ترجمة الحالات إلى العربية
  static translateStatus(status: string): string {
    const statusMap: Record<string, string> = {
      pending: "في الانتظار",
      for_production: "للإنتاج",
      in_progress: "قيد التنفيذ",
      completed: "مكتمل",
      delivered: "مُسلم",
      active: "نشط",
      maintenance: "صيانة",
      down: "متوقف",
      for_printing: "للطباعة",
      for_cutting: "للقطع",
      done: "منجز",
    };

    return statusMap[status] || status;
  }

  // تحليل بيانات الإنتاج
  static analyzeProductionData(stats: any): string {
    let analysis = "";

    if (stats.productionRate < 70) {
      analysis += "معدل الإنتاج منخفض - يُنصح بمراجعة عمليات الإنتاج. ";
    } else if (stats.productionRate > 90) {
      analysis += "معدل الإنتاج ممتاز! ";
    }

    if (stats.qualityScore < 80) {
      analysis +=
        "نسبة الجودة تحتاج تحسين - يُنصح بمراجعة إجراءات فحص الجودة. ";
    }

    if (stats.wastePercentage > 5) {
      analysis += "نسبة الهدر مرتفعة - يُنصح بتحليل أسباب الهدر وتقليلها. ";
    }

    if (stats.activeOrders > 10) {
      analysis += "عدد كبير من الطلبات النشطة - قد تحتاج لزيادة الإنتاجية. ";
    }

    return analysis || "الأداء ضمن المعدلات الطبيعية.";
  }

  // توليد SQL آمن من النص الطبيعي
  static async generateSQLFromNaturalLanguage(text: string): Promise<string> {
    try {
      const response = await openai.chat.completions.create({
        model: "gpt-4o",
        messages: [
          {
            role: "system",
            content: `حول النص التالي إلى استعلام SQL آمن للجداول التالية:
- customers (العملاء)
- orders (الطلبات)
- production_orders (أوامر التشغيل)
- rolls (الرولات)
- machines (المكائن)
- users (المستخدمين)
- quality_checks (فحص الجودة)
- maintenance_records (سجلات الصيانة)

قواعد مهمة:
1. استخدم فقط SELECT (لا INSERT/UPDATE/DELETE)
2. استخدم معاملات آمنة
3. أضف LIMIT للحد من النتائج
4. تجنب استعلامات معقدة

أرجع فقط SQL بدون شرح أو markdown.`,
          },
          {
            role: "user",
            content: text,
          },
        ],
        temperature: 0.1,
      });

      let sql = response.choices[0].message.content?.trim() || "";

      // إزالة markdown code blocks إذا وجدت
      sql = sql.replace(/```sql\n?/gi, "").replace(/```\n?/g, "").trim();

      // إزالة أي نصوص توضيحية في البداية أو النهاية
      const lines = sql.split("\n");
      const sqlLines = lines.filter(
        (line) =>
          line.trim().toLowerCase().startsWith("select") ||
          line.trim().toLowerCase().startsWith("from") ||
          line.trim().toLowerCase().startsWith("where") ||
          line.trim().toLowerCase().startsWith("order") ||
          line.trim().toLowerCase().startsWith("limit") ||
          line.trim().toLowerCase().startsWith("join") ||
          line.trim().toLowerCase().startsWith("and") ||
          line.trim().toLowerCase().startsWith("or") ||
          line.trim().toLowerCase().startsWith("group") ||
          line.trim().toLowerCase().startsWith("having"),
      );

      sql = sqlLines.join("\n").trim();

      // فحص أمان أساسي
      if (!sql || !sql.toLowerCase().startsWith("select")) {
        console.error("Invalid SQL generated:", sql);
        throw new Error("استعلام غير آمن");
      }

      // فحص إضافي - التأكد من عدم وجود عمليات خطرة
      const dangerousKeywords = [
        "drop",
        "delete",
        "insert",
        "update",
        "alter",
        "truncate",
        "create",
      ];
      const sqlLower = sql.toLowerCase();
      for (const keyword of dangerousKeywords) {
        if (sqlLower.includes(keyword)) {
          console.error("Dangerous SQL detected:", sql);
          throw new Error("استعلام غير آمن");
        }
      }

      return sql;
    } catch (error) {
      console.error("SQL generation error:", error);
      throw new Error("فشل في توليد استعلام SQL من النص");
    }
  }
}

