    queryKey: ["/api/notifications"],
    refetchInterval: false, // Disabled polling to reduce server load
    staleTime: 2 * 60 * 1000, // 2 minutes stale time
    gcTime: 5 * 60 * 1000, // 5 minutes garbage collection
    refetchOnWindowFocus: false, // Prevent unnecessary refetches
  });

  // تنظيف الاستعلامات عند إلغاء تحميل المكون
  useEffect(() => {
    return () => {
      // Cancel all queries for this component when unmounting
      queryClient.cancelQueries({ queryKey: ["/api/notifications"] });
    };
  }, [queryClient]);

  const notificationsList = Array.isArray(notifications) ? notifications : [];

  // إرسال رسالة باستخدام القالب
  const sendTemplateMessage = useMutation({
    mutationFn: async (data: {
      phone: string;
      template: string;
      variables: string[];
      useTemplate: boolean;
    }) => {
      const response = await apiRequest("/api/notifications/whatsapp", {
        method: "POST",
        body: JSON.stringify({
          phone_number: data.phone,
          message: data.variables[0] || "رسالة اختبار",
          title: "رسالة اختبار القالب",
          template_name: data.template,
          variables: data.variables,
          use_template: data.useTemplate,
        }),
      });
      return response;
    },
    onSuccess: (data: any) => {
      toast({
        title: "تم إرسال الرسالة بنجاح",
        description: `تم إرسال رسالة باستخدام القالب إلى ${phoneNumber}`,
      });

      setTestResults((prev) => [
        {
          timestamp: new Date(),
          phone: phoneNumber,
          template: selectedTemplate,
          variables: templateVariables,
          status: "sent",
          messageId: data?.messageId || "unknown",
          success: true,
          useTemplate,
        },
        ...prev,
      ]);

      refetchNotifications();
    },
    onError: (error: any) => {
      toast({
        title: "فشل في الإرسال",
        description: error.message || "حدث خطأ أثناء إرسال الرسالة",
        variant: "destructive",
      });

      setTestResults((prev) => [
        {
          timestamp: new Date(),
          phone: phoneNumber,
          template: selectedTemplate,
          variables: templateVariables,
          status: "failed",
          error: error.message,
          success: false,
          useTemplate,
        },
        ...prev,
      ]);
    },
  });

  const handleSendTest = () => {
    if (!phoneNumber) {
      toast({
        title: "بيانات ناقصة",
        description: "يرجى ملء رقم الهاتف",
        variant: "destructive",
      });
      return;
    }

    if (!phoneNumber.startsWith("+")) {
      toast({
        title: "رقم هاتف غير صحيح",
        description: "يجب أن يبدأ رقم الهاتف بـ +",
        variant: "destructive",
      });
      return;
    }

    sendTemplateMessage.mutate({
      phone: phoneNumber,
      template: selectedTemplate,
      variables: templateVariables,
      useTemplate,
    });
  };

  const addVariable = () => {
    setTemplateVariables([...templateVariables, ""]);
  };

  const updateVariable = (index: number, value: string) => {
    const newVariables = [...templateVariables];
    newVariables[index] = value;
    setTemplateVariables(newVariables);
  };

  const removeVariable = (index: number) => {
    setTemplateVariables(templateVariables.filter((_, i) => i !== index));
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "sent":
        return <CheckCircle className="h-4 w-4 text-green-600" />;
      case "delivered":
        return <CheckCircle className="h-4 w-4 text-blue-600" />;
      case "failed":
        return <XCircle className="h-4 w-4 text-red-600" />;
      default:
        return <MessageSquare className="h-4 w-4 text-yellow-600" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "sent":
        return "bg-green-100 text-green-800";
      case "delivered":
        return "bg-blue-100 text-blue-800";
      case "failed":
        return "bg-red-100 text-red-800";
      default:
        return "bg-yellow-100 text-yellow-800";
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-4" dir="rtl">
      <div className="max-w-6xl mx-auto space-y-6">
        {/* Header */}
        <div className="text-center">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            ✨ اختبار قوالب WhatsApp المُوافقة
          </h1>
          <p className="text-gray-600">
            اختبار إرسال رسائل WhatsApp باستخدام القوالب المُوافق عليها من Meta
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* نموذج الإرسال */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Sparkles className="h-5 w-5" />
                إرسال رسالة بقالب مُوافق
              </CardTitle>
              <CardDescription>
                استخدام القوالب المُوافق عليها من Meta لإرسال الرسائل
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label htmlFor="phone">رقم الهاتف</Label>
                <Input
                  id="phone"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  placeholder="+966501234567"
                  dir="ltr"
                  data-testid="input-phone"
                />
              </div>

              <div>
                <Label htmlFor="template">القالب</Label>
                <Select
                  value={selectedTemplate}
                  onValueChange={setSelectedTemplate}
                >
                  <SelectTrigger data-testid="select-template">
                    <SelectValue placeholder="اختر القالب" />
                  </SelectTrigger>
                  <SelectContent>
                    {approvedTemplates
                      .filter(
                        (template) =>
                          template.id &&
                          template.id !== "" &&
                          template.id !== null &&
                          template.id !== undefined,
                      )
                      .map((template) => (
                        <SelectItem
                          key={template.id}
                          value={template.id.toString()}
                        >
                          <div className="flex items-center gap-2">
                            <span>{template.name}</span>
                            <Badge variant="secondary" className="text-xs">
                              {template.language}
                            </Badge>
                          </div>
                        </SelectItem>
                      ))}
                  </SelectContent>
                </Select>
              </div>

              {/* متغيرات القالب */}
              <div>
                <Label>متغيرات القالب</Label>
                <div className="space-y-2">
                  {templateVariables.map((variable, index) => (
                    <div key={index} className="flex items-center gap-2">
                      <Input
                        value={variable}
                        onChange={(e) => updateVariable(index, e.target.value)}
                        placeholder={`متغير ${index + 1}`}
                        data-testid={`input-variable-${index}`}
                      />
                      <Button
                        type="button"
                        variant="outline"
                        size="sm"
                        onClick={() => removeVariable(index)}
                        data-testid={`button-remove-variable-${index}`}
                      >
                        حذف
                      </Button>
                    </div>
                  ))}
                  <Button
                    type="button"
                    variant="outline"
                    size="sm"
                    onClick={addVariable}
                    data-testid="button-add-variable"
                  >
                    إضافة متغير
                  </Button>
                </div>
              </div>

              <div className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  id="useTemplate"
                  checked={useTemplate}
                  onChange={(e) => setUseTemplate(e.target.checked)}
                  data-testid="checkbox-use-template"
                />
                <Label htmlFor="useTemplate" className="text-sm">
                  استخدام القالب المُوافق عليه (Production Mode)
                </Label>
              </div>

              <Button
                onClick={handleSendTest}
                disabled={sendTemplateMessage.isPending}
                className="w-full"
                data-testid="button-send-template"
              >
                {sendTemplateMessage.isPending ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    جاري الإرسال...
                  </>
                ) : (
                  <>
                    <Send className="mr-2 h-4 w-4" />
                    إرسال رسالة بالقالب
                  </>
                )}
              </Button>
            </CardContent>
          </Card>

          {/* تفاصيل القالب المُختار */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <MessageSquare className="h-5 w-5" />
                تفاصيل القالب
              </CardTitle>
            </CardHeader>
            <CardContent>
              {approvedTemplates.find((t) => t.id === selectedTemplate) && (
                <div className="space-y-4">
                  <div>
                    <Label className="text-sm font-medium">اسم القالب:</Label>
                    <p className="text-sm text-gray-600 font-mono">
                      {
                        approvedTemplates.find((t) => t.id === selectedTemplate)
                          ?.name
                      }
                    </p>
                  </div>

                  <div>
                    <Label className="text-sm font-medium">معرف القالب:</Label>
                    <p className="text-xs text-gray-500 font-mono break-all">
                      {selectedTemplate}
                    </p>
                  </div>

                  <div>
                    <Label className="text-sm font-medium">اللغة:</Label>
                    <Badge variant="outline">
                      {
                        approvedTemplates.find((t) => t.id === selectedTemplate)
                          ?.language
                      }
                    </Badge>
                  </div>

                  <div>
                    <Label className="text-sm font-medium">المتغيرات:</Label>
                    <div className="text-sm text-gray-600">
                      {approvedTemplates
                        .find((t) => t.id === selectedTemplate)
                        ?.variables.join(", ")}
                    </div>
                  </div>

                  <div>
                    <Label className="text-sm font-medium">مثال:</Label>
                    <div className="bg-gray-50 p-3 rounded text-sm font-mono">
                      {
                        approvedTemplates.find((t) => t.id === selectedTemplate)
                          ?.example
                      }
                    </div>
                  </div>

                  <div className="bg-blue-50 p-3 rounded">
                    <p className="text-xs text-blue-700">
                      <strong>حالة القالب:</strong> مُوافق عليه من Meta ✅
                      <br />
                      يمكن استخدامه لإرسال رسائل إلى أي رقم WhatsApp مُسجل
                    </p>
                  </div>
                </div>
              )}
            </CardContent>
          </Card>
        </div>

        {/* سجل النتائج */}
        {testResults.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle>📋 سجل اختبارات القوالب</CardTitle>
              <CardDescription>
                نتائج الرسائل المُرسلة باستخدام القوالب
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                {testResults.map((result, index) => (
                  <div
                    key={index}
                    className="border rounded-lg p-3 bg-white"
                    data-testid={`template-result-${index}`}
                  >
                    <div className="flex items-center justify-between mb-2">
                      <div className="flex items-center gap-2">
                        {getStatusIcon(result.status)}
                        <span className="font-medium">{result.phone}</span>
                        <Badge className={getStatusColor(result.status)}>
                          {result.status}
                        </Badge>
                        {result.useTemplate && (
                          <Badge variant="outline" className="text-xs">
                            قالب
                          </Badge>
                        )}
                      </div>
                      <span className="text-sm text-gray-500">
                        {result.timestamp.toLocaleTimeString("ar")}
                      </span>
                    </div>

                    <div className="space-y-1 text-sm">
                      <p>
                        <strong>القالب:</strong> {result.template}
                      </p>
                      <p>
                        <strong>المتغيرات:</strong>{" "}
                        {result.variables?.join(", ") || "لا يوجد"}
                      </p>
                    </div>

                    {result.messageId && (
                      <p className="text-xs text-gray-500 mt-2">
                        Message ID: {result.messageId}
                      </p>
                    )}

                    {result.error && (
                      <p className="text-xs text-red-600 mt-2">
                        خطأ: {result.error}
                      </p>
                    )}
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        )}

        {/* الإشعارات الأخيرة */}
        {notificationsList && notificationsList.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle>📬 آخر الإشعارات</CardTitle>
              <CardDescription>آخر الرسائل المُرسلة عبر النظام</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                {notificationsList.slice(0, 5).map((notification: any) => (
                  <div
                    key={notification.id}
                    className="border rounded-lg p-3 bg-white"
                    data-testid={`notification-${notification.id}`}
                  >
                    <div className="flex items-center justify-between mb-2">
                      <div className="flex items-center gap-2">
                        <MessageSquare className="h-4 w-4" />
                        <span className="font-medium">
                          {notification.title}
                        </span>
                        <Badge className={getStatusColor(notification.status)}>
                          {notification.status}
                        </Badge>
                      </div>
                      <span className="text-sm text-gray-500">
                        {new Date(notification.created_at).toLocaleString("ar")}
                      </span>
                    </div>

                    <p className="text-sm text-gray-700 mb-1">
                      {notification.message}
                    </p>

                    {notification.phone_number && (
                      <p className="text-xs text-gray-500">
                        إلى: {notification.phone_number}
                      </p>
                    )}
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

client/src/components/ui/toaster.tsx.content:
import { useToast } from "../../hooks/use-toast";
import {
  Toast,
  ToastClose,
  ToastDescription,
  ToastProvider,
  ToastTitle,
  ToastViewport,
} from "./toast";

export function Toaster() {
  const { toasts } = useToast();

  return (
    <ToastProvider>
      {toasts.map(function ({ id, title, description, action, ...props }) {
        return (
          <Toast key={id} {...props}>
            <div className="grid gap-1">
              {title && <ToastTitle>{title}</ToastTitle>}
              {description && (
                <ToastDescription>{description}</ToastDescription>
              )}
            </div>
            {action}
            <ToastClose />
          </Toast>
        );
      })}
      <ToastViewport />
    </ToastProvider>
  );
}

client/src/pages/AlertsCenter.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "../components/ui/card";
import { Button } from "../components/ui/button";
import { Badge } from "../components/ui/badge";
import {
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
} from "../components/ui/tabs";
import { Alert, AlertDescription, AlertTitle } from "../components/ui/alert";
import { ScrollArea } from "../components/ui/scroll-area";
import { Separator } from "../components/ui/separator";
import {
  AlertTriangle,
  Shield,
  Activity,
  Database,
  Factory,
  Package,
  Settings,
  CheckCircle2,
  XCircle,
  Clock,
  Eye,
  AlertCircle,
  TrendingUp,
  Users,
  Bell,
  Filter,
  Search,
  ChevronRight,
  Zap,
} from "lucide-react";
import { Input } from "../components/ui/input";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../components/ui/select";
import { apiRequest } from "../lib/queryClient";
import { useToast } from "../hooks/use-toast";
import { formatDistanceToNow, format } from "date-fns";
import { ar } from "date-fns/locale";

// أنواع البيانات
interface SystemAlert {
  id: number;
  title: string;
  title_ar: string;
  message: string;
  message_ar: string;
  type: string;
  category: string;
  severity: string;
  source: string;
  source_id?: string;
  status: string;
  requires_action: boolean;
  context_data?: Record<string, any>;
  suggested_actions?: {
    action: string;
    priority: number;
    description?: string;
  }[];
  target_users?: number[];
  target_roles?: number[];
  occurrences: number;
  first_occurrence: string;
  last_occurrence: string;
  resolved_by?: number;
  resolved_at?: string;
  created_at: string;
}

interface AlertStats {
  total_alerts: number;
  active_alerts: number;
  critical_alerts: number;
  resolved_today: number;
  by_type: Record<string, number>;
  by_severity: Record<string, number>;
}

interface HealthStatus {
  overall_status: string;
  healthy_checks: number;
  warning_checks: number;
  critical_checks: number;
  last_check: string;
}

/**
 * مركز التحذيرات الذكية الشامل
 */
export default function AlertsCenter() {
  const [searchQuery, setSearchQuery] = useState("");
  const [filterType, setFilterType] = useState<string>("all");
  const [filterSeverity, setFilterSeverity] = useState<string>("all");
  const [filterStatus, setFilterStatus] = useState<string>("active");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // جلب التحذيرات
  const { data: alerts = [], isLoading: alertsLoading } = useQuery<
    SystemAlert[]
  >({
    queryKey: [
      "/api/alerts",
      {
        status: filterStatus,
        type: filterType === "all" ? undefined : filterType,
        severity: filterSeverity === "all" ? undefined : filterSeverity,
      },
    ],
    refetchInterval: 30000, // تحديث كل 30 ثانية
  });

  // جلب إحصائيات التحذيرات
  const { data: stats } = useQuery<AlertStats>({
    queryKey: ["/api/alerts/stats"],
    refetchInterval: 60000, // تحديث كل دقيقة
  });

  // جلب حالة النظام
  const { data: healthStatus } = useQuery<HealthStatus>({
    queryKey: ["/api/system/health"],
    refetchInterval: 30000,
  });

  // حل التحذير
  const resolveAlertMutation = useMutation({
    mutationFn: async ({
      alertId,
      notes,
    }: {
      alertId: number;
      notes?: string;
    }) => {
      return apiRequest(`/api/alerts/${alertId}/resolve`, {
        method: "POST",
        body: JSON.stringify({ notes }),
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/alerts"] });
      queryClient.invalidateQueries({ queryKey: ["/api/alerts/stats"] });
      toast({
        title: "تم حل التحذير بنجاح",
        description: "تم تحديث حالة التحذير",
      });
    },
    onError: (error: any) => {
      toast({
        title: "خطأ في حل التحذير",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // إغلاق التحذير
  const dismissAlertMutation = useMutation({
    mutationFn: async (alertId: number) => {
      return apiRequest(`/api/alerts/${alertId}/dismiss`, {
        method: "POST",
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/alerts"] });
      queryClient.invalidateQueries({ queryKey: ["/api/alerts/stats"] });
      toast({
        title: "تم إغلاق التحذير",
        description: "تم إغلاق التحذير بنجاح",
      });
    },
    onError: (error: any) => {
      toast({
        title: "خطأ في إغلاق التحذير",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // فلترة التحذيرات
  const filteredAlerts = alerts.filter((alert: SystemAlert) => {
    const matchesSearch =
      searchQuery === "" ||
      alert.title_ar.toLowerCase().includes(searchQuery.toLowerCase()) ||
      alert.message_ar.toLowerCase().includes(searchQuery.toLowerCase());

    return matchesSearch;
  });

  // الحصول على أيقونة النوع
  const getTypeIcon = (type: string) => {
    const icons = {
      system: Database,
      production: Factory,
      inventory: Package,
      quality: CheckCircle2,
      maintenance: Settings,
      security: Shield,
      performance: Activity,
    };
    return icons[type as keyof typeof icons] || AlertTriangle;
  };

  // الحصول على أيقونة الخطورة
  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case "critical":
        return "destructive";
      case "high":
        return "destructive";
      case "medium":
        return "warning";
      case "low":
        return "secondary";
      default:
        return "secondary";
    }
  };

  // الحصول على أيقونة الحالة
  const getStatusIcon = (status: string) => {
    switch (status) {
      case "active":
        return AlertTriangle;
      case "resolved":
        return CheckCircle2;
      case "dismissed":
        return XCircle;
      default:
        return Clock;
    }
  };

  return (
    <div className="container mx-auto p-6 space-y-6" dir="rtl">
      {/* رأس الصفحة */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
            مركز التحذيرات الذكية
          </h1>
          <p className="text-gray-600 dark:text-gray-300 mt-2">
            مراقبة شاملة لحالة النظام والتحذيرات الاستباقية
          </p>
        </div>
        <div className="flex items-center gap-2">
          <Badge variant="outline" className="text-sm">
            <Activity className="w-4 h-4 ml-1" />
            مراقبة مباشرة
          </Badge>
        </div>
      </div>

      {/* ملخص حالة النظام */}
      {healthStatus && (
        <Card className="border-2">
          <CardHeader className="pb-3">
            <CardTitle className="flex items-center gap-2">
              <Shield className="w-5 h-5" />
              حالة النظام العامة
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div className="text-center">
                <div
                  className={`text-2xl font-bold ${
                    healthStatus.overall_status === "healthy"
                      ? "text-green-600"
                      : healthStatus.overall_status === "warning"
                        ? "text-yellow-600"
                        : "text-red-600"
                  }`}
                >
                  {healthStatus.overall_status === "healthy"
                    ? "سليم"
                    : healthStatus.overall_status === "warning"
                      ? "تحذير"
                      : "خطر"}
                </div>
                <div className="text-sm text-gray-600 dark:text-gray-300">
                  الحالة العامة
                </div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-green-600">
                  {healthStatus.healthy_checks}
                </div>
                <div className="text-sm text-gray-600 dark:text-gray-300">
                  فحوصات سليمة
                </div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-yellow-600">
                  {healthStatus.warning_checks}
                </div>
                <div className="text-sm text-gray-600 dark:text-gray-300">
                  تحذيرات
                </div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-red-600">
                  {healthStatus.critical_checks}
                </div>
                <div className="text-sm text-gray-600 dark:text-gray-300">
                  حالات خطرة
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* إحصائيات التحذيرات */}
      {stats && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-300">
                    إجمالي التحذيرات
                  </p>
                  <p className="text-2xl font-bold">{stats.total_alerts}</p>
                </div>
                <Bell className="w-8 h-8 text-blue-600" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-300">
                    تحذيرات نشطة
                  </p>
                  <p className="text-2xl font-bold text-orange-600">
                    {stats.active_alerts}
                  </p>
                </div>
                <AlertTriangle className="w-8 h-8 text-orange-600" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-300">
                    تحذيرات حرجة
                  </p>
                  <p className="text-2xl font-bold text-red-600">
                    {stats.critical_alerts}
                  </p>
                </div>
                <AlertCircle className="w-8 h-8 text-red-600" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-300">
                    تم حلها اليوم
                  </p>
                  <p className="text-2xl font-bold text-green-600">
                    {stats.resolved_today}
                  </p>
                </div>
                <CheckCircle2 className="w-8 h-8 text-green-600" />
              </div>
            </CardContent>
          </Card>
        </div>
      )}

      {/* الفلاتر والبحث */}
      <Card>
        <CardContent className="p-6">
          <div className="flex flex-col md:flex-row gap-4">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                <Input
                  placeholder="البحث في التحذيرات..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pr-10"
                  data-testid="input-search-alerts"
                />
              </div>
            </div>

            <Select value={filterStatus} onValueChange={setFilterStatus}>
              <SelectTrigger
                className="w-40"
                data-testid="select-filter-status"
              >
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">جميع الحالات</SelectItem>
                <SelectItem value="active">نشطة</SelectItem>
                <SelectItem value="resolved">محلولة</SelectItem>
                <SelectItem value="dismissed">مغلقة</SelectItem>
              </SelectContent>
            </Select>

            <Select value={filterType} onValueChange={setFilterType}>
              <SelectTrigger className="w-40" data-testid="select-filter-type">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">جميع الأنواع</SelectItem>
                <SelectItem value="system">نظام</SelectItem>
                <SelectItem value="production">إنتاج</SelectItem>
                <SelectItem value="inventory">مخزون</SelectItem>
                <SelectItem value="quality">جودة</SelectItem>
                <SelectItem value="maintenance">صيانة</SelectItem>
                <SelectItem value="security">أمان</SelectItem>
              </SelectContent>
            </Select>

            <Select value={filterSeverity} onValueChange={setFilterSeverity}>
              <SelectTrigger
                className="w-40"
                data-testid="select-filter-severity"
              >
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">جميع المستويات</SelectItem>
                <SelectItem value="critical">حرج</SelectItem>
                <SelectItem value="high">عالي</SelectItem>
                <SelectItem value="medium">متوسط</SelectItem>
                <SelectItem value="low">منخفض</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* قائمة التحذيرات */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <AlertTriangle className="w-5 h-5" />
            التحذيرات ({filteredAlerts.length})
          </CardTitle>
        </CardHeader>
        <CardContent>
          <ScrollArea className="h-[600px]">
            {alertsLoading ? (
              <div className="flex items-center justify-center h-32">
                <div className="text-center">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
                  <p className="mt-2 text-sm text-gray-600 dark:text-gray-300">
                    جاري تحميل التحذيرات...
                  </p>
                </div>
              </div>
            ) : filteredAlerts.length === 0 ? (
              <div className="text-center py-8">
                <CheckCircle2 className="w-16 h-16 text-green-600 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                  لا توجد تحذيرات
                </h3>
                <p className="text-gray-600 dark:text-gray-300">
                  رائع! لا توجد تحذيرات تطابق المعايير المحددة
                </p>
              </div>
            ) : (
              <div className="space-y-4">
                {filteredAlerts.map((alert: SystemAlert) => {
                  const TypeIcon = getTypeIcon(alert.type);
                  const StatusIcon = getStatusIcon(alert.status);

                  return (
                    <Card
                      key={alert.id}
                      className={`border-r-4 ${
                        alert.severity === "critical"
                          ? "border-r-red-500"
                          : alert.severity === "high"
                            ? "border-r-orange-500"
                            : alert.severity === "medium"
                              ? "border-r-yellow-500"
                              : "border-r-blue-500"
                      }`}
                    >
                      <CardContent className="p-4">
                        <div className="flex items-start justify-between">
                          <div className="flex items-start gap-3 flex-1">
                            <div className="p-2 rounded-lg bg-gray-100 dark:bg-gray-800">
                              <TypeIcon className="w-5 h-5 text-gray-700 dark:text-gray-300" />
                            </div>

                            <div className="flex-1">
                              <div className="flex items-center gap-2 mb-2">
                                <h3 className="font-semibold text-gray-900 dark:text-white">
                                  {alert.title_ar}
                                </h3>
                                <Badge
                                  variant={getSeverityColor(alert.severity)}
                                >
                                  {alert.severity === "critical"
                                    ? "حرج"
                                    : alert.severity === "high"
                                      ? "عالي"
                                      : alert.severity === "medium"
                                        ? "متوسط"
                                        : "منخفض"}
                                </Badge>
                                <Badge variant="outline">
                                  {alert.type === "system"
                                    ? "نظام"
                                    : alert.type === "production"
                                      ? "إنتاج"
                                      : alert.type === "inventory"
                                        ? "مخزون"
                                        : alert.type === "quality"
                                          ? "جودة"
                                          : alert.type === "maintenance"
                                            ? "صيانة"
                                            : alert.type === "security"
                                              ? "أمان"
                                              : alert.type}
                                </Badge>
                              </div>

                              <p className="text-gray-600 dark:text-gray-300 mb-3">
                                {alert.message_ar}
                              </p>

                              <div className="flex items-center gap-4 text-sm text-gray-500 dark:text-gray-400">
                                <div className="flex items-center gap-1">
                                  <StatusIcon className="w-4 h-4" />
                                  {alert.status === "active"
                                    ? "نشط"
                                    : alert.status === "resolved"
                                      ? "محلول"
                                      : alert.status === "dismissed"
                                        ? "مغلق"
                                        : alert.status}
                                </div>
                                <div className="flex items-center gap-1">
                                  <Clock className="w-4 h-4" />
                                  {formatDistanceToNow(
                                    new Date(alert.created_at),
                                    {
                                      addSuffix: true,
                                      locale: ar,
                                    },
                                  )}
                                </div>
                                {alert.occurrences > 1 && (
                                  <div className="flex items-center gap-1">
                                    <TrendingUp className="w-4 h-4" />
                                    {alert.occurrences} مرة
                                  </div>
                                )}
                              </div>

                              {/* الإجراءات المقترحة */}
                              {alert.suggested_actions &&
                                alert.suggested_actions.length > 0 && (
                                  <div className="mt-3 p-3 bg-blue-50 dark:bg-blue-950/20 rounded-lg">
                                    <p className="text-sm font-medium text-blue-900 dark:text-blue-100 mb-2">
                                      إجراءات مقترحة:
                                    </p>
                                    <ul className="text-sm text-blue-800 dark:text-blue-200 space-y-1">
                                      {alert.suggested_actions.map(
                                        (action, index) => (
                                          <li
                                            key={index}
                                            className="flex items-center gap-2"
                                          >
                                            <ChevronRight className="w-3 h-3" />
                                            {action.description ||
                                              action.action}
                                          </li>
                                        ),
                                      )}
                                    </ul>
                                  </div>
                                )}
                            </div>
                          </div>

                          {/* أزرار الإجراءات */}
                          {alert.status === "active" && (
                            <div className="flex gap-2">
                              <Button
                                size="sm"
                                onClick={() =>
                                  resolveAlertMutation.mutate({
                                    alertId: alert.id,
                                  })
                                }
                                disabled={resolveAlertMutation.isPending}
                                data-testid={`button-resolve-${alert.id}`}
                              >
                                <CheckCircle2 className="w-4 h-4 ml-1" />
                                حل
                              </Button>
                              <Button
                                size="sm"
                                variant="outline"
                                onClick={() =>
                                  dismissAlertMutation.mutate(alert.id)
                                }
                                disabled={dismissAlertMutation.isPending}
                                data-testid={`button-dismiss-${alert.id}`}
                              >
                                <XCircle className="w-4 h-4 ml-1" />
                                إغلاق
                              </Button>
                            </div>
                          )}
                        </div>
                      </CardContent>
                    </Card>
                  );
                })}
              </div>
            )}
          </ScrollArea>
        </CardContent>
      </Card>
    </div>
  );
}

