                        <p>
                          <strong>النطاق:</strong> {location.allowed_radius} متر
                        </p>
                      </div>
                    </div>
                    <div className="flex gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => toggleActiveMutation.mutate({ 
                          id: location.id, 
                          isActive: location.is_active 
                        })}
