                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleEdit(location)}
                        data-testid={`button-edit-${location.id}`}
                      >
                        تعديل
                      </Button>
                      <Button
                        variant="destructive"
                        size="sm"
                        onClick={() => deleteMutation.mutate(location.id)}
                        data-testid={`button-delete-${location.id}`}
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : (
          <p className="text-center text-gray-500 py-8">لا توجد مواقع مضافة بعد</p>
        )}
      </div>

      {/* Add/Edit form */}
      {showForm && (
        <Card>
          <CardHeader>
            <CardTitle>{editingLocation ? "تعديل الموقع" : "إضافة موقع جديد"}</CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            <div className="grid gap-4 md:grid-cols-2">
              <div className="space-y-2">
                <Label htmlFor="name-en">الاسم (English)</Label>
                <Input
                  id="name-en"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  placeholder="Main Factory"
                  data-testid="input-name-en"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="name-ar">الاسم (عربي)</Label>
                <Input
                  id="name-ar"
                  value={nameAr}
                  onChange={(e) => setNameAr(e.target.value)}
                  placeholder="المصنع الرئيسي"
                  data-testid="input-name-ar"
                />
              </div>
            </div>

            <div className="space-y-2">
              <Label htmlFor="description">الوصف (اختياري)</Label>
              <Input
                id="description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                placeholder="وصف الموقع..."
                data-testid="input-description"
              />
            </div>

            <div className="space-y-2">
              <Label>اختر الموقع من الخريطة</Label>
              <LocationMapPicker
                latitude={latitude}
                longitude={longitude}
                radius={radius}
                onLocationChange={(lat, lng) => {
                  setLatitude(lat);
                  setLongitude(lng);
                }}
                editable={true}
              />
              <p className="text-xs text-gray-500">
                انقر على الخريطة لتحديد الموقع
              </p>
            </div>

            <div className="grid gap-4 md:grid-cols-3">
              <div className="space-y-2">
                <Label htmlFor="lat">دائرة العرض</Label>
                <Input
                  id="lat"
                  type="number"
                  step="0.0001"
                  value={latitude}
                  onChange={(e) => setLatitude(parseFloat(e.target.value))}
                  data-testid="input-lat"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="lng">خط الطول</Label>
                <Input
                  id="lng"
                  type="number"
                  step="0.0001"
                  value={longitude}
                  onChange={(e) => setLongitude(parseFloat(e.target.value))}
                  data-testid="input-lng"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="radius">النطاق (متر)</Label>
                <Input
                  id="radius"
                  type="number"
                  value={radius}
                  onChange={(e) => setRadius(parseInt(e.target.value))}
                  data-testid="input-radius"
                />
              </div>
            </div>

            <div className="flex gap-2">
              <Button
                onClick={handleSubmit}
                disabled={createMutation.isPending || updateMutation.isPending}
                className="flex-1"
                data-testid="button-submit-location"
              >
                <Save className="w-4 h-4 ml-2" />
                {editingLocation ? "تحديث الموقع" : "إضافة الموقع"}
              </Button>
              <Button
                variant="outline"
                onClick={resetForm}
                data-testid="button-cancel-form"
              >
                إلغاء
              </Button>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

