  async getFilmQueue(): Promise<ProductionOrder[]> {
    try {
      // Optimized: Reduce JOINs and simplify query for better performance
      // الطلبات تبقى في قائمة الفيلم حتى يتم إنتاج الكمية الكاملة
      const results = await db
        .select({
