  async getUserNotifications(
    userId: number,
    options?: { unreadOnly?: boolean; limit?: number; offset?: number },
  ): Promise<Notification[]> {
    return withDatabaseErrorHandling(
      async () => {
        if (!userId || typeof userId !== "number" || userId <= 0) {
          throw new Error("معرف المستخدم مطلوب");
        }

        const user = await this.getSafeUser(userId);
        if (!user) {
          throw new Error("المستخدم غير موجود");
        }

        const limit = options?.limit || 50;
        const offset = options?.offset || 0;

        const roleCondition = user.role_id
          ? and(
              eq(notifications.recipient_type, "role"),
              eq(notifications.recipient_id, user.role_id.toString()),
            )
          : undefined;

        const conditions = [
          eq(notifications.recipient_id, userId.toString()),
          and(
            eq(notifications.recipient_type, "all"),
            eq(notifications.type, "system"),
          ),
        ];

        if (roleCondition) {
          conditions.push(roleCondition);
        }

        let query = db
          .select()
          .from(notifications)
          .where(or(...conditions))
          .orderBy(desc(notifications.created_at))
          .limit(limit)
          .offset(offset);

        // Add unread filter if specified
        if (options?.unreadOnly) {
          query = db
            .select()
            .from(notifications)
            .where(
              and(
                or(...conditions),
                sql`${notifications.read_at} IS NULL`,
              ),
            )
            .orderBy(desc(notifications.created_at))
            .limit(limit)
            .offset(offset);
        }

        return await query;
      },
      "جلب إشعارات المستخدم",
      `المستخدم رقم ${userId}`,
    );
  }

  async markNotificationAsRead(notificationId: number): Promise<Notification> {
    return withDatabaseErrorHandling(
      async () => {
        if (
          !notificationId ||
          typeof notificationId !== "number" ||
          notificationId <= 0
        ) {
          throw new Error("معرف الإشعار غير صحيح");
        }

        const [notification] = await db
          .update(notifications)
          .set({
            read_at: new Date(),
            status: "read",
            updated_at: new Date(),
          })
          .where(eq(notifications.id, notificationId))
          .returning();

        if (!notification) {
          throw new Error("الإشعار غير موجود");
        }

        return notification;
      },
      "تعليم الإشعار كمقروء",
      `الإشعار رقم ${notificationId}`,
    );
  }

  async markAllNotificationsAsRead(userId: number): Promise<void> {
    return withDatabaseErrorHandling(
      async () => {
        if (!userId || typeof userId !== "number" || userId <= 0) {
          throw new Error("معرف المستخدم مطلوب");
        }

        await db
          .update(notifications)
          .set({
            read_at: new Date(),
            status: "read",
            updated_at: new Date(),
          })
          .where(
            and(
              or(
                eq(notifications.recipient_id, userId.toString()),
                eq(notifications.recipient_type, "all"),
              ),
              sql`${notifications.read_at} IS NULL`,
            ),
          );
      },
      "تعليم جميع الإشعارات كمقروءة",
      `المستخدم رقم ${userId}`,
    );
  }

