    // كمية الإنتاج الأساسية
    quantity_kg: decimal("quantity_kg", { precision: 10, scale: 2 }).notNull(), // الكمية المطلوبة من الطلب
    overrun_percentage: decimal("overrun_percentage", {
      precision: 5,
      scale: 2,
    })
      .notNull()
      .default("5.00"),
    final_quantity_kg: decimal("final_quantity_kg", {
      precision: 10,
