// 📦 MPBF Next - Drizzle ORM Schema (TypeScript)
// مخصص لمشروع مصنع الأكياس البلاستيكية - النسخة المتطورة

import {
  pgTable,
  serial,
  varchar,
  integer,
  boolean,
  date,
  timestamp,
  json,
  text,
} from "drizzle-orm/pg-core";

// 🧑‍💼 جدول الموظفين (users)
export const users = pgTable("users", {
  id: serial("id").primaryKey(),
