  const createSparePartMutation = useMutation({
    mutationFn: (data: any) =>
      apiRequest("/api/spare-parts", {
        method: "POST",
        body: JSON.stringify(data),
      }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/spare-parts"] });
      toast({ title: "تم إنشاء قطعة الغيار بنجاح" });
