import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  Legend,
} from "recharts";
import { Card, CardContent, CardHeader, CardTitle } from "../ui/card";

interface InteractiveBarChartProps {
  data: any[];
  title: string;
  description?: string;
  xAxisKey: string;
  yAxisKey: string;
  barColor?: string;
  height?: number;
  showLegend?: boolean;
  formatValue?: (value: any) => string;
  className?: string;
}

const CustomTooltip = ({ active, payload, label, formatValue }: any) => {
  if (active && payload && payload.length) {
    return (
      <div
        className="bg-white p-3 border border-gray-300 rounded-lg shadow-lg"
        dir="rtl"
      >
        <p className="font-medium text-gray-900">{`${label}`}</p>
        {payload.map((entry: any, index: number) => (
          <p key={index} className="text-sm" style={{ color: entry.color }}>
            {`${entry.name}: ${formatValue ? formatValue(entry.value) : entry.value}`}
          </p>
        ))}
      </div>
    );
  }
  return null;
};

export function InteractiveBarChart({
  data,
  title,
  description,
  xAxisKey,
  yAxisKey,
  barColor = "#3b82f6",
  height = 300,
  showLegend = false,
  formatValue,
  className = "",
}: InteractiveBarChartProps) {
  return (
    <Card className={`${className}`} data-testid="chart-interactive-bar">
      <CardHeader>
        <CardTitle
          className="text-lg font-semibold text-gray-900"
          data-testid="text-chart-title"
        >
          {title}
        </CardTitle>
        {description && (
          <p
            className="text-sm text-gray-600"
            data-testid="text-chart-description"
          >
            {description}
          </p>
        )}
      </CardHeader>
      <CardContent>
        <ResponsiveContainer width="100%" height={height}>
          <BarChart
            data={data}
            margin={{
              top: 10,
              right: 30,
              left: 20,
              bottom: 5,
            }}
          >
            <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
            <XAxis
              dataKey={xAxisKey}
              tick={{ fontSize: 12, fill: "#374151" }}
              tickLine={{ stroke: "#d1d5db" }}
              axisLine={{ stroke: "#d1d5db" }}
            />
            <YAxis
              tick={{ fontSize: 12, fill: "#374151" }}
              tickLine={{ stroke: "#d1d5db" }}
              axisLine={{ stroke: "#d1d5db" }}
              tickFormatter={formatValue}
            />
            <Tooltip content={<CustomTooltip formatValue={formatValue} />} />
            {showLegend && <Legend />}
            <Bar
              dataKey={yAxisKey}
              fill={barColor}
              radius={[4, 4, 0, 0]}
              stroke={barColor}
              strokeWidth={1}
            />
          </BarChart>
        </ResponsiveContainer>
      </CardContent>
    </Card>
  );
}

server/services/notification-service.ts.content:
import twilio from "twilio";
import type { IStorage } from "../storage";
import { MetaWhatsAppService } from "./meta-whatsapp";

export interface NotificationData {
  title: string;
  title_ar?: string;
  message: string;
  message_ar?: string;
  type: "whatsapp" | "sms" | "email" | "push" | "system";
  priority?: "low" | "normal" | "high" | "urgent";
  recipient_type: "user" | "group" | "role" | "all";
  recipient_id?: string;
  phone_number?: string;
  context_type?: string;
  context_id?: string;
  scheduled_for?: Date;
}

export interface WhatsAppTemplate {
  name: string;
  variables?: string[];
  language?: string;
}

export class NotificationService {
  private twilioClient: twilio.Twilio;
  public metaWhatsApp: MetaWhatsAppService;
  private storage: IStorage;
  private twilioPhoneNumber: string;
  private useMetaAPI: boolean;

  constructor(storage: IStorage) {
    this.storage = storage;

    // تحديد استخدام Meta API أو Twilio
    // نستخدم Twilio افتراضياً لأن Meta Access Token غير صالح
    this.useMetaAPI = false; // !!( process.env.META_ACCESS_TOKEN && process.env.META_PHONE_NUMBER_ID );

    // تهيئة Meta WhatsApp API
    this.metaWhatsApp = new MetaWhatsAppService(storage);

    // Initialize Twilio client
    const accountSid = process.env.TWILIO_ACCOUNT_SID;
    const authToken = process.env.TWILIO_AUTH_TOKEN;
    this.twilioPhoneNumber = process.env.TWILIO_PHONE_NUMBER || "";

    if (!accountSid || !authToken) {
      console.warn(
        "Twilio credentials not found. WhatsApp messaging via Twilio will be disabled.",
      );
      this.twilioClient = null as any;
    } else {
      this.twilioClient = twilio(accountSid, authToken);
      console.log("✅ Twilio WhatsApp service initialized successfully");
    }

    if (this.useMetaAPI) {
      console.log("🚀 Using Meta WhatsApp Business API directly");
    } else {
      console.log("📱 Using Twilio as WhatsApp gateway");
    }
  }

  /**
   * إرسال رسالة واتس اب باستخدام قالب مُوافق عليه
   */
  async sendWhatsAppTemplateMessage(
    phoneNumber: string,
    templateName: string,
    variables: string[] = [],
    options?: {
      title?: string;
      priority?: string;
      context_type?: string;
      context_id?: string;
    },
  ): Promise<{ success: boolean; messageId?: string; error?: string }> {
    try {
      if (!this.twilioClient) {
        throw new Error("خدمة Twilio غير مُعدة بشكل صحيح");
      }

      const formattedNumber = phoneNumber.startsWith("whatsapp:")
        ? phoneNumber
        : `whatsapp:${phoneNumber}`;

      // التحقق من وجود ContentSid في متغيرات البيئة
      const contentSid = process.env.TWILIO_CONTENT_SID;

      let messageData: any;

      if (
        contentSid &&
        templateName.includes("welcome_hxc4485f514cb7d4536026fc56250f75e7")
      ) {
        // استخدام Content Template المُعد في Twilio Console
        messageData = {
          from: `whatsapp:${this.twilioPhoneNumber}`,
          to: formattedNumber,
          contentSid: contentSid,
        };

        // إضافة متغيرات القالب
        if (variables && variables.length > 0) {
          messageData.contentVariables = JSON.stringify({
            "1": variables[0] || "مرحباً من نظام MPBF",
          });
        }
      } else {
        // استخدام النص المباشر كحل بديل
        messageData = {
          from: `whatsapp:${this.twilioPhoneNumber}`,
          to: formattedNumber,
          body: variables[0] || "مرحباً من نظام MPBF",
        };

        console.warn(
          "⚠️ TWILIO_CONTENT_SID not configured. Using direct text message. Visit /twilio-content for setup instructions.",
        );
      }

      const twilioMessage =
        await this.twilioClient.messages.create(messageData);

      // Save notification to database
      const notificationData = {
        title: options?.title || "إشعار واتس اب",
        message: `قالب: ${templateName} - متغيرات: ${variables.join(", ")}`,
        type: "whatsapp" as const,
        priority: options?.priority || "normal",
        recipient_type: "user" as const,
        phone_number: phoneNumber,
        status: "sent" as const,
        twilio_sid: twilioMessage.sid,
        external_status: twilioMessage.status,
        sent_at: new Date(),
        context_type: options?.context_type,
        context_id: options?.context_id,
      };

      await this.storage.createNotification(notificationData);

      console.log(
        `📱 تم إرسال رسالة واتس اب (قالب) إلى ${phoneNumber} - SID: ${twilioMessage.sid}`,
      );

      return {
        success: true,
        messageId: twilioMessage.sid,
      };
    } catch (error: any) {
      console.error("خطأ في إرسال رسالة واتس اب (قالب):", error);

      const notificationData = {
        title: options?.title || "إشعار واتس اب",
        message: `قالب: ${templateName} - خطأ: ${error.message}`,
        type: "whatsapp" as const,
        priority: options?.priority || "normal",
        recipient_type: "user" as const,
        phone_number: phoneNumber,
        status: "failed" as const,
        error_message: error.message,
        context_type: options?.context_type,
        context_id: options?.context_id,
      };

      await this.storage.createNotification(notificationData);

      return {
        success: false,
        error: error.message,
      };
    }
  }

  /**
   * إرسال إشعار واتس اب (يختار API المناسب تلقائياً)
   */
  async sendWhatsAppMessage(
    phoneNumber: string,
    message: string,
    options?: {
      title?: string;
      priority?: string;
      context_type?: string;
      context_id?: string;
      useTemplate?: boolean;
      templateName?: string;
    },
  ): Promise<{ success: boolean; messageId?: string; error?: string }> {
    if (this.useMetaAPI) {
      // استخدام Meta API مباشرة
      if (options?.useTemplate && options?.templateName) {
        return this.metaWhatsApp.sendTemplateMessage(
          phoneNumber,
          options.templateName,
          "ar",
          [message],
          options,
        );
      } else {
        return this.metaWhatsApp.sendTextMessage(phoneNumber, message, options);
      }
    } else {
      // استخدام Twilio (القديم)
      if (options?.useTemplate) {
        return this.sendWhatsAppTemplateMessage(
          phoneNumber,
          options.templateName || "welcome_hxc4485f514cb7d4536026fc56250f75e7",
          [message],
          options,
        );
      } else {
        return this.sendWhatsAppDirectMessage(phoneNumber, message, options);
      }
    }
  }

  /**
   * إرسال رسالة واتس اب مباشرة (للاختبار فقط في Sandbox)
   */
  async sendWhatsAppDirectMessage(
    phoneNumber: string,
    message: string,
    options?: {
      title?: string;
      priority?: string;
      context_type?: string;
      context_id?: string;
    },
  ): Promise<{ success: boolean; messageId?: string; error?: string }> {
    try {
      if (!this.twilioClient) {
        throw new Error("خدمة Twilio غير مُعدة بشكل صحيح");
      }

      // Format phone number for WhatsApp (must include whatsapp: prefix)
      const formattedNumber = phoneNumber.startsWith("whatsapp:")
        ? phoneNumber
        : `whatsapp:${phoneNumber}`;

      // Send WhatsApp message via Twilio
      const twilioMessage = await this.twilioClient.messages.create({
        body: message,
        from: `whatsapp:${this.twilioPhoneNumber}`,
        to: formattedNumber,
      });

      // Save notification to database
      const notificationData = {
        title: options?.title || "إشعار واتس اب",
        message: message,
        type: "whatsapp" as const,
        priority: options?.priority || "normal",
        recipient_type: "user" as const,
        phone_number: phoneNumber,
        status: "sent" as const,
        twilio_sid: twilioMessage.sid,
        external_status: twilioMessage.status,
        sent_at: new Date(),
        context_type: options?.context_type,
        context_id: options?.context_id,
      };

      await this.storage.createNotification(notificationData);

      console.log(
        `📱 تم إرسال رسالة واتس اب إلى ${phoneNumber} - SID: ${twilioMessage.sid}`,
      );

      return {
        success: true,
        messageId: twilioMessage.sid,
      };
    } catch (error: any) {
      console.error("خطأ في إرسال رسالة واتس اب:", error);

      // Save failed notification to database
      const notificationData = {
        title: options?.title || "إشعار واتس اب",
        message: message,
        type: "whatsapp" as const,
        priority: options?.priority || "normal",
        recipient_type: "user" as const,
        phone_number: phoneNumber,
        status: "failed" as const,
        error_message: error.message,
        context_type: options?.context_type,
        context_id: options?.context_id,
      };

      await this.storage.createNotification(notificationData);

      return {
        success: false,
        error: error.message,
      };
    }
  }

  /**
   * إرسال إشعار إلى مستخدم محدد
   */
  async notifyUser(
    userId: number,
    notificationData: NotificationData,
  ): Promise<boolean> {
    try {
      // Get user details
      const user = await this.storage.getUserById(userId);
      if (!user) {
        console.warn(`المستخدم ${userId} غير موجود`);
        return false;
      }

      // Check if user has phone number for WhatsApp
      if (notificationData.type === "whatsapp" && user.phone) {
        const result = await this.sendWhatsAppMessage(
          user.phone,
          notificationData.message_ar || notificationData.message,
          {
            title: notificationData.title_ar || notificationData.title,
            priority: notificationData.priority,
            context_type: notificationData.context_type,
            context_id: notificationData.context_id,
          },
        );
        return result.success;
      }

      // Save as system notification if WhatsApp not available
      const dbNotification = {
        ...notificationData,
        recipient_type: "user" as const,
        recipient_id: userId.toString(),
        status: "sent" as const,
      };

      await this.storage.createNotification(dbNotification);
      return true;
    } catch (error: any) {
      console.error(`خطأ في إرسال الإشعار للمستخدم ${userId}:`, error);
      return false;
    }
  }

  /**
   * إرسال إشعارات جماعية بناءً على الدور
   */
  async notifyByRole(
    roleId: number,
    notificationData: NotificationData,
  ): Promise<number> {
    try {
      const users = await this.storage.getUsersByRole(roleId);
      let successCount = 0;

      for (const user of users) {
        const success = await this.notifyUser(user.id, notificationData);
        if (success) successCount++;

        // Add small delay to avoid rate limiting
        await new Promise((resolve) => setTimeout(resolve, 100));
      }

      console.log(
        `تم إرسال ${successCount} إشعار من أصل ${users.length} للدور ${roleId}`,
      );
      return successCount;
    } catch (error: any) {
      console.error(`خطأ في إرسال الإشعارات للدور ${roleId}:`, error);
      return 0;
    }
  }

  /**
   * معالجة إشعارات الحضور
   */
  async notifyAttendanceEvent(
    userId: number,
    eventType: "check_in" | "check_out" | "lunch_start" | "lunch_end" | "late",
    additionalInfo?: any,
  ): Promise<boolean> {
    const messages = {
      check_in: "تم تسجيل دخولك بنجاح ✅",
      check_out: "تم تسجيل خروجك بنجاح 👋",
      lunch_start: "تم تسجيل بداية استراحة الغداء 🍽️",
      lunch_end: "تم تسجيل انتهاء استراحة الغداء ✅",
      late: "تنبيه: تم تسجيل تأخير في الحضور ⚠️",
    };

    const titles = {
      check_in: "تسجيل الدخول",
      check_out: "تسجيل الخروج",
      lunch_start: "استراحة الغداء",
      lunch_end: "العودة من الاستراحة",
      late: "تنبيه تأخير",
    };

    const notificationData: NotificationData = {
      title: titles[eventType],
      message: messages[eventType],
      type: "whatsapp",
      priority: eventType === "late" ? "high" : "normal",
      recipient_type: "user",
      context_type: "attendance",
      context_id: additionalInfo?.attendanceId?.toString(),
    };

    return await this.notifyUser(userId, notificationData);
  }

  /**
   * معالجة إشعارات الطلبات
   */
  async notifyOrderEvent(
    orderNumber: string,
    eventType: "created" | "completed" | "delayed" | "cancelled",
    userIds?: number[],
  ): Promise<number> {
    const messages = {
      created: `تم إنشاء طلب جديد: ${orderNumber} 📦`,
      completed: `تم إكمال الطلب: ${orderNumber} ✅`,
      delayed: `تأخير في الطلب: ${orderNumber} ⚠️`,
      cancelled: `تم إلغاء الطلب: ${orderNumber} ❌`,
    };

    const titles = {
      created: "طلب جديد",
      completed: "اكتمال طلب",
      delayed: "تأخير طلب",
      cancelled: "إلغاء طلب",
    };

    const notificationData: NotificationData = {
      title: titles[eventType],
      message: messages[eventType],
      type: "whatsapp",
      priority: eventType === "delayed" ? "high" : "normal",
      recipient_type: userIds ? "user" : "role",
      context_type: "order",
      context_id: orderNumber,
    };

    if (userIds && userIds.length > 0) {
      let successCount = 0;
      for (const userId of userIds) {
        const success = await this.notifyUser(userId, notificationData);
        if (success) successCount++;
        await new Promise((resolve) => setTimeout(resolve, 100));
      }
      return successCount;
    } else {
      // Notify managers and supervisors (role_id = 2)
      return await this.notifyByRole(2, notificationData);
    }
  }

  /**
   * الحصول على حالة الرسالة من Twilio
   */
  async updateMessageStatus(twilioSid: string): Promise<boolean> {
    try {
      if (!this.twilioClient) return false;

      const message = await this.twilioClient.messages(twilioSid).fetch();

      // Update notification status in database
      await this.storage.updateNotificationStatus(twilioSid, {
        external_status: message.status,
        delivered_at: message.status === "delivered" ? new Date() : undefined,
        error_message: message.errorMessage || undefined,
      });

      return true;
    } catch (error: any) {
      console.error(`خطأ في تحديث حالة الرسالة ${twilioSid}:`, error);
      return false;
    }
  }

  /**
   * إرسال رسالة اختبار
   */
  async sendTestMessage(
    phoneNumber: string,
  ): Promise<{ success: boolean; message?: string; error?: string }> {
    const testMessage = `
🔧 رسالة اختبار من نظام إدارة المصنع

مرحباً! هذه رسالة اختبار للتأكد من عمل خدمة الواتس اب بشكل صحيح.

⏰ التوقيت: ${new Date().toLocaleString("ar")}
✅ الخدمة تعمل بنجاح

شكراً لاستخدام نظامنا! 
    `.trim();

    const result = await this.sendWhatsAppMessage(phoneNumber, testMessage, {
      title: "رسالة اختبار",
      priority: "normal",
      context_type: "system",
      context_id: "test",
    });

    return {
      success: result.success,
      message: result.success ? "تم إرسال رسالة الاختبار بنجاح" : undefined,
      error: result.error,
    };
  }
}

client/src/components/orders/OrdersTable.content:
import { Badge } from "../ui/badge";
import { Button } from "../ui/button";
import { Checkbox } from "../ui/checkbox";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "../ui/table";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "../ui/dropdown-menu";
import {
  FileText,
  Eye,
  Trash2,
  Edit,
  RefreshCw,
  ChevronDown,
} from "lucide-react";
import { format } from "date-fns";
import ProductionProgress from "./ProductionProgress";

interface OrdersTableProps {
  orders: any[];
  customers: any[];
  users: any[];
  productionOrders?: any[];
  onViewOrder: (order: any) => void;
  onPrintOrder: (order: any) => void;
  onEditOrder?: (order: any) => void;
  onDeleteOrder: (order: any) => void;
  onStatusChange: (order: any, status: string) => void;
  currentUser?: any;
  isAdmin?: boolean;
  selectedOrders?: number[];
  onOrderSelect?: (orderId: number, selected: boolean) => void;
  onSelectAll?: (selected: boolean) => void;
}

export default function OrdersTable({
  orders,
  customers,
  users,
  productionOrders = [],
  onViewOrder,
  onPrintOrder,
  onEditOrder,
  onDeleteOrder,
  onStatusChange,
  currentUser,
  isAdmin = false,
  selectedOrders = [],
  onOrderSelect,
  onSelectAll,
}: OrdersTableProps) {
  // Check if all orders are selected
  const allOrdersSelected =
    orders.length > 0 &&
    orders.every((order: any) => selectedOrders.includes(order.id));
  const someOrdersSelected =
    selectedOrders.length > 0 && selectedOrders.length < orders.length;

  const handleSelectAll = (checked: boolean) => {
    if (onSelectAll) {
      onSelectAll(checked);
    }
  };

  const handleOrderSelect = (orderId: number, checked: boolean) => {
    if (onOrderSelect) {
      onOrderSelect(orderId, checked);
    }
  };

  const getStatusBadge = (status: string) => {
    const statusMap: {
      [key: string]: { label: string; variant: any; color: string };
    } = {
      waiting: {
        label: "انتظار",
        variant: "secondary",
        color: "bg-yellow-100 text-yellow-800",
      },
      pending: {
        label: "معلق",
        variant: "secondary",
        color: "bg-yellow-100 text-yellow-800",
      },
      in_production: {
        label: "قيد الإنتاج",
        variant: "default",
        color: "bg-blue-100 text-blue-800",
      },
      for_production: {
        label: "للإنتاج",
        variant: "default",
        color: "bg-blue-100 text-blue-800",
      },
      paused: {
        label: "معلق",
        variant: "destructive",
        color: "bg-red-100 text-red-800",
      },
      on_hold: {
        label: "إيقاف مؤقت",
        variant: "destructive",
        color: "bg-red-100 text-red-800",
      },
      completed: {
        label: "مكتمل",
        variant: "default",
        color: "bg-green-100 text-green-800",
      },
      received: {
        label: "مستلم",
        variant: "default",
        color: "bg-purple-100 text-purple-800",
      },
      delivered: {
        label: "تم التوصيل",
        variant: "default",
        color: "bg-gray-100 text-gray-800",
      },
      cancelled: {
        label: "ملغي",
        variant: "destructive",
        color: "bg-red-100 text-red-800",
      },
    };

    const statusInfo = statusMap[status] || {
      label: status,
      variant: "outline",
      color: "bg-gray-100 text-gray-800",
    };

    return (
      <Badge className={statusInfo.color} data-testid={`status-${status}`}>
        {statusInfo.label}
      </Badge>
    );
  };

  const calculateDeliveryInfo = (order: any) => {
    if (!order.created_at || !order.delivery_days) {
      return { deliveryDate: null, daysRemaining: null };
    }

    const createdDate = new Date(order.created_at);
    const deliveryDate = new Date(createdDate);
    deliveryDate.setDate(
      deliveryDate.getDate() + parseInt(order.delivery_days),
    );

    const today = new Date();
    today.setHours(0, 0, 0, 0);
    deliveryDate.setHours(0, 0, 0, 0);

    const timeDiff = deliveryDate.getTime() - today.getTime();
    const daysRemaining = Math.ceil(timeDiff / (1000 * 3600 * 24));

    return { deliveryDate, daysRemaining };
  };

  return (
    <Table>
      <TableHeader>
        <TableRow>
          {onOrderSelect && onSelectAll && (
            <TableHead className="w-12">
              <Checkbox
                checked={
                  allOrdersSelected
                    ? true
                    : someOrdersSelected
                      ? "indeterminate"
                      : false
                }
                onCheckedChange={handleSelectAll}
                data-testid="checkbox-select-all"
              />
            </TableHead>
          )}
          <TableHead className="text-right">رقم الطلب</TableHead>
          <TableHead className="text-right">العميل</TableHead>
          <TableHead className="text-right">تاريخ الإنشاء</TableHead>
          <TableHead className="text-right">المنشئ</TableHead>
          <TableHead className="text-right">التسليم</TableHead>
          <TableHead className="text-right">نسبة الإكمال</TableHead>
          <TableHead className="text-right">ملاحظات</TableHead>
          <TableHead className="text-center">الحالة</TableHead>
          <TableHead className="text-center w-10 md:w-14">الإجراءات</TableHead>

        </TableRow>
      </TableHeader>
      <TableBody>
        {orders.map((order: any) => {
          const customer = customers.find(
            (c: any) => c.id === order.customer_id,
          );
          const user = users.find(
            (u: any) => u.id === parseInt(order.created_by),
          );
          const { deliveryDate, daysRemaining } = calculateDeliveryInfo(order);

          // حساب نسب الإكمال من أوامر الإنتاج المرتبطة بهذا الطلب
          const orderProductionOrders = productionOrders.filter(
            (po: any) => po.order_id === order.id
          );

          // حساب متوسط مرجح لنسبة الإكمال لكل مرحلة بناءً على الكميات الفعلية
          let avgFilmPercentage = 0;
          let avgPrintingPercentage = 0;
          let avgCuttingPercentage = 0;

          if (orderProductionOrders.length > 0) {
            // حساب الكمية الإجمالية لجميع أوامر الإنتاج
            const totalQuantity = orderProductionOrders.reduce(
              (sum: number, po: any) => sum + parseFloat(po.final_quantity_kg || po.quantity_kg || 0),
              0
            );

            if (totalQuantity > 0) {
              // حساب المتوسط المرجح لكل مرحلة
              const weightedFilm = orderProductionOrders.reduce(
                (sum: number, po: any) => {
                  const quantity = parseFloat(po.final_quantity_kg || po.quantity_kg || 0);
                  const percentage = parseFloat(po.film_completion_percentage || 0);
                  return sum + (quantity * percentage);
                },
                0
              );

              const weightedPrinting = orderProductionOrders.reduce(
                (sum: number, po: any) => {
                  const quantity = parseFloat(po.final_quantity_kg || po.quantity_kg || 0);
                  const percentage = parseFloat(po.printing_completion_percentage || 0);
                  return sum + (quantity * percentage);
                },
                0
              );

              const weightedCutting = orderProductionOrders.reduce(
                (sum: number, po: any) => {
                  const quantity = parseFloat(po.final_quantity_kg || po.quantity_kg || 0);
                  const percentage = parseFloat(po.cutting_completion_percentage || 0);
                  return sum + (quantity * percentage);
                },
                0
              );

              avgFilmPercentage = weightedFilm / totalQuantity;
              avgPrintingPercentage = weightedPrinting / totalQuantity;
              avgCuttingPercentage = weightedCutting / totalQuantity;
            }
          }

          return (
            <TableRow
              key={order.id}
              data-testid={`order-row-${order.id}`}
              className={selectedOrders.includes(order.id) ? "bg-blue-50" : ""}
            >
              {onOrderSelect && onSelectAll && (
                <TableCell>
                  <Checkbox
                    checked={selectedOrders.includes(order.id)}
                    onCheckedChange={(checked) =>
                      handleOrderSelect(order.id, !!checked)
                    }
                    data-testid={`checkbox-select-order-${order.id}`}
                  />
                </TableCell>
              )}
              <TableCell
                className="font-medium"
                data-testid={`order-number-${order.id}`}
              >
                {order.order_number}
              </TableCell>
              <TableCell data-testid={`customer-${order.id}`}>
                <div className="text-right">
                  <div className="font-medium">
                    {customer?.name_ar || customer?.name}
                  </div>
                  <div className="text-sm text-gray-500">{customer?.id}</div>
                </div>
              </TableCell>
              <TableCell data-testid={`created-date-${order.id}`}>
                {order.created_at
                  ? format(new Date(order.created_at), "dd/MM/yyyy")
                  : "-"}
              </TableCell>
              <TableCell data-testid={`created-by-${order.id}`}>
                <div className="text-right">
                  <div className="font-medium">{user?.display_name_ar || user?.display_name || user?.username || '-'}</div>
                  <div className="text-sm text-gray-500">#{user?.id}</div>
                </div>
              </TableCell>
              <TableCell data-testid={`delivery-${order.id}`}>
                <div className="text-right">
                  {deliveryDate && daysRemaining !== null ? (
                    <>
                      <div className="font-medium">
                        {daysRemaining > 0 ? (
                          <span className="text-green-600">
                            {daysRemaining} يوم متبقي
                          </span>
                        ) : daysRemaining === 0 ? (
                          <span className="text-orange-600">
                            يجب التسليم اليوم
                          </span>
                        ) : (
                          <span className="text-red-600">
                            متأخر {Math.abs(daysRemaining)} يوم
                          </span>
                        )}
                      </div>
                      <div className="text-sm text-gray-500">
                        التسليم: {format(deliveryDate, "dd/MM/yyyy")}
                      </div>
                    </>
                  ) : (
                    "-"
                  )}
                </div>
              </TableCell>
              <TableCell data-testid={`production-progress-${order.id}`}>
                {orderProductionOrders.length > 0 ? (
                  <ProductionProgress
                    filmPercentage={avgFilmPercentage}
                    printingPercentage={avgPrintingPercentage}
                    cuttingPercentage={avgCuttingPercentage}
                  />
                ) : (
                  <div className="text-gray-400 text-center">-</div>
                )}
              </TableCell>
              <TableCell data-testid={`notes-${order.id}`}>
                {order.notes || "-"}
              </TableCell>
              <TableCell className="text-center">
                {getStatusBadge(order.status || "pending")}
              </TableCell>
              <TableCell>
                <div className="grid grid-cols-3 gap-1">
                  <Button
                    variant="outline"
                    size="sm"
                    className="text-blue-600 border-blue-600 hover:bg-blue-50 p-1"
                    onClick={() => onViewOrder(order)}
                    title="عرض"
                    data-testid={`button-view-${order.id}`}
                  >
                    <Eye className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    className="text-green-600 border-green-600 hover:bg-green-50 p-1"
                    onClick={() => onPrintOrder(order)}
                    title="طباعة"
                    data-testid={`button-print-${order.id}`}
                  >
                    <FileText className="h-4 w-4" />
                  </Button>
                  {isAdmin && onEditOrder && (
                    <Button
                      variant="outline"
                      size="sm"
                      className="text-purple-600 border-purple-600 hover:bg-purple-50 p-1"
                      onClick={() => onEditOrder(order)}
                      title="تعديل"
                      data-testid={`button-edit-${order.id}`}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                  )}
                  <DropdownMenu>
                    <DropdownMenuTrigger asChild>
                      <Button
                        variant="outline"
                        size="sm"
                        className="text-orange-600 border-orange-600 hover:bg-orange-50 p-1"
                        title="تغيير الحالة"
                        data-testid={`button-status-${order.id}`}
                      >
                        <RefreshCw className="h-3 w-3" />
                      </Button>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent align="end" className="w-48">
                      <DropdownMenuItem
                        onClick={() => onStatusChange(order, "for_production")}
                      >
                        <div className="flex items-center w-full">
                          <div className="w-3 h-3 bg-blue-500 rounded-full mr-2"></div>
                          إلى الإنتاج
                        </div>
                      </DropdownMenuItem>
                      <DropdownMenuItem
                        onClick={() => onStatusChange(order, "on_hold")}
                      >
                        <div className="flex items-center w-full">
                          <div className="w-3 h-3 bg-red-500 rounded-full mr-2"></div>
                          إيقاف مؤقت
                        </div>
                      </DropdownMenuItem>
                      <DropdownMenuItem
                        onClick={() => onStatusChange(order, "pending")}
                      >
                        <div className="flex items-center w-full">
                          <div className="w-3 h-3 bg-yellow-500 rounded-full mr-2"></div>
                          في الانتظار
                        </div>
                      </DropdownMenuItem>
                      <DropdownMenuItem
                        onClick={() => onStatusChange(order, "completed")}
                      >
                        <div className="flex items-center w-full">
                          <div className="w-3 h-3 bg-green-500 rounded-full mr-2"></div>
                          مكتمل
                        </div>
                      </DropdownMenuItem>
                    </DropdownMenuContent>
                  </DropdownMenu>
                  {isAdmin && (
                    <Button
                      variant="outline"
                      size="sm"
                      className="text-red-600 border-red-600 hover:bg-red-50 p-1"
                      onClick={() => onDeleteOrder(order)}
                      title="حذف"
                      data-testid={`button-delete-${order.id}`}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  )}
                </div>
              </TableCell>
            </TableRow>
          );
        })}
      </TableBody>
    </Table>
  );
}

