                    name="category"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>فئة التدريب</FormLabel>
                        <FormControl>
                          <Select
                            value={field.value}
                            onValueChange={field.onChange}
                          >
                            <SelectTrigger data-testid="select-program-category">
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="general">تدريب عام</SelectItem>
                              <SelectItem value="department_specific">
                                تدريب متخصص
                              </SelectItem>
                            </SelectContent>
                          </Select>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={programForm.control}
                    name="training_scope"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>نوع التدريب</FormLabel>
                        <FormControl>
                          <Select
                            value={field.value}
                            onValueChange={field.onChange}
                          >
                            <SelectTrigger data-testid="select-program-scope">
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="safety">
                                السلامة المهنية
                              </SelectItem>
                              <SelectItem value="first_aid">
                                الإسعافات الأولية
                              </SelectItem>
                              <SelectItem value="fire_safety">
                                السلامة من الحريق
                              </SelectItem>
                              <SelectItem value="technical">التقني</SelectItem>
                              <SelectItem value="film">الفيلم</SelectItem>
                              <SelectItem value="printing">الطباعة</SelectItem>
                              <SelectItem value="cutting">التقطيع</SelectItem>
                            </SelectContent>
                          </Select>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <div className="grid grid-cols-3 gap-4">
                  <FormField
                    control={programForm.control}
                    name="duration_hours"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>مدة التدريب (ساعات)</FormLabel>
                        <FormControl>
                          <Input
                            type="number"
                            {...field}
                            onChange={(e) =>
                              field.onChange(parseInt(e.target.value))
                            }
