                        </div>
                      </div>

                      {/* Lunch End Button */}
                      <div className="flex flex-col items-center">
                        <Button
                          onClick={() =>
                            handleAttendanceAction("يعمل", "end_lunch")
                          }
                          className="bg-blue-600 hover:bg-blue-700 w-full"
                          disabled={
