import React, { useMemo } from "react";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "../client/src/components/ui/select";
import { FormControl } from "../client/src/components/ui/form";
import type { Machine } from "./schema";
export function MachineSelect({ value, onChange, loading, machines = [], sections = [], sectionKeyword, onlyActive = true }:{ value?: number; onChange:(id:number)=>void; loading?: boolean; machines: Machine[]; sections?: any[]; sectionKeyword?: string; onlyActive?: boolean; }){
  const filtered = useMemo(()=>{
    let list:any[] = machines as any[];
    if (sections.length && sectionKeyword) {
      const section = sections.find((s:any)=>[s.name, s.name_ar].filter(Boolean).map((x:string)=>x.toLowerCase()).some((n:string)=> n.includes(sectionKeyword.toLowerCase()) || (sectionKeyword==='film' && n.includes('فيلم')) || (sectionKeyword==='printing' && n.includes('طباعة')) || (sectionKeyword==='cutting' && n.includes('تقطيع')) ));
      if (section) list = list.filter((m:any)=> m.section_id === section.id);
    }
    if (onlyActive) list = list.filter((m:any)=> m.status === 'active');
    return list.filter((m:any)=> m.id);
  }, [machines, sections, sectionKeyword, onlyActive]);
  return (
    <Select value={value != null ? String(value) : undefined} onValueChange={(val: string)=>onChange(Number.parseInt(val,10))} disabled={loading}>
      <FormControl><SelectTrigger data-testid="select-machine"><SelectValue placeholder="اختر المكينة" /></SelectTrigger></FormControl>
      <SelectContent>
        {loading ? <SelectItem value="loading" disabled>جارِ التحميل...</SelectItem> : (filtered.length ? filtered.map((m:any)=>(<SelectItem key={String(m.id)} value={String(m.id)}>{m.name_ar || m.name}</SelectItem>)) : <SelectItem value="empty" disabled>لا توجد مكائن متاحة</SelectItem>)}
      </SelectContent>
    </Select>
  );
}

server/middleware/transaction.ts.content:
/**
 * Transaction middleware for ensuring data consistency
 */

import { db } from "../db";
import { sql } from "drizzle-orm";
import { Response, Request, NextFunction } from "express";

/**
 * Wraps an async route handler to ensure it runs within a database transaction
 */
export function withTransaction(
  handler: (req: Request, res: Response, tx: any) => Promise<void>
) {
  return async (req: Request, res: Response, next: NextFunction) => {
    try {
      await db.transaction(async (tx) => {
        await handler(req, res, tx);
      });
    } catch (error) {
      next(error);
    }
  };
}

/**
 * Acquires an advisory lock for the given key
 * This prevents concurrent modifications to the same resource
 * @param tx - Transaction object
 * @param lockKey - Unique key for the lock
 * @param timeout - Timeout in milliseconds (default: 5000)
 */
export async function acquireAdvisoryLock(
  tx: any,
  lockKey: number,
  timeout: number = 5000
): Promise<boolean> {
  try {
    // Try to acquire the lock with timeout
    const result = await tx.execute(
      sql`SELECT pg_advisory_xact_lock(${lockKey})`
    );
    return true;
  } catch (error) {
    console.error(`Failed to acquire advisory lock for key ${lockKey}:`, error);
    return false;
  }
}

/**
 * Ensures a resource is locked for update within a transaction
 * @param tx - Transaction object
 * @param table - Table name
 * @param id - Resource ID
 * @param lockTimeout - Lock timeout in milliseconds
 */
export async function lockResourceForUpdate(
  tx: any,
  table: string,
  id: number,
  lockTimeout: number = 5000
): Promise<void> {
  try {
    const timeoutMs = parseInt(String(lockTimeout), 10);
    if (isNaN(timeoutMs) || timeoutMs < 0) {
      throw new Error('Invalid lock timeout value');
    }
    const timeoutValue = `${timeoutMs}ms`;
    await tx.execute(
      sql`SET LOCAL lock_timeout = ${timeoutValue}`
    );
    await tx.execute(
      sql`SELECT * FROM ${sql.identifier(table)} WHERE id = ${id} FOR UPDATE NOWAIT`
    );
  } catch (error: any) {
    if (error.code === '55P03') {
      throw new Error(`المورد مشغول حالياً، يرجى المحاولة مرة أخرى`);
    }
    throw error;
  }
}

/**
 * Middleware to handle optimistic locking using version numbers
 */
export interface OptimisticLockable {
  version?: number;
  updated_at?: Date;
}

export function checkOptimisticLock<T extends OptimisticLockable>(
  currentRecord: T,
  requestVersion?: number
): void {
  if (requestVersion !== undefined && currentRecord.version !== requestVersion) {
    throw new Error(
      `البيانات تم تحديثها من قبل مستخدم آخر، يرجى إعادة تحميل الصفحة والمحاولة مرة أخرى`
    );
  }
}

/**
 * Retry a database operation with exponential backoff
 * @param operation - The operation to retry
 * @param maxRetries - Maximum number of retries
 * @param initialDelay - Initial delay in milliseconds
 */
export async function retryWithBackoff<T>(
  operation: () => Promise<T>,
  maxRetries: number = 3,
  initialDelay: number = 100
): Promise<T> {
  let lastError: any;

  for (let attempt = 1; attempt <= maxRetries; attempt++) {
    try {
      return await operation();
    } catch (error: any) {
      lastError = error;

      // Don't retry for non-retryable errors
      if (error.code && !['40001', '40P01', '55P03'].includes(error.code)) {
        throw error;
      }

      if (attempt < maxRetries) {
        const delay = initialDelay * Math.pow(2, attempt - 1);
        await new Promise(resolve => setTimeout(resolve, delay));
      }
    }
  }

  throw lastError;
}

shared/number-utils.ts.content:
/**
 * Utility functions for safe number parsing and validation
 */

/**
 * Safely parse a float value with validation
 * @param value - The value to parse
 * @param defaultValue - Default value if parsing fails
 * @param min - Minimum allowed value
 * @param max - Maximum allowed value
 * @returns Parsed number or default value
 */
export function safeParseFloat(
  value: any,
  defaultValue: number = 0,
  min?: number,
  max?: number
): number {
  if (value === null || value === undefined || value === '') {
    return defaultValue;
  }

  // Convert to string and handle comma as decimal separator
  const stringValue = String(value).replace(',', '.');

  // Remove any non-numeric characters except . and -
  const cleanValue = stringValue.replace(/[^0-9.-]/g, '');

  const parsed = Number.parseFloat(cleanValue);

  // Check if parsing resulted in a valid number
  if (Number.isNaN(parsed) || !Number.isFinite(parsed)) {
    console.warn(`Invalid number value: ${value}, using default: ${defaultValue}`);
    return defaultValue;
  }

  // Apply min/max constraints
  let result = parsed;
  if (min !== undefined && result < min) {
    console.warn(`Value ${result} is below minimum ${min}, using minimum`);
    result = min;
  }
  if (max !== undefined && result > max) {
    console.warn(`Value ${result} is above maximum ${max}, using maximum`);
    result = max;
  }

  return result;
}

/**
 * Safely parse an integer value with validation
 * @param value - The value to parse
 * @param defaultValue - Default value if parsing fails
 * @param min - Minimum allowed value
 * @param max - Maximum allowed value
 * @returns Parsed integer or default value
 */
export function safeParseInt(
  value: any,
  defaultValue: number = 0,
  min?: number,
  max?: number
): number {
  const floatValue = safeParseFloat(value, defaultValue, min, max);
  return Math.round(floatValue);
}

/**
 * Format a number for display with Arabic numerals
 * @param value - The number to format
 * @param decimals - Number of decimal places
 * @returns Formatted string
 */
export function formatNumberAr(value: number, decimals: number = 2): string {
  if (!Number.isFinite(value)) {
    return '0';
  }

  return value.toFixed(decimals);
}

/**
 * Validate that a value is a positive number
 * @param value - The value to check
 * @returns True if valid positive number
 */
export function isPositiveNumber(value: any): boolean {
  const parsed = safeParseFloat(value, -1);
  return parsed > 0;
}

/**
 * Calculate percentage with safe division
 * @param value - The value
 * @param total - The total
 * @param decimals - Number of decimal places
 * @returns Percentage or 0 if total is 0
 */
export function safePercentage(value: number, total: number, decimals: number = 2): number {
  if (total === 0 || !Number.isFinite(total) || !Number.isFinite(value)) {
    return 0;
  }

  const percentage = (value / total) * 100;
  return Number(percentage.toFixed(decimals));
}

/**
 * Safe division to prevent divide by zero
 * @param numerator - The numerator
 * @param denominator - The denominator
 * @param defaultValue - Default value if division is invalid
 * @returns Result of division or default value
 */
export function safeDivide(
  numerator: number,
  denominator: number,
  defaultValue: number = 0
): number {
  if (denominator === 0 || !Number.isFinite(denominator) || !Number.isFinite(numerator)) {
    return defaultValue;
  }

  return numerator / denominator;
}

/**
 * Round a number to specified decimal places
 * @param value - The value to round
 * @param decimals - Number of decimal places
 * @returns Rounded number
 */
export function roundToDecimals(value: number, decimals: number = 2): number {
  if (!Number.isFinite(value)) {
    return 0;
  }

  const factor = Math.pow(10, decimals);
  return Math.round(value * factor) / factor;
}

