            <CardContent className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="test-phone">رقم الهاتف</Label>
                <Input
                  id="test-phone"
                  value={testPhone}
                  onChange={(e) => setTestPhone(e.target.value)}
                  placeholder="+966xxxxxxxxx"
                  data-testid="input-test-phone"
                />
