                        <Badge className="bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-100">
                          <CheckCircle className="h-3 w-3 ml-1" />
                          {msg.status}
                        </Badge>
                      ) : msg.status === "failed" ? (
                        <Badge className="bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-100">
