                  />
                  <span className="text-sm text-gray-500">إلى</span>
                  <Input
                    type="date"
                    value={dateTo}
                    onChange={(e) => setDateTo(e.target.value)}
                    className="w-36"
                    data-testid="input-date-to"
                  />
                </div>

                <Badge variant="outline" className="text-xs">
                  آخر تحديث:{" "}
                  {new Date(stats.lastUpdated).toLocaleString("ar-EG")}
