  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "../ui/dialog";
import { Label } from "../ui/label";
import { Textarea } from "../ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../ui/select";
import {
  Plus,
  AlertCircle,
  Package,
  Palette,
  FileText,
  DollarSign,
  Truck,
  Phone,
  MoreHorizontal,
  Trash2,
  Eye,
  Paperclip,
} from "lucide-react";
import { useAuth } from "../../hooks/use-auth";
import { useToast } from "../../hooks/use-toast";
import { apiRequest } from "../../lib/queryClient";
import { formatDistanceToNow } from "date-fns";
import { ar } from "date-fns/locale";

interface QuickNote {
  id: number;
  content: string;
  note_type: string;
  priority: string;
  created_by: number;
  assigned_to: number;
  is_read: boolean;
  created_at: string;
  creator_name: string;
  assignee_name: string;
  attachments?: any[];
}

const noteTypeConfig = {
  order: { label: "طلبية", icon: Package, color: "bg-blue-100 text-blue-700 border-blue-300" },
  design: { label: "تصميم", icon: Palette, color: "bg-purple-100 text-purple-700 border-purple-300" },
  statement: { label: "كشف حساب", icon: FileText, color: "bg-green-100 text-green-700 border-green-300" },
  quote: { label: "عرض أسعار", icon: DollarSign, color: "bg-yellow-100 text-yellow-700 border-yellow-300" },
  delivery: { label: "توصيل", icon: Truck, color: "bg-orange-100 text-orange-700 border-orange-300" },
  call_customer: { label: "الاتصال بعميل", icon: Phone, color: "bg-pink-100 text-pink-700 border-pink-300" },
  other: { label: "أخرى", icon: MoreHorizontal, color: "bg-gray-100 text-gray-700 border-gray-300" },
};

const priorityConfig = {
  low: { label: "منخفضة", color: "bg-gray-50 border-gray-200" },
  normal: { label: "عادية", color: "bg-blue-50 border-blue-200" },
  high: { label: "عالية", color: "bg-orange-50 border-orange-200" },
  urgent: { label: "عاجلة", color: "bg-red-50 border-red-300 shadow-md" },
};

export default function QuickNotes() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newNote, setNewNote] = useState({
    content: "",
    note_type: "other",
    priority: "normal",
    assigned_to: 0,
  });

  // Update assigned_to when user is loaded
  useEffect(() => {
    if (user?.id && newNote.assigned_to === 0) {
      setNewNote(prev => ({ ...prev, assigned_to: user.id }));
    }
  }, [user?.id, newNote.assigned_to]);

  // Fetch quick notes for current user
  const { data: notes = [], isLoading } = useQuery<QuickNote[]>({
    queryKey: ["/api/quick-notes"],
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  // Fetch users for assignment
  const { data: users = [] } = useQuery<any[]>({
    queryKey: ["/api/users"],
  });

  // Create note mutation
  const createNoteMutation = useMutation({
    mutationFn: async (noteData: any) => {
      return await apiRequest("/api/quick-notes", {
        method: "POST",
        body: JSON.stringify(noteData),
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/quick-notes"] });
      toast({
        title: "تم إنشاء الملاحظة",
        description: "تم إضافة الملاحظة بنجاح",
      });
      setIsModalOpen(false);
      setNewNote({
        content: "",
        note_type: "other",
        priority: "normal",
        assigned_to: user?.id || 0,
      });
    },
    onError: (error: any) => {
      toast({
        title: "خطأ",
        description: error.message || "فشل إنشاء الملاحظة",
        variant: "destructive",
      });
    },
  });

  // Mark as read mutation
  const markAsReadMutation = useMutation({
    mutationFn: async (noteId: number) => {
      return await apiRequest(`/api/quick-notes/${noteId}/read`, {
        method: "PATCH",
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/quick-notes"] });
    },
  });

  // Delete note mutation
  const deleteNoteMutation = useMutation({
    mutationFn: async (noteId: number) => {
      return await apiRequest(`/api/quick-notes/${noteId}`, {
        method: "DELETE",
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/quick-notes"] });
      toast({
        title: "تم الحذف",
        description: "تم حذف الملاحظة بنجاح",
      });
    },
  });

  const handleCreateNote = () => {
    if (!newNote.content.trim()) {
      toast({
        title: "خطأ",
        description: "يرجى إدخال محتوى الملاحظة",
        variant: "destructive",
      });
      return;
    }

    createNoteMutation.mutate(newNote);
  };

  // Filter notes assigned to current user and unread notes
  const userNotes = notes.filter(
    (note) => note.assigned_to === user?.id || note.created_by === user?.id
  );
  const unreadNotes = userNotes.filter((note) => !note.is_read);

  return (
    <Card className="shadow-lg" data-testid="card-quick-notes">
      <CardHeader className="flex flex-row items-center justify-between pb-3">
        <div className="flex items-center gap-2">
          <AlertCircle className="w-5 h-5 text-blue-600" />
          <CardTitle className="text-lg font-bold">ملاحظات سريعة</CardTitle>
          {unreadNotes.length > 0 && (
            <span className="bg-red-500 text-white text-xs font-bold px-2 py-1 rounded-full">
              {unreadNotes.length}
            </span>
          )}
        </div>
        <Dialog open={isModalOpen} onOpenChange={setIsModalOpen}>
          <DialogTrigger asChild>
            <Button size="sm" className="gap-2" data-testid="button-add-note">
              <Plus className="w-4 h-4" />
              إضافة ملاحظة
            </Button>
          </DialogTrigger>
          <DialogContent className="sm:max-w-[500px]">
            <DialogHeader>
              <DialogTitle>إضافة ملاحظة جديدة</DialogTitle>
            </DialogHeader>
            <div className="space-y-4 py-4">
              <div>
                <Label>نوع الملاحظة</Label>
                <Select
                  value={newNote.note_type}
                  onValueChange={(value) =>
                    setNewNote({ ...newNote, note_type: value })
                  }
                >
                  <SelectTrigger data-testid="select-note-type">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {Object.entries(noteTypeConfig).map(([key, config]) => (
                      <SelectItem key={key} value={key}>
                        <div className="flex items-center gap-2">
                          <config.icon className="w-4 h-4" />
                          {config.label}
                        </div>
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label>الأولوية</Label>
                <Select
                  value={newNote.priority}
                  onValueChange={(value) =>
                    setNewNote({ ...newNote, priority: value })
                  }
                >
                  <SelectTrigger data-testid="select-priority">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {Object.entries(priorityConfig).map(([key, config]) => (
                      <SelectItem key={key} value={key}>
                        {config.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label>تعيين إلى</Label>
                <Select
                  value={newNote.assigned_to.toString()}
                  onValueChange={(value) =>
                    setNewNote({ ...newNote, assigned_to: parseInt(value) })
                  }
                >
                  <SelectTrigger data-testid="select-assigned-to">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {users.map((u: any) => (
                      <SelectItem key={u.id} value={u.id.toString()}>
                        {u.display_name || u.username}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label>المحتوى</Label>
                <Textarea
                  value={newNote.content}
                  onChange={(e) =>
                    setNewNote({ ...newNote, content: e.target.value })
                  }
                  placeholder="اكتب ملاحظتك هنا..."
                  className="min-h-[100px]"
                  data-testid="textarea-note-content"
                />
              </div>

              <div className="flex justify-end gap-2">
                <Button
                  variant="outline"
                  onClick={() => setIsModalOpen(false)}
                  data-testid="button-cancel"
                >
                  إلغاء
                </Button>
                <Button
                  onClick={handleCreateNote}
                  disabled={createNoteMutation.isPending}
                  data-testid="button-save-note"
                >
                  {createNoteMutation.isPending ? "جاري الحفظ..." : "حفظ"}
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </CardHeader>

      <CardContent className="space-y-3 max-h-[500px] overflow-y-auto">
        {isLoading ? (
          <div className="text-center py-4 text-gray-500">جاري التحميل...</div>
        ) : userNotes.length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            لا توجد ملاحظات حالياً
          </div>
        ) : (
          userNotes.map((note) => {
            const typeConfig = noteTypeConfig[note.note_type as keyof typeof noteTypeConfig] || noteTypeConfig.other;
            const priorityStyle = priorityConfig[note.priority as keyof typeof priorityConfig] || priorityConfig.normal;
            const Icon = typeConfig.icon;

            return (
              <div
                key={note.id}
                className={`p-4 rounded-lg border-2 ${priorityStyle.color} ${!note.is_read ? "ring-2 ring-blue-400" : ""}`}
                data-testid={`note-${note.id}`}
              >
                <div className="flex items-start justify-between gap-3">
                  <div className="flex-1">
                    <div className="flex items-center gap-2 mb-2">
                      <div className={`p-2 rounded-lg ${typeConfig.color}`}>
                        <Icon className="w-4 h-4" />
                      </div>
                      <span className="text-xs font-semibold px-2 py-1 rounded bg-gray-100">
                        {typeConfig.label}
                      </span>
                      {!note.is_read && (
                        <span className="text-xs font-bold text-blue-600">
                          جديد
                        </span>
                      )}
                    </div>
                    <p className="font-bold text-gray-900 mb-2 whitespace-pre-wrap">
                      {note.content}
                    </p>
                    <div className="flex items-center gap-3 text-xs text-gray-600">
                      <span>من: {note.creator_name}</span>
                      <span>•</span>
                      <span>إلى: {note.assignee_name}</span>
                      <span>•</span>
                      <span>
                        {formatDistanceToNow(new Date(note.created_at), {
                          addSuffix: true,
                          locale: ar,
                        })}
                      </span>
                      {note.attachments && note.attachments.length > 0 && (
                        <>
                          <span>•</span>
                          <span className="flex items-center gap-1">
                            <Paperclip className="w-3 h-3" />
                            {note.attachments.length}
                          </span>
                        </>
                      )}
                    </div>
                  </div>
                  <div className="flex gap-1">
                    {!note.is_read && note.assigned_to === user?.id && (
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => markAsReadMutation.mutate(note.id)}
                        data-testid={`button-mark-read-${note.id}`}
                      >
                        <Eye className="w-4 h-4" />
                      </Button>
                    )}
                    {(note.created_by === user?.id || user?.role_id === 1) && (
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => deleteNoteMutation.mutate(note.id)}
                        data-testid={`button-delete-${note.id}`}
                      >
                        <Trash2 className="w-4 h-4 text-red-600" />
                      </Button>
                    )}
                  </div>
                </div>
              </div>
            );
          })
        )}
      </CardContent>
    </Card>
  );
}

client/src/pages/ProductionOrdersManagem.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "../lib/queryClient";
import { useAuth } from "../hooks/use-auth";
import { useToast } from "../hooks/use-toast";
import Header from "../components/layout/Header";
import Sidebar from "../components/layout/Sidebar";
import MobileNav from "../components/layout/MobileNav";
import { Card } from "../components/ui/card";
import { Button } from "../components/ui/button";
import { Badge } from "../components/ui/badge";
import { Input } from "../components/ui/input";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "../components/ui/table";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../components/ui/select";
import { Loader2, Play, Settings, BarChart3, Printer } from "lucide-react";
import ProductionOrderActivationModal from "../components/production/ProductionOrderActivationModal";
import ProductionOrderStatsCard from "../components/production/ProductionOrderStatsCard";
import ProductionOrderFilters from "../components/production/ProductionOrderFilters";
import ProductionOrderPrintTemplate from "../components/production/ProductionOrderPrintTemplate";
import { toastMessages } from "../lib/toastMessages";
import { IconWithTooltip } from "../components/ui/icon-with-tooltip";

export default function ProductionOrdersManagement() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedOrder, setSelectedOrder] = useState<any>(null);
  const [isActivationModalOpen, setIsActivationModalOpen] = useState(false);
  const [showStats, setShowStats] = useState<number | null>(null);
  const [printingProductionOrder, setPrintingProductionOrder] = useState<any>(null);
  const [filters, setFilters] = useState({
    status: "all",
    customerId: "",
    searchTerm: "",
    dateFrom: "",
    dateTo: "",
  });

  // جلب أوامر الإنتاج مع التفاصيل
  const { data: ordersData, isLoading: ordersLoading } = useQuery({
    queryKey: ["/api/production-orders/management"],
    queryFn: async () => {
      const response = await fetch("/api/production-orders/management");
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "خطأ في جلب أوامر الإنتاج");
      }
      return response.json();
    },
  });

  // جلب المكائن
  const { data: machines = [] } = useQuery({
    queryKey: ["/api/machines"],
    queryFn: async () => {
      const response = await fetch("/api/machines");
      if (!response.ok) throw new Error("فشل في جلب المكائن");
      return response.json();
    },
  });

  // جلب المستخدمين
  const { data: users = [] } = useQuery({
    queryKey: ["/api/users"],
    queryFn: async () => {
      const response = await fetch("/api/users");
      if (!response.ok) throw new Error("فشل في جلب المستخدمين");
      const result = await response.json();
      return result.data || result;
    },
  });

  // تفعيل أمر الإنتاج
  const activateMutation = useMutation({
    mutationFn: async ({ id, machineId, operatorId }: any) => {
      const response = await apiRequest(`/api/production-orders/${id}/activate`, {
        method: "PATCH",
        body: JSON.stringify({ machineId, operatorId }),
      });
      return response;
    },
    onSuccess: (data, variables) => {
      const orderNumber = selectedOrder?.production_order_number || `#${variables.id}`;
      const message = toastMessages.productionOrders.activated(orderNumber);
      toast({
        title: message.title,
        description: message.description,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/production-orders/management"] });
      queryClient.invalidateQueries({ queryKey: ["/api/production-queues"] });
      setIsActivationModalOpen(false);
      setSelectedOrder(null);
    },
    onError: (error: any) => {
      toast({
        title: "❌ خطأ في التفعيل",
        description: error.message || toastMessages.productionOrders.errors.activation,
        variant: "destructive",
      });
    },
  });

  // تحديث التخصيص
  const assignMutation = useMutation({
    mutationFn: async ({ id, machineId, operatorId }: any) => {
      const response = await apiRequest(`/api/production-orders/${id}/assign`, {
        method: "PATCH",
        body: JSON.stringify({ machineId, operatorId }),
      });
      return response;
    },
    onSuccess: (data, variables) => {
      const orderNumber = selectedOrder?.production_order_number || `#${variables.id}`;
      const message = toastMessages.productionOrders.assigned(orderNumber);
      toast({
        title: message.title,
        description: message.description,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/production-orders/management"] });
      queryClient.invalidateQueries({ queryKey: ["/api/production-queues"] });
    },
    onError: (error: any) => {
      toast({
        title: "❌ خطأ في التخصيص",
        description: error.message || toastMessages.productionOrders.errors.assignment,
        variant: "destructive",
      });
    },
  });

  // معالج الطباعة
  const handlePrintProductionOrder = (order: any) => {
    setPrintingProductionOrder(order);
  };

  const handleActivate = (order: any) => {
    setSelectedOrder(order);
    setIsActivationModalOpen(true);
  };

  const handleActivationConfirm = (machineId?: string, operatorId?: number) => {
    if (selectedOrder) {
      activateMutation.mutate({
        id: selectedOrder.id,
        machineId,
        operatorId,
      });
    }
  };

  // فلترة الأوامر
  const filteredOrders = ordersData?.data?.filter((order: any) => {
    // فلتر الحالة
    if (filters.status !== "all" && order.status !== filters.status) {
      return false;
    }

    // فلتر العميل
    if (filters.customerId && order.customer_id !== filters.customerId) {
      return false;
    }

    // فلتر البحث
    if (filters.searchTerm) {
      const searchLower = filters.searchTerm.toLowerCase();
      const matchesSearch =
        order.production_order_number?.toLowerCase().includes(searchLower) ||
        order.order_number?.toLowerCase().includes(searchLower) ||
        order.customer_name?.toLowerCase().includes(searchLower) ||
        order.customer_name_ar?.toLowerCase().includes(searchLower) ||
        order.size_caption?.toLowerCase().includes(searchLower);

      if (!matchesSearch) return false;
    }

    // فلتر التاريخ
    if (filters.dateFrom) {
      const orderDate = new Date(order.created_at);
      const fromDate = new Date(filters.dateFrom);
      if (orderDate < fromDate) return false;
    }

    if (filters.dateTo) {
      const orderDate = new Date(order.created_at);
      const toDate = new Date(filters.dateTo);
      toDate.setHours(23, 59, 59, 999);
      if (orderDate > toDate) return false;
    }

    return true;
  }) || [];

  // دالة عرض الحالة
  const getStatusBadge = (status: string) => {
    switch (status) {
      case "pending":
        return (
          <Badge className="bg-yellow-100 text-yellow-800">
            ⏳ انتظار
          </Badge>
        );
      case "active":
        return (
          <Badge className="bg-green-100 text-green-800">
            ▶️ نشط
          </Badge>
        );
      case "in_production":
        return (
          <Badge className="bg-blue-100 text-blue-800">
            🔄 قيد الإنتاج
          </Badge>
        );
      case "completed":
        return (
          <Badge className="bg-gray-100 text-gray-800">
            ✅ مكتمل
          </Badge>
        );
      case "cancelled":
        return (
          <Badge className="bg-red-100 text-red-800">
            ❌ ملغي
          </Badge>
        );
      default:
        return <Badge>{status}</Badge>;
    }
  };

  // دالة عرض التخصيص
  const getAssignmentBadges = (order: any) => {
    const badges = [];
    if (order.assigned_machine_id) {
      badges.push(
        <Badge key="machine" variant="secondary" className="mr-1">
          🏭 {order.machine_name_ar || order.machine_name || order.assigned_machine_id}
        </Badge>
      );
    }
    if (order.assigned_operator_id) {
      badges.push(
        <Badge key="operator" variant="secondary">
          👷 {order.operator_name_ar || order.operator_name || `عامل #${order.assigned_operator_id}`}
        </Badge>
      );
    }
    return badges;
  };

  // حساب الإحصائيات الإجمالية
  const totalStats = {
    total: filteredOrders.length,
    pending: filteredOrders.filter((o: any) => o.status === "pending").length,
    active: filteredOrders.filter((o: any) => o.status === "active").length,
    completed: filteredOrders.filter((o: any) => o.status === "completed").length,
  };

  if (ordersLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <Loader2 className="h-8 w-8 animate-spin text-primary" />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />
      <div className="flex">
        <Sidebar />
        <MobileNav />
        <main className="flex-1 lg:mr-64 p-4 pb-20 lg:pb-4">
          <div className="mb-6">
            <h1 className="text-2xl font-bold text-gray-900 mb-2">
              إدارة أوامر الإنتاج
            </h1>
            <p className="text-gray-600">
              مراقبة وإدارة جميع أوامر الإنتاج وتحويلها للإنتاج
            </p>
          </div>

          {/* الإحصائيات الإجمالية */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
            <Card className="p-4" data-testid="card-total-orders">
              <div className="text-sm text-gray-600">إجمالي الأوامر</div>
              <div className="text-2xl font-bold" data-testid="stat-total-orders">{totalStats.total}</div>
            </Card>
            <Card className="p-4" data-testid="card-pending-orders">
              <div className="text-sm text-gray-600">في الانتظار</div>
              <div className="text-2xl font-bold text-yellow-600" data-testid="stat-pending-orders">{totalStats.pending}</div>
            </Card>
            <Card className="p-4" data-testid="card-active-orders">
              <div className="text-sm text-gray-600">نشطة</div>
              <div className="text-2xl font-bold text-green-600" data-testid="stat-active-orders">{totalStats.active}</div>
            </Card>
            <Card className="p-4" data-testid="card-completed-orders">
              <div className="text-sm text-gray-600">مكتملة</div>
              <div className="text-2xl font-bold text-gray-600" data-testid="stat-completed-orders">{totalStats.completed}</div>
            </Card>
          </div>

          {/* الفلاتر */}
          <ProductionOrderFilters
            filters={filters}
            onFiltersChange={setFilters}
            customers={Array.from(new Map(filteredOrders.map((o: any) => [o.customer_id, { id: o.customer_id, name_ar: o.customer_name_ar, name: o.customer_name }])).values())}
          />

          {/* جدول أوامر الإنتاج */}
          <Card data-testid="card-production-orders-table">
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead data-testid="header-order-number">رقم الطلب</TableHead>
                    <TableHead data-testid="header-production-order">رقم أمر الإنتاج</TableHead>
                    <TableHead data-testid="header-customer">العميل</TableHead>
                    <TableHead data-testid="header-product">المنتج</TableHead>
