import { useState } from "react";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "../ui/dialog";
import { Button } from "../ui/button";
import { Label } from "../ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../ui/select";
import { Badge } from "../ui/badge";

interface ProductionOrderActivationModalProps {
  isOpen: boolean;
  onClose: () => void;
  onConfirm: (machineId?: string, operatorId?: number) => void;
  order: any;
  machines: any[];
  operators: any[];
  isUpdating?: boolean;
}

export default function ProductionOrderActivationModal({
  isOpen,
  onClose,
  onConfirm,
  order,
  machines,
  operators,
  isUpdating = false,
}: ProductionOrderActivationModalProps) {
  const [selectedMachineId, setSelectedMachineId] = useState<string>("");
  const [selectedOperatorId, setSelectedOperatorId] = useState<string>("");

  const handleConfirm = () => {
    const machineId = selectedMachineId || undefined;
    const operatorId = selectedOperatorId ? parseInt(selectedOperatorId) : undefined;
    onConfirm(machineId, operatorId);
  };

  // فلتر المكائن النشطة فقط
  const activeMachines = machines.filter(m => m.status === "active");

  // فلتر العمال حسب الأقسام المناسبة
  const productionOperators = operators.filter(u => {
    // يمكنك تعديل هذا الفلتر حسب هيكل البيانات
    return u.section_id && ['production', 'factory'].includes(u.section_id);
  });

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent>
        <DialogHeader>
          <DialogTitle>
            {isUpdating ? "تحديث تخصيص أمر الإنتاج" : "تفعيل أمر الإنتاج"}
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {order && (
            <div className="bg-gray-50 p-3 rounded-lg">
              <div className="text-sm space-y-1">
                <div className="flex justify-between">
                  <span className="text-gray-600">رقم أمر الإنتاج:</span>
                  <span className="font-medium">{order.production_order_number}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">العميل:</span>
                  <span className="font-medium">
                    {order.customer_name_ar || order.customer_name}
                  </span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">المنتج:</span>
                  <span className="font-medium">{order.size_caption}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">الكمية:</span>
                  <span className="font-medium">{order.quantity_kg} كجم</span>
                </div>
              </div>
            </div>
          )}

          <div>
            <Label htmlFor="machine">الماكينة (اختياري)</Label>
            <Select value={selectedMachineId} onValueChange={setSelectedMachineId}>
              <SelectTrigger id="machine" data-testid="select-machine">
                <SelectValue placeholder="اختر الماكينة..." />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="none" data-testid="option-no-machine">
                  بدون تخصيص
                </SelectItem>
                {activeMachines.map((machine) => (
                  <SelectItem
                    key={machine.id}
                    value={machine.id}
                    data-testid={`option-machine-${machine.id}`}
                  >
                    <div className="flex items-center gap-2">
                      {machine.name_ar || machine.name}
                      {machine.type && (
                        <Badge variant="outline" className="text-xs">
                          {machine.type}
                        </Badge>
                      )}
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            {selectedMachineId && selectedMachineId !== "none" && (
              <p className="text-sm text-green-600 mt-1">
                سيتم تخصيص الماكينة لهذا الأمر
              </p>
            )}
          </div>

          <div>
            <Label htmlFor="operator">العامل المسؤول (اختياري)</Label>
            <Select value={selectedOperatorId} onValueChange={setSelectedOperatorId}>
              <SelectTrigger id="operator" data-testid="select-operator">
                <SelectValue placeholder="اختر العامل..." />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="none" data-testid="option-no-operator">
                  بدون تخصيص
                </SelectItem>
                {productionOperators.map((operator) => (
                  <SelectItem
                    key={operator.id}
                    value={operator.id.toString()}
                    data-testid={`option-operator-${operator.id}`}
                  >
                    {operator.display_name_ar || operator.display_name || operator.username}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            {selectedOperatorId && selectedOperatorId !== "none" && (
              <p className="text-sm text-green-600 mt-1">
                سيتم تخصيص العامل لهذا الأمر
              </p>
            )}
          </div>

          {!isUpdating && (
            <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3">
              <p className="text-sm text-yellow-800">
                <strong>ملاحظة:</strong> سيتم تفعيل أمر الإنتاج وتغيير حالته إلى "نشط".
                يمكنك تخصيص الماكينة والعامل الآن أو لاحقاً.
              </p>
            </div>
          )}
        </div>

        <DialogFooter>
          <Button variant="outline" onClick={onClose}>
            إلغاء
          </Button>
          <Button onClick={handleConfirm} data-testid="button-confirm-activation">
            {isUpdating ? "تحديث التخصيص" : "تفعيل الأمر"}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

import { Card } from "../ui/card";
import { Input } from "../ui/input";
import { Label } from "../ui/label";
import { Button } from "../ui/button";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../ui/select";
import { Search, Filter, X } from "lucide-react";

interface ProductionOrderFiltersProps {
  filters: {
    status: string;
    customerId: string;
    searchTerm: string;
    dateFrom: string;
    dateTo: string;
  };
  onFiltersChange: (filters: any) => void;
  customers: any[];
}

export default function ProductionOrderFilters({
  filters,
  onFiltersChange,
  customers,
}: ProductionOrderFiltersProps) {
  const handleFilterChange = (key: string, value: any) => {
    onFiltersChange({
      ...filters,
      [key]: value,
    });
  };

  const handleReset = () => {
    onFiltersChange({
      status: "all",
      customerId: "",
      searchTerm: "",
      dateFrom: "",
      dateTo: "",
    });
  };

  const hasActiveFilters = 
    filters.status !== "all" ||
    filters.customerId ||
    filters.searchTerm ||
    filters.dateFrom ||
    filters.dateTo;

  return (
    <Card className="p-4 mb-4">
      <div className="flex items-center gap-2 mb-4">
        <Filter className="h-5 w-5 text-gray-600" />
        <h3 className="font-semibold">فلاتر البحث</h3>
        {hasActiveFilters && (
          <Button
            size="sm"
            variant="ghost"
            onClick={handleReset}
            className="mr-auto"
            data-testid="button-reset-filters"
          >
            <X className="h-4 w-4 ml-1" />
            مسح الفلاتر
          </Button>
        )}
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4">
        {/* البحث النصي */}
        <div>
          <Label htmlFor="search">البحث</Label>
          <div className="relative">
            <Search className="absolute right-3 top-2.5 h-4 w-4 text-gray-400" />
            <Input
              id="search"
              placeholder="رقم الطلب أو أمر الإنتاج..."
              value={filters.searchTerm}
              onChange={(e) => handleFilterChange("searchTerm", e.target.value)}
              className="pr-9"
              data-testid="input-search"
            />
          </div>
        </div>

        {/* فلتر الحالة */}
        <div>
          <Label htmlFor="status">الحالة</Label>
          <Select
            value={filters.status}
            onValueChange={(value) => handleFilterChange("status", value)}
          >
            <SelectTrigger id="status" data-testid="select-status">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all" data-testid="option-status-all">
                جميع الحالات
              </SelectItem>
              <SelectItem value="pending" data-testid="option-status-pending">
                <div className="flex items-center gap-2">
                  <span className="text-yellow-600">⏳</span>
                  في الانتظار
                </div>
              </SelectItem>
              <SelectItem value="active" data-testid="option-status-active">
                <div className="flex items-center gap-2">
                  <span className="text-green-600">▶️</span>
                  نشط
                </div>
              </SelectItem>
              <SelectItem value="completed" data-testid="option-status-completed">
                <div className="flex items-center gap-2">
                  <span className="text-gray-600">✅</span>
                  مكتمل
                </div>
              </SelectItem>
              <SelectItem value="cancelled" data-testid="option-status-cancelled">
                <div className="flex items-center gap-2">
                  <span className="text-red-600">❌</span>
                  ملغي
                </div>
              </SelectItem>
            </SelectContent>
          </Select>
        </div>

        {/* فلتر العميل */}
        <div>
          <Label htmlFor="customer">العميل</Label>
          <Select
            value={filters.customerId || "all"}
            onValueChange={(value) => handleFilterChange("customerId", value === "all" ? "" : value)}
          >
            <SelectTrigger id="customer" data-testid="select-customer">
              <SelectValue placeholder="جميع العملاء" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all" data-testid="option-customer-all">
                جميع العملاء
              </SelectItem>
              {customers.map((customer) => (
                <SelectItem
                  key={customer.id}
                  value={customer.id}
                  data-testid={`option-customer-${customer.id}`}
                >
                  {customer.name_ar || customer.name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        {/* التاريخ من */}
        <div>
          <Label htmlFor="date-from">من تاريخ</Label>
          <Input
            id="date-from"
            type="date"
            value={filters.dateFrom}
            onChange={(e) => handleFilterChange("dateFrom", e.target.value)}
            data-testid="input-date-from"
          />
        </div>

        {/* التاريخ إلى */}
        <div>
          <Label htmlFor="date-to">إلى تاريخ</Label>
          <Input
            id="date-to"
            type="date"
            value={filters.dateTo}
            onChange={(e) => handleFilterChange("dateTo", e.target.value)}
            data-testid="input-date-to"
          />
        </div>
      </div>
    </Card>
  );
}

