import { useState } from "react";
import { useMutation, useQuery } from "@tanstack/react-query";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogDescription,
  DialogFooter,
} from "../ui/dialog";
import { Button } from "../ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "../ui/card";
import { RadioGroup, RadioGroupItem } from "../ui/radio-group";
import { Label } from "../ui/label";
import { Slider } from "../ui/slider";
import { Alert, AlertDescription } from "../ui/alert";
import { Badge } from "../ui/badge";
import { Progress } from "../ui/progress";
import { ScrollArea } from "../ui/scroll-area";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "../ui/tabs";
import { apiRequest, queryClient } from "../../lib/queryClient";
import { useToast } from "../../hooks/use-toast";
import {
  Sparkles,
  Scale,
  Package,
  AlertTriangle,
  Layers,
  Zap,
  TrendingUp,
  Loader2,
  Info,
  CheckCircle,
  XCircle,
  Factory,
  BarChart3,
  Clock,
  Weight,
} from "lucide-react";

interface SmartDistributionModalProps {
  isOpen: boolean;
  onClose: () => void;
  onDistribute: () => void;
}

interface DistributionAlgorithm {
  id: string;
  name: string;
  nameAr: string;
  description: string;
  icon: React.ReactNode;
  color: string;
}

interface MachinePreview {
  machineId: number;
  machineName: string;
  machineNameAr: string;
  currentLoad: number;
  proposedLoad: number;
  proposedUtilization: number;
  newCapacityStatus: string;
  proposedOrders: any[];
  productionRate: number;
}

interface DistributionPreviewData {
  totalOrders: number;
  machineCount: number;
  efficiency: number;
  preview: MachinePreview[];
}

interface DistributionPreviewResponse {
  data: DistributionPreviewData;
}

interface MachineCapacityStat {
  machineId: number;
  machineName: string;
  machineNameAr: string;
  currentLoad: number;
  maxCapacity: number;
  utilizationPercentage: number;
  capacityStatus: string;
  orderCount: number;
  productionRate: number;
}

interface CapacityStatsResponse {
  data: MachineCapacityStat[];
}

interface DistributionResult {
  success: boolean;
  message: string;
}

const algorithms: DistributionAlgorithm[] = [
  {
    id: "balanced",
    name: "Balanced Distribution",
    nameAr: "التوزيع المتوازن",
    description: "توزيع متساوٍ حسب عدد الأوامر على جميع المكائن",
    icon: <Scale className="h-5 w-5" />,
    color: "bg-blue-100 border-blue-300",
  },
  {
    id: "load-based",
    name: "Load-Based Distribution",
    nameAr: "التوزيع حسب الحمولة",
    description: "توزيع الأوامر حسب الكمية الإجمالية والسعة المتاحة",
    icon: <Weight className="h-5 w-5" />,
    color: "bg-green-100 border-green-300",
  },
  {
    id: "priority",
    name: "Priority Distribution",
    nameAr: "التوزيع حسب الأولوية",
    description: "توزيع الأوامر العاجلة أولاً على المكائن الأقل حملاً",
    icon: <AlertTriangle className="h-5 w-5" />,
    color: "bg-red-100 border-red-300",
  },
  {
    id: "product-type",
    name: "Product Type Grouping",
    nameAr: "التوزيع حسب نوع المنتج",
    description: "تجميع المنتجات المشابهة على نفس الماكينة",
    icon: <Layers className="h-5 w-5" />,
    color: "bg-purple-100 border-purple-300",
  },
  {
    id: "hybrid",
    name: "Hybrid Optimization",
    nameAr: "التوزيع الهجين",
    description: "مزج جميع المعايير للحصول على أفضل توزيع",
    icon: <Zap className="h-5 w-5" />,
    color: "bg-orange-100 border-orange-300",
  },
];

export default function SmartDistributionModal({
  isOpen,
  onClose,
  onDistribute,
}: SmartDistributionModalProps) {
  const { toast } = useToast();
  const [selectedAlgorithm, setSelectedAlgorithm] = useState("balanced");
  const [hybridParams, setHybridParams] = useState({
    loadWeight: 30,
    capacityWeight: 30,
    priorityWeight: 20,
    typeWeight: 20,
  });

  // Fetch distribution preview
  const { data: preview, isLoading: isPreviewLoading } = useQuery<DistributionPreviewResponse>({
    queryKey: ["/api/machine-queues/distribution-preview", selectedAlgorithm, hybridParams],
    queryFn: async () => {
      const params = new URLSearchParams({
        algorithm: selectedAlgorithm,
        ...(selectedAlgorithm === "hybrid" 
          ? {
              loadWeight: String(hybridParams.loadWeight),
              capacityWeight: String(hybridParams.capacityWeight),
              priorityWeight: String(hybridParams.priorityWeight),
              typeWeight: String(hybridParams.typeWeight),
            }
          : {}),
      });
      const response = await fetch(`/api/machine-queues/distribution-preview?${params}`, {
        credentials: "include",
      });
      if (!response.ok) throw new Error("Failed to fetch preview");
      return response.json();
    },
    enabled: isOpen,
  });

  // Fetch machine capacity stats
  const { data: capacityStats } = useQuery<CapacityStatsResponse>({
    queryKey: ["/api/machines/capacity-stats"],
    enabled: isOpen,
  });

  // Apply distribution mutation
  const distributeMutation = useMutation({
    mutationFn: async (): Promise<DistributionResult> => {
      const response = await apiRequest("/api/machine-queues/smart-distribute", {
        method: "POST",
        body: JSON.stringify({
          algorithm: selectedAlgorithm,
          params: selectedAlgorithm === "hybrid" ? hybridParams : {},
        }),
      });
      return response.json();
    },
    onSuccess: (result) => {
      if (result.success) {
        toast({
          title: "تم التوزيع بنجاح",
          description: result.message,
        });
        queryClient.invalidateQueries({ queryKey: ["/api/machine-queues"] });
        queryClient.invalidateQueries({ queryKey: ["/api/production-orders"] });
        onDistribute();
        onClose();
      }
    },
    onError: (error: any) => {
      toast({
        title: "خطأ في التوزيع",
        description: error.message || "فشل تطبيق التوزيع الذكي",
        variant: "destructive",
      });
    },
  });

  const handleApply = () => {
    distributeMutation.mutate();
  };

  const getCapacityStatusColor = (status: string) => {
    switch (status) {
      case "low":
        return "text-green-600 bg-green-100";
      case "moderate":
        return "text-blue-600 bg-blue-100";
      case "high":
        return "text-yellow-600 bg-yellow-100";
      case "overloaded":
        return "text-red-600 bg-red-100";
      default:
        return "text-gray-600 bg-gray-100";
    }
  };

  const getUtilizationColor = (utilization: number) => {
    if (utilization > 90) return "bg-red-500";
    if (utilization > 70) return "bg-yellow-500";
    if (utilization > 40) return "bg-blue-500";
    return "bg-green-500";
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-hidden flex flex-col">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2 text-xl">
            <Sparkles className="h-6 w-6 text-purple-600" />
            التوزيع الذكي لأوامر الإنتاج
          </DialogTitle>
          <DialogDescription>
            اختر خوارزمية التوزيع المناسبة وعاين النتائج قبل التطبيق
          </DialogDescription>
        </DialogHeader>

        <Tabs defaultValue="algorithm" className="flex-1 overflow-hidden">
          <TabsList className="grid grid-cols-3 w-full">
            <TabsTrigger value="algorithm" data-testid="tab-algorithm">اختيار الخوارزمية</TabsTrigger>
            <TabsTrigger value="preview" data-testid="tab-preview">معاينة التوزيع</TabsTrigger>
            <TabsTrigger value="stats" data-testid="tab-stats">إحصائيات السعة</TabsTrigger>
          </TabsList>

          <ScrollArea className="flex-1 h-[500px] mt-4">
            <TabsContent value="algorithm" className="space-y-4">
              <RadioGroup
                value={selectedAlgorithm}
                onValueChange={setSelectedAlgorithm}
              >
                {algorithms.map((algo) => (
                  <Card
                    key={algo.id}
                    className={`cursor-pointer transition-all ${
                      selectedAlgorithm === algo.id
                        ? `${algo.color} border-2`
                        : "hover:shadow-md"
                    }`}
                    onClick={() => setSelectedAlgorithm(algo.id)}
                    data-testid={`card-algorithm-${algo.id}`}
                  >
                    <CardContent className="p-4">
                      <div className="flex items-start gap-3">
                        <RadioGroupItem value={algo.id} id={algo.id} data-testid={`radio-algorithm-${algo.id}`} />
                        <div className="flex-1">
                          <Label
                            htmlFor={algo.id}
                            className="flex items-center gap-2 text-base font-semibold cursor-pointer"
                          >
                            {algo.icon}
                            {algo.nameAr}
                          </Label>
                          <p className="text-sm text-muted-foreground mt-1">
                            {algo.description}
                          </p>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </RadioGroup>

              {selectedAlgorithm === "hybrid" && (
                <Card className="mt-4">
                  <CardHeader>
                    <CardTitle className="text-sm">معايير التوزيع الهجين</CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    <div>
                      <div className="flex justify-between mb-2">
                        <Label>وزن الحمولة</Label>
                        <span className="text-sm font-medium">
                          {hybridParams.loadWeight}%
                        </span>
                      </div>
                      <Slider
                        value={[hybridParams.loadWeight]}
                        onValueChange={([value]) =>
                          setHybridParams({ ...hybridParams, loadWeight: value })
                        }
                        max={100}
                        step={10}
                      />
                    </div>

                    <div>
                      <div className="flex justify-between mb-2">
                        <Label>وزن السعة</Label>
                        <span className="text-sm font-medium">
                          {hybridParams.capacityWeight}%
                        </span>
                      </div>
                      <Slider
                        value={[hybridParams.capacityWeight]}
                        onValueChange={([value]) =>
                          setHybridParams({ ...hybridParams, capacityWeight: value })
                        }
                        max={100}
                        step={10}
                      />
                    </div>

                    <div>
                      <div className="flex justify-between mb-2">
                        <Label>وزن الأولوية</Label>
                        <span className="text-sm font-medium">
                          {hybridParams.priorityWeight}%
                        </span>
                      </div>
                      <Slider
                        value={[hybridParams.priorityWeight]}
                        onValueChange={([value]) =>
                          setHybridParams({ ...hybridParams, priorityWeight: value })
                        }
                        max={100}
                        step={10}
                      />
                    </div>

                    <div>
                      <div className="flex justify-between mb-2">
                        <Label>وزن نوع المنتج</Label>
                        <span className="text-sm font-medium">
                          {hybridParams.typeWeight}%
                        </span>
                      </div>
                      <Slider
                        value={[hybridParams.typeWeight]}
                        onValueChange={([value]) =>
                          setHybridParams({ ...hybridParams, typeWeight: value })
                        }
                        max={100}
                        step={10}
                      />
                    </div>
                  </CardContent>
                </Card>
              )}
            </TabsContent>

            <TabsContent value="preview" className="space-y-4">
              {isPreviewLoading ? (
                <div className="flex items-center justify-center h-64">
                  <Loader2 className="h-8 w-8 animate-spin" />
                </div>
              ) : preview?.data ? (
                <>
                  <Alert>
                    <Info className="h-4 w-4" />
                    <AlertDescription>
                      <div className="flex items-center justify-between">
                        <span>
                          سيتم توزيع {preview.data.totalOrders} أمر على{" "}
                          {preview.data.machineCount} ماكينة
                        </span>
                        <Badge variant="outline">
                          كفاءة التوزيع: {preview.data.efficiency}%
                        </Badge>
                      </div>
                    </AlertDescription>
                  </Alert>

                  <div className="space-y-3">
                    {preview.data.preview?.map((machine: any) => (
                      <Card key={machine.machineId}>
                        <CardHeader className="pb-3">
                          <div className="flex items-center justify-between">
                            <CardTitle className="text-sm flex items-center gap-2">
                              <Factory className="h-4 w-4" />
                              {machine.machineNameAr || machine.machineName}
                            </CardTitle>
                            <div className="flex items-center gap-2">
                              <Badge
                                className={getCapacityStatusColor(
                                  machine.newCapacityStatus
                                )}
                              >
                                {machine.newCapacityStatus === "low"
                                  ? "منخفض"
                                  : machine.newCapacityStatus === "moderate"
                                  ? "متوسط"
                                  : machine.newCapacityStatus === "high"
                                  ? "مرتفع"
                                  : "مُحمل بشدة"}
                              </Badge>
                              <Badge variant="outline">
                                {machine.proposedOrders?.length || 0} أمر جديد
                              </Badge>
                            </div>
                          </div>
                        </CardHeader>
                        <CardContent>
                          <div className="space-y-3">
                            <div className="flex items-center justify-between text-sm">
                              <span>الحمولة الحالية:</span>
                              <span className="font-medium">
                                {machine.currentLoad?.toFixed(2)} كجم
                              </span>
                            </div>
                            <div className="flex items-center justify-between text-sm">
                              <span>الحمولة المقترحة:</span>
                              <span className="font-medium">
                                {machine.proposedLoad?.toFixed(2)} كجم
                              </span>
                            </div>
                            <div className="space-y-1">
                              <div className="flex justify-between text-sm">
                                <span>نسبة الاستخدام:</span>
                                <span>
                                  {machine.proposedUtilization?.toFixed(1)}%
                                </span>
                              </div>
                              <Progress
                                value={machine.proposedUtilization || 0}
                                className="h-2"
                                style={{
                                  backgroundColor: "#e5e5e5",
                                }}
                              >
                                <div
                                  className={`h-full transition-all ${getUtilizationColor(
                                    machine.proposedUtilization || 0
                                  )}`}
                                  style={{
                                    width: `${machine.proposedUtilization || 0}%`,
                                  }}
                                />
                              </Progress>
                            </div>
                            <div className="flex items-center gap-2 text-sm text-muted-foreground">
                              <Clock className="h-3 w-3" />
                              <span>
                                وقت الإنتاج المتوقع:{" "}
                                {((machine.currentLoad + machine.proposedLoad) /
                                  machine.productionRate).toFixed(1)}{" "}
                                ساعة
                              </span>
                            </div>
                          </div>
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                </>
              ) : (
                <Alert>
                  <AlertTriangle className="h-4 w-4" />
                  <AlertDescription>
                    لا توجد أوامر غير مخصصة للتوزيع
                  </AlertDescription>
                </Alert>
              )}
            </TabsContent>

            <TabsContent value="stats" className="space-y-4">
              {capacityStats?.data ? (
                <div className="space-y-3">
                  {capacityStats.data.map((stat: any) => (
                    <Card key={stat.machineId}>
                      <CardHeader className="pb-3">
                        <div className="flex items-center justify-between">
                          <CardTitle className="text-sm flex items-center gap-2">
                            <BarChart3 className="h-4 w-4" />
                            {stat.machineNameAr || stat.machineName}
                          </CardTitle>
                          <Badge
                            className={getCapacityStatusColor(stat.capacityStatus)}
                          >
                            {stat.utilizationPercentage?.toFixed(1)}%
                          </Badge>
                        </div>
                      </CardHeader>
                      <CardContent>
                        <div className="grid grid-cols-2 gap-4 text-sm">
                          <div>
                            <span className="text-muted-foreground">
                              الحمولة الحالية:
                            </span>
                            <p className="font-medium">
                              {stat.currentLoad?.toFixed(2)} كجم
                            </p>
                          </div>
                          <div>
                            <span className="text-muted-foreground">
                              السعة القصوى:
                            </span>
                            <p className="font-medium">
                              {stat.maxCapacity?.toFixed(2)} كجم
                            </p>
                          </div>
                          <div>
                            <span className="text-muted-foreground">
                              عدد الأوامر:
                            </span>
                            <p className="font-medium">{stat.orderCount}</p>
                          </div>
                          <div>
                            <span className="text-muted-foreground">
                              معدل الإنتاج:
                            </span>
                            <p className="font-medium">
                              {stat.productionRate} كجم/ساعة
                            </p>
                          </div>
                        </div>
                        <div className="mt-3 pt-3 border-t">
                          <Progress
                            value={stat.utilizationPercentage || 0}
                            className="h-3"
                          />
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              ) : (
                <div className="flex items-center justify-center h-64">
                  <p className="text-muted-foreground">
                    جاري تحميل إحصائيات السعة...
                  </p>
                </div>
              )}
            </TabsContent>
          </ScrollArea>
        </Tabs>

        <DialogFooter>
          <Button
            variant="outline"
            onClick={onClose}
            disabled={distributeMutation.isPending}
            data-testid="button-cancel-distribution"
          >
            إلغاء
          </Button>
          <Button
            onClick={handleApply}
            disabled={distributeMutation.isPending || !preview?.data?.preview?.length}
            data-testid="button-apply-distribution"
          >
            {distributeMutation.isPending ? (
              <>
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                جاري التطبيق...
              </>
            ) : (
              <>
                <CheckCircle className="mr-2 h-4 w-4" />
                تطبيق التوزيع
              </>
            )}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

client/src/print.css.content:
/* 
 * Print Styles for Orders and Production Orders
 * Professional A4 Format with RTL Support
 */

@media print {
  /* إخفاء العناصر غير المطلوبة */
  body > *:not(.print-container) {
    display: none !important;
  }

  .no-print,
  nav,
  header,
  footer,
  .sidebar,
  .mobile-nav,
  button:not(.print-container button),
  .toast,
  .dialog-overlay,
  .dropdown-menu {
    display: none !important;
  }

  /* إعدادات الصفحة */
  @page {
    size: A4 portrait;
    margin: 15mm 15mm 15mm 15mm;
  }

  body {
    margin: 0;
    padding: 0;
    font-family: 'Arial', 'Helvetica', 'Tahoma', sans-serif;
    font-size: 11pt;
    line-height: 1.4;
    color: #000;
    background: white;
    direction: rtl;
  }

  /* حاوية الطباعة */
  .print-container {
    display: block !important;
    width: 100%;
    max-width: none;
    margin: 0;
    padding: 0;
    background: white;
    page-break-after: always;
  }

  .print-container:last-child {
    page-break-after: auto;
  }

  /* رأس المستند */
  .print-header {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    margin-bottom: 20px;
    padding-bottom: 15px;
    border-bottom: 2px solid #000;
  }

  .print-logo {
    max-width: 150px;
    max-height: 80px;
  }

  .print-title {
    font-size: 18pt;
    font-weight: bold;
    text-align: center;
    margin: 0;
  }

  .print-subtitle {
    font-size: 12pt;
    text-align: center;
    color: #333;
    margin: 5px 0 0 0;
  }

  .print-qr {
    max-width: 80px;
    max-height: 80px;
  }

  /* معلومات المستند */
  .print-info {
    margin: 15px 0;
    padding: 10px;
    background: #f5f5f5;
    border: 1px solid #ddd;
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 10px;
  }

  .print-info-item {
    margin: 0;
  }

  .print-info-label {
    font-weight: bold;
    display: inline-block;
    min-width: 120px;
  }

  .print-info-value {
    display: inline;
  }

  /* الجداول */
  .print-table {
    width: 100%;
    border-collapse: collapse;
    margin: 15px 0;
    font-size: 10pt;
  }

  .print-table th {
    background: #333;
    color: white;
    padding: 8px 5px;
    text-align: right;
    font-weight: bold;
    border: 1px solid #000;
  }

  .print-table td {
    padding: 6px 5px;
    border: 1px solid #333;
    text-align: right;
  }

  .print-table tr:nth-child(even) {
    background: #f9f9f9;
  }

  .print-table tfoot td {
    font-weight: bold;
    background: #e9e9e9;
    border-top: 2px solid #000;
  }

  /* القسم */
  .print-section {
    margin: 20px 0;
    page-break-inside: avoid;
  }

  .print-section-title {
    font-size: 13pt;
    font-weight: bold;
    margin-bottom: 10px;
    padding-bottom: 5px;
    border-bottom: 1px solid #666;
  }

  /* الملاحظات */
  .print-notes {
    margin: 15px 0;
    padding: 10px;
    border: 1px solid #999;
    background: #fafafa;
    min-height: 60px;
  }

  .print-notes-title {
    font-weight: bold;
    margin-bottom: 5px;
  }

  .print-notes-content {
    white-space: pre-wrap;
    word-wrap: break-word;
  }

  /* التوقيعات */
  .print-signatures {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 20px;
    margin-top: 30mm;
    page-break-inside: avoid;
  }

  .print-signature {
    text-align: center;
    border-top: 1px solid #000;
    padding-top: 5px;
  }

  .print-signature-label {
    font-weight: bold;
    font-size: 10pt;
  }

  .print-signature-line {
    margin: 40px 0 5px 0;
    border-bottom: 1px solid #000;
  }

  /* Footer المستند */
  .print-footer {
    margin-top: 20px;
    padding-top: 10px;
    border-top: 1px solid #999;
    font-size: 9pt;
    text-align: center;
    color: #666;
  }

  /* الباركود و QR */
  .print-barcode,
  .print-qr-code {
    display: block;
    margin: 10px auto;
    max-width: 200px;
  }

  /* Badge و الحالة */
  .print-badge {
    display: inline-block;
    padding: 3px 8px;
    border-radius: 3px;
    font-size: 9pt;
    font-weight: bold;
    border: 1px solid #000;
  }

  .print-badge-success {
    background: #e0f0e0;
  }

  .print-badge-warning {
    background: #fff8dc;
  }

  .print-badge-danger {
    background: #ffe0e0;
  }

  .print-badge-info {
    background: #e0e8ff;
  }

  /* فواصل الصفحات */
  .page-break {
    page-break-after: always;
  }

  .page-break-before {
    page-break-before: always;
  }

  .avoid-page-break {
    page-break-inside: avoid;
  }

  /* Grid layouts */
  .print-grid-2 {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 15px;
  }

  .print-grid-3 {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 15px;
  }

  /* تحسينات الطباعة */
  * {
    -webkit-print-color-adjust: exact !important;
    print-color-adjust: exact !important;
    color-adjust: exact !important;
  }

  a {
    text-decoration: none;
    color: #000;
  }

  img {
    max-width: 100%;
    height: auto;
  }

  /* إخفاء scrollbars */
  ::-webkit-scrollbar {
    display: none;
  }

  /* تحسين النصوص */
  p, div, span {
    orphans: 3;
    widows: 3;
  }

  h1, h2, h3, h4, h5, h6 {
    page-break-after: avoid;
    page-break-inside: avoid;
  }

  /* Progress bar للطباعة */
  .print-progress {
    width: 100%;
    height: 20px;
    background: #e0e0e0;
    border: 1px solid #999;
    position: relative;
    overflow: hidden;
  }

  .print-progress-bar {
    height: 100%;
    background: #4caf50;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 9pt;
    font-weight: bold;
    color: #000;
  }

  /* Stats cards للطباعة */
  .print-stats {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 10px;
    margin: 15px 0;
  }

  .print-stat-card {
    padding: 10px;
    border: 1px solid #999;
    text-align: center;
  }

  .print-stat-label {
    font-size: 9pt;
    color: #666;
  }

  .print-stat-value {
    font-size: 14pt;
    font-weight: bold;
    margin-top: 5px;
  }
}

/* Print preview styles (shown on screen before printing) */
.print-preview {
  background: #f5f5f5;
  padding: 20px;
  min-height: 100vh;
}

.print-preview .print-container {
  background: white;
  max-width: 210mm;
  margin: 0 auto 20px auto;
  padding: 15mm;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

client/src/pages/ProductionReports.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { format, subDays } from "date-fns";
import {
  FileDown,
  Search,
  RotateCcw,
  TrendingUp,
  Package,
  Activity,
  Clock,
  AlertTriangle,
  CheckCircle,
  FileSpreadsheet,
  FileText,
} from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "../components/ui/card";
import { Button } from "../components/ui/button";
import { Input } from "../components/ui/input";
import { Label } from "../components/ui/label";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "../components/ui/tabs";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "../components/ui/table";
import { Badge } from "../components/ui/badge";
import { Skeleton } from "../components/ui/skeleton";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../components/ui/select";
import {
  LineChart,
  Line,
  BarChart,
  Bar,
  PieChart,
  Pie,
  AreaChart,
  Area,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  Cell,
} from "recharts";
import * as XLSX from "xlsx";
import { useToast } from "../hooks/use-toast";

const COLORS = ["#10b981", "#3b82f6", "#f59e0b", "#ef4444", "#8b5cf6", "#ec4899"];

export default function ProductionReports() {
  const { toast } = useToast();
  const [filters, setFilters] = useState({
    dateFrom: format(subDays(new Date(), 30), "yyyy-MM-dd"),
    dateTo: format(new Date(), "yyyy-MM-dd"),
    customerId: [] as number[],
    productId: [] as number[],
    status: [] as string[],
    sectionId: "",
    machineId: "",
    operatorId: "",
  });

  const [activeFilters, setActiveFilters] = useState(filters);

  // Fetch data using React Query
  const { data: summary, isLoading: summaryLoading } = useQuery<any>({
    queryKey: ["/api/reports/production-summary", activeFilters],
    enabled: !!activeFilters.dateFrom && !!activeFilters.dateTo,
  });

  const { data: productionByDate, isLoading: dateLoading } = useQuery<any>({
    queryKey: ["/api/reports/production-by-date", activeFilters],
    enabled: !!activeFilters.dateFrom && !!activeFilters.dateTo,
  });

  const { data: productionByProduct, isLoading: productLoading } = useQuery<any>({
    queryKey: ["/api/reports/production-by-product", activeFilters],
    enabled: !!activeFilters.dateFrom && !!activeFilters.dateTo,
  });

  const { data: wasteAnalysis, isLoading: wasteLoading } = useQuery<any>({
    queryKey: ["/api/reports/waste-analysis", activeFilters],
    enabled: !!activeFilters.dateFrom && !!activeFilters.dateTo,
  });

  const { data: machinePerformance, isLoading: machineLoading } = useQuery<any>({
    queryKey: ["/api/reports/machine-performance", activeFilters],
    enabled: !!activeFilters.dateFrom && !!activeFilters.dateTo,
  });

  const { data: operatorPerformance, isLoading: operatorLoading } = useQuery<any>({
    queryKey: ["/api/reports/operator-performance", activeFilters],
    enabled: !!activeFilters.dateFrom && !!activeFilters.dateTo,
  });

  // Fetch filter options
  const { data: customers } = useQuery<any>({ queryKey: ["/api/customers"] });
  const { data: products } = useQuery<any>({ queryKey: ["/api/customer-products"] });
  const { data: machines } = useQuery<any>({ queryKey: ["/api/machines"] });
  const { data: users } = useQuery<any>({ queryKey: ["/api/users"] });
  const { data: sections } = useQuery<any>({ queryKey: ["/api/sections"] });

  const handleSearch = () => {
    setActiveFilters(filters);
  };

  const handleReset = () => {
    const defaultFilters = {
      dateFrom: format(subDays(new Date(), 30), "yyyy-MM-dd"),
      dateTo: format(new Date(), "yyyy-MM-dd"),
      customerId: [] as number[],
      productId: [] as number[],
      status: [] as string[],
      sectionId: "",
      machineId: "",
      operatorId: "",
    };
    setFilters(defaultFilters);
    setActiveFilters(defaultFilters);
  };

  const exportToExcel = () => {
    try {
      const workbook = XLSX.utils.book_new();

      // Summary sheet
      if (summary?.data) {
        const summaryData = [
          ["إجمالي الطلبات", summary.data.totalOrders],
          ["أوامر نشطة", summary.data.activeOrders],
          ["أوامر مكتملة", summary.data.completedOrders],
          ["الرولات المنتجة", summary.data.totalRolls],
          ["الوزن الكلي (كجم)", summary.data.totalWeight],
          ["متوسط وقت الإنتاج (ساعة)", summary.data.avgProductionTime?.toFixed(2)],
          ["نسبة الهدر %", summary.data.wastePercentage?.toFixed(2)],
          ["معدل الإنجاز %", summary.data.completionRate?.toFixed(2)],
        ];
        const summarySheet = XLSX.utils.aoa_to_sheet(summaryData);
        XLSX.utils.book_append_sheet(workbook, summarySheet, "الملخص");
      }

      // Production by date sheet
      if (productionByDate?.data) {
        const dateSheet = XLSX.utils.json_to_sheet(productionByDate.data);
        XLSX.utils.book_append_sheet(workbook, dateSheet, "الإنتاج اليومي");
      }

      // Production by product sheet
      if (productionByProduct?.data) {
        const productSheet = XLSX.utils.json_to_sheet(productionByProduct.data);
        XLSX.utils.book_append_sheet(workbook, productSheet, "الإنتاج حسب المنتج");
      }

      // Machine performance sheet
      if (machinePerformance?.data) {
        const machineSheet = XLSX.utils.json_to_sheet(machinePerformance.data);
        XLSX.utils.book_append_sheet(workbook, machineSheet, "أداء المكائن");
      }

      // Operator performance sheet
      if (operatorPerformance?.data) {
        const operatorSheet = XLSX.utils.json_to_sheet(operatorPerformance.data);
        XLSX.utils.book_append_sheet(workbook, operatorSheet, "أداء العمال");
      }

      XLSX.writeFile(workbook, `تقرير_الإنتاج_${format(new Date(), "yyyy-MM-dd")}.xlsx`);
      toast({
        title: "تم التصدير بنجاح",
        description: "تم تصدير التقرير إلى ملف Excel",
      });
    } catch (error) {
      console.error("Export error:", error);
      toast({
        title: "خطأ في التصدير",
        description: "حدث خطأ أثناء تصدير التقرير",
        variant: "destructive",
      });
    }
  };

  const exportToPDF = () => {
    window.print();
  };

  const getStatusColor = (value: number, metric: string) => {
    if (metric === "waste") {
      if (value < 3) return "text-green-600 bg-green-50";
      if (value < 5) return "text-yellow-600 bg-yellow-50";
      return "text-red-600 bg-red-50";
    }
    if (value >= 90) return "text-green-600 bg-green-50";
    if (value >= 70) return "text-yellow-600 bg-yellow-50";
    return "text-red-600 bg-red-50";
  };

  return (
    <div className="container mx-auto p-6 space-y-6 max-w-7xl">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold" data-testid="text-page-title">تقارير الإنتاج الشاملة</h1>
          <p className="text-muted-foreground">تحليل شامل لأداء الإنتاج مع رسوم بيانية تفاعلية</p>
        </div>
        <div className="flex gap-2">
          <Button onClick={exportToExcel} variant="outline" data-testid="button-export-excel">
            <FileSpreadsheet className="mr-2 h-4 w-4" />
            تصدير Excel
          </Button>
          <Button onClick={exportToPDF} variant="outline" data-testid="button-export-pdf">
            <FileText className="mr-2 h-4 w-4" />
            طباعة PDF
          </Button>
        </div>
      </div>

      {/* Filters Section */}
      <Card>
        <CardHeader>
          <CardTitle>فلاتر التقرير</CardTitle>
          <CardDescription>اختر المعايير لتخصيص التقرير</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            <div className="space-y-2">
              <Label htmlFor="dateFrom">من تاريخ</Label>
              <Input
                id="dateFrom"
                type="date"
                value={filters.dateFrom}
                onChange={(e) => setFilters({ ...filters, dateFrom: e.target.value })}
                data-testid="input-date-from"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="dateTo">إلى تاريخ</Label>
              <Input
                id="dateTo"
                type="date"
                value={filters.dateTo}
                onChange={(e) => setFilters({ ...filters, dateTo: e.target.value })}
                data-testid="input-date-to"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="section">القسم</Label>
              <Select
                value={filters.sectionId || "all"}
                onValueChange={(value) => setFilters({ ...filters, sectionId: value === "all" ? "" : value })}
              >
                <SelectTrigger data-testid="select-section">
                  <SelectValue placeholder="اختر القسم" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">جميع الأقسام</SelectItem>
                  {sections?.map((section: any) => (
                    <SelectItem key={section.id} value={section.id}>
                      {section.name_ar || section.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <Label htmlFor="machine">الماكينة</Label>
              <Select
                value={filters.machineId || "all"}
                onValueChange={(value) => setFilters({ ...filters, machineId: value === "all" ? "" : value })}
              >
                <SelectTrigger data-testid="select-machine">
                  <SelectValue placeholder="اختر الماكينة" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">جميع المكائن</SelectItem>
                  {machines?.map((machine: any) => (
                    <SelectItem key={machine.id} value={machine.id}>
                      {machine.name_ar || machine.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>

          <div className="flex gap-2 mt-4">
            <Button onClick={handleSearch} data-testid="button-search">
              <Search className="mr-2 h-4 w-4" />
              بحث
            </Button>
            <Button onClick={handleReset} variant="outline" data-testid="button-reset">
              <RotateCcw className="mr-2 h-4 w-4" />
              إعادة تعيين
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* KPI Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-6 gap-4">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">إجمالي الطلبات</CardTitle>
            <Package className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            {summaryLoading ? (
              <Skeleton className="h-8 w-20" />
            ) : (
              <div className="text-2xl font-bold" data-testid="text-total-orders">
                {summary?.data?.totalOrders || 0}
              </div>
            )}
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">أوامر نشطة</CardTitle>
            <Activity className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            {summaryLoading ? (
              <Skeleton className="h-8 w-20" />
            ) : (
              <div className="text-2xl font-bold text-blue-600" data-testid="text-active-orders">
                {summary?.data?.activeOrders || 0}
              </div>
            )}
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">الرولات المنتجة</CardTitle>
            <TrendingUp className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            {summaryLoading ? (
              <Skeleton className="h-8 w-20" />
            ) : (
              <div className="text-2xl font-bold" data-testid="text-total-rolls">
                {summary?.data?.totalRolls || 0}
              </div>
            )}
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">متوسط وقت الإنتاج</CardTitle>
            <Clock className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            {summaryLoading ? (
              <Skeleton className="h-8 w-20" />
            ) : (
              <div className="text-2xl font-bold" data-testid="text-avg-time">
                {summary?.data?.avgProductionTime?.toFixed(1) || "0"} ساعة
              </div>
            )}
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">نسبة الهدر</CardTitle>
            <AlertTriangle className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            {summaryLoading ? (
              <Skeleton className="h-8 w-20" />
            ) : (
              <div
                className={`text-2xl font-bold rounded px-2 ${getStatusColor(summary?.data?.wastePercentage || 0, "waste")}`}
                data-testid="text-waste-percentage"
              >
                {summary?.data?.wastePercentage?.toFixed(2) || "0"}%
              </div>
            )}
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">معدل الإنجاز</CardTitle>
            <CheckCircle className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            {summaryLoading ? (
              <Skeleton className="h-8 w-20" />
            ) : (
              <div
                className={`text-2xl font-bold rounded px-2 ${getStatusColor(summary?.data?.completionRate || 0, "completion")}`}
                data-testid="text-completion-rate"
              >
                {summary?.data?.completionRate?.toFixed(1) || "0"}%
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      {/* Charts Section */}
      <Tabs defaultValue="daily" className="space-y-4">
        <TabsList className="grid w-full grid-cols-5">
          <TabsTrigger value="daily" data-testid="tab-daily">الإنتاج اليومي</TabsTrigger>
          <TabsTrigger value="products" data-testid="tab-products">حسب المنتج</TabsTrigger>
          <TabsTrigger value="waste" data-testid="tab-waste">تحليل الهدر</TabsTrigger>
          <TabsTrigger value="machines" data-testid="tab-machines">أداء المكائن</TabsTrigger>
          <TabsTrigger value="operators" data-testid="tab-operators">أداء العمال</TabsTrigger>
        </TabsList>

        <TabsContent value="daily" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>الإنتاج اليومي</CardTitle>
              <CardDescription>عدد الرولات والوزن المنتج يومياً</CardDescription>
            </CardHeader>
            <CardContent>
              {dateLoading ? (
                <Skeleton className="h-[300px] w-full" />
              ) : (
                <ResponsiveContainer width="100%" height={300}>
                  <LineChart data={productionByDate?.data || []}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="date" />
                    <YAxis yAxisId="left" />
                    <YAxis yAxisId="right" orientation="right" />
                    <Tooltip />
                    <Legend />
                    <Line
                      yAxisId="left"
                      type="monotone"
                      dataKey="rollsCount"
                      stroke="#3b82f6"
                      name="عدد الرولات"
                      strokeWidth={2}
                    />
                    <Line
                      yAxisId="right"
                      type="monotone"
                      dataKey="totalWeight"
                      stroke="#10b981"
                      name="الوزن (كجم)"
                      strokeWidth={2}
                    />
                  </LineChart>
                </ResponsiveContainer>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="products" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>الإنتاج حسب المنتج</CardTitle>
              <CardDescription>توزيع الإنتاج على المنتجات المختلفة</CardDescription>
            </CardHeader>
            <CardContent>
              {productLoading ? (
                <Skeleton className="h-[300px] w-full" />
              ) : (
                <ResponsiveContainer width="100%" height={300}>
                  <BarChart data={productionByProduct?.data || []}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="productName" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey="totalWeight" fill="#3b82f6" name="الوزن (كجم)" />
                    <Bar dataKey="rollsCount" fill="#10b981" name="عدد الرولات" />
                  </BarChart>
                </ResponsiveContainer>
              )}
            </CardContent>
          </Card>

          {/* Pie Chart */}
          <Card>
            <CardHeader>
              <CardTitle>توزيع الإنتاج</CardTitle>
              <CardDescription>النسبة المئوية لكل منتج</CardDescription>
            </CardHeader>
            <CardContent>
              {productLoading ? (
                <Skeleton className="h-[300px] w-full" />
              ) : (
                <ResponsiveContainer width="100%" height={300}>
                  <PieChart>
                    <Pie
                      data={productionByProduct?.data || []}
                      cx="50%"
                      cy="50%"
                      labelLine={false}
                      label={(entry) => entry.productName}
                      outerRadius={80}
                      fill="#8884d8"
                      dataKey="totalWeight"
                    >
                      {(productionByProduct?.data || []).map((entry: any, index: number) => (
                        <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                      ))}
                    </Pie>
                    <Tooltip />
                    <Legend />
                  </PieChart>
                </ResponsiveContainer>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="waste" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>اتجاه الهدر عبر الوقت</CardTitle>
              <CardDescription>تحليل كمية الهدر اليومية</CardDescription>
            </CardHeader>
            <CardContent>
              {wasteLoading ? (
                <Skeleton className="h-[300px] w-full" />
              ) : (
                <ResponsiveContainer width="100%" height={300}>
                  <AreaChart data={wasteAnalysis?.data || []}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="date" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Area
                      type="monotone"
                      dataKey="totalWaste"
                      stroke="#ef4444"
                      fill="#fecaca"
                      name="كمية الهدر (كجم)"
                    />
                  </AreaChart>
                </ResponsiveContainer>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="machines" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>أداء المكائن</CardTitle>
              <CardDescription>الإنتاجية حسب كل ماكينة</CardDescription>
            </CardHeader>
            <CardContent>
              {machineLoading ? (
                <Skeleton className="h-[300px] w-full" />
              ) : (
                <ResponsiveContainer width="100%" height={300}>
                  <BarChart data={machinePerformance?.data || []}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="machineName" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey="totalWeight" fill="#3b82f6" name="الوزن (كجم)" />
                    <Bar dataKey="rollsCount" fill="#10b981" name="عدد الرولات" />
                  </BarChart>
                </ResponsiveContainer>
              )}
            </CardContent>
          </Card>

          {/* Machine Performance Table */}
          <Card>
            <CardHeader>
              <CardTitle>جدول أداء المكائن</CardTitle>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>الماكينة</TableHead>
                    <TableHead>عدد الرولات</TableHead>
                    <TableHead>الوزن الكلي (كجم)</TableHead>
                    <TableHead>متوسط الوقت (ساعة)</TableHead>
                    <TableHead>الكفاءة</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {machinePerformance?.data?.map((machine: any) => (
                    <TableRow key={machine.machineId}>
                      <TableCell className="font-medium">{machine.machineName}</TableCell>
                      <TableCell>{machine.rollsCount}</TableCell>
                      <TableCell>{machine.totalWeight?.toFixed(2)}</TableCell>
                      <TableCell>{machine.avgProductionTime?.toFixed(2)}</TableCell>
                      <TableCell>
                        <Badge variant={machine.efficiency > 100 ? "default" : "secondary"}>
                          {machine.efficiency?.toFixed(2)}
                        </Badge>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="operators" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>أداء العمال</CardTitle>
              <CardDescription>إنتاجية كل عامل</CardDescription>
            </CardHeader>
            <CardContent>
              {operatorLoading ? (
                <Skeleton className="h-[300px] w-full" />
              ) : (
                <ResponsiveContainer width="100%" height={300}>
                  <BarChart data={operatorPerformance?.data || []}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="operatorName" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey="totalWeight" fill="#8b5cf6" name="الوزن (كجم)" />
                    <Bar dataKey="rollsCount" fill="#ec4899" name="عدد الرولات" />
                  </BarChart>
                </ResponsiveContainer>
              )}
            </CardContent>
          </Card>

          {/* Operator Performance Table */}
          <Card>
            <CardHeader>
              <CardTitle>جدول أداء العمال</CardTitle>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>العامل</TableHead>
                    <TableHead>عدد الرولات</TableHead>
                    <TableHead>الوزن الكلي (كجم)</TableHead>
                    <TableHead>متوسط وزن الرولة</TableHead>
                    <TableHead>الإنتاجية</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {operatorPerformance?.data?.map((operator: any) => (
                    <TableRow key={operator.operatorId}>
                      <TableCell className="font-medium">{operator.operatorName}</TableCell>
                      <TableCell>{operator.rollsCount}</TableCell>
                      <TableCell>{operator.totalWeight?.toFixed(2)}</TableCell>
                      <TableCell>{operator.avgRollWeight?.toFixed(2)}</TableCell>
                      <TableCell>
                        <Badge variant={operator.productivity > 50 ? "default" : "secondary"}>
                          {operator.productivity?.toFixed(2)}
                        </Badge>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

