client/src/components/ui/icon-with-toolt.content:
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "./tooltip";

interface IconWithTooltipProps {
  icon: React.ReactNode;
  tooltip: string;
  side?: "top" | "right" | "bottom" | "left";
  className?: string;
  onClick?: () => void;
  "data-testid"?: string;
}

export function IconWithTooltip({
  icon,
  tooltip,
  side = "top",
  className = "",
  onClick,
  "data-testid": dataTestId,
}: IconWithTooltipProps) {
  return (
    <TooltipProvider delayDuration={200}>
      <Tooltip>
        <TooltipTrigger asChild>
          <span 
            className={`cursor-pointer ${className}`} 
            onClick={onClick}
            data-testid={dataTestId}
          >
            {icon}
          </span>
        </TooltipTrigger>
        <TooltipContent side={side} className="text-sm">
          {tooltip}
        </TooltipContent>
      </Tooltip>
    </TooltipProvider>
  );
}

