type LogLevel = 'debug' | 'info' | 'warn' | 'error';

interface LoggerConfig {
  level: LogLevel;
  enableConsole: boolean;
  redactSensitiveData: boolean;
}

const LOG_LEVELS: Record<LogLevel, number> = {
  debug: 0,
  info: 1,
  warn: 2,
  error: 3,
};

class Logger {
  private config: LoggerConfig;

  constructor() {
    const isProduction = process.env.NODE_ENV === 'production';

    this.config = {
      level: isProduction ? 'warn' : 'debug',
      enableConsole: true,
      redactSensitiveData: isProduction,
    };
  }

  private shouldLog(level: LogLevel): boolean {
    return LOG_LEVELS[level] >= LOG_LEVELS[this.config.level];
  }

  private redact(data: any): any {
    if (!this.config.redactSensitiveData) {
      return data;
    }

    if (typeof data === 'string') {
      return data
        .replace(/user[_\s]?id[:\s=]+\d+/gi, 'user_id=[REDACTED]')
        .replace(/session[_\s]?id[:\s=]+[\w-]+/gi, 'session_id=[REDACTED]')
        .replace(/password[:\s=]+.+/gi, 'password=[REDACTED]')
        .replace(/token[:\s=]+[\w.-]+/gi, 'token=[REDACTED]')
        .replace(/api[_\s]?key[:\s=]+[\w-]+/gi, 'api_key=[REDACTED]')
        .replace(/authorization:\s*bearer\s+[\w.-]+/gi, 'authorization: Bearer [REDACTED]');
    }

    if (typeof data === 'object' && data !== null) {
      const redacted = { ...data };
      const sensitiveKeys = [
        'password', 'token', 'apiKey', 'api_key', 'accessToken', 
        'access_token', 'refreshToken', 'refresh_token', 'secret',
        'authorization', 'sessionId', 'session_id'
      ];

      for (const key of Object.keys(redacted)) {
        if (sensitiveKeys.some(sk => key.toLowerCase().includes(sk.toLowerCase()))) {
          redacted[key] = '[REDACTED]';
        } else if (typeof redacted[key] === 'object') {
          redacted[key] = this.redact(redacted[key]);
        }
      }

      return redacted;
    }

    return data;
  }

  private formatMessage(level: LogLevel, message: string, ...args: any[]): string {
    const timestamp = new Date().toISOString();
    const formattedArgs = args.map(arg => {
      // Handle Error objects specially to preserve stack traces
      if (arg instanceof Error) {
        const errorObj = {
          name: arg.name,
          message: this.config.redactSensitiveData ? this.redact(arg.message) : arg.message,
          stack: this.config.redactSensitiveData ? this.redact(arg.stack || '') : arg.stack,
        };
        return errorObj;
      }
      return this.redact(arg);
    });

    const argsString = formattedArgs.length > 0 
      ? ' ' + formattedArgs.map(arg => {
          if (typeof arg === 'object' && arg !== null) {
            // For error objects and other objects, use JSON.stringify
            return JSON.stringify(arg, null, 2);
          }
          return String(arg);
        }).join(' ')
      : '';

    return `[${timestamp}] [${level.toUpperCase()}] ${message}${argsString}`;
  }

  debug(message: string, ...args: any[]): void {
    if (this.shouldLog('debug') && this.config.enableConsole) {
      console.log(this.formatMessage('debug', message, ...args));
    }
  }

  info(message: string, ...args: any[]): void {
    if (this.shouldLog('info') && this.config.enableConsole) {
      console.log(this.formatMessage('info', message, ...args));
    }
  }

  warn(message: string, ...args: any[]): void {
    if (this.shouldLog('warn') && this.config.enableConsole) {
      console.warn(this.formatMessage('warn', message, ...args));
    }
  }

  error(message: string, ...args: any[]): void {
    if (this.shouldLog('error') && this.config.enableConsole) {
      console.error(this.formatMessage('error', message, ...args));
    }
  }

  session(action: string, userId?: number): void {
    if (process.env.NODE_ENV === 'production') {
      this.info(`Session ${action}`, userId ? { userId: '[REDACTED]' } : {});
    } else {
      this.debug(`Session ${action}`, userId ? { userId } : {});
    }
  }
}

export const logger = new Logger();
export default logger;

client/src/components/orders/RollsTab.ts.content:
import { useState, useMemo } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "../ui/card";
import { Input } from "../ui/input";
import { Button } from "../ui/button";
import { Badge } from "../ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "../ui/select";
import { Label } from "../ui/label";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "../ui/table";
import { Skeleton } from "../ui/skeleton";
import { Calendar } from "../ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "../ui/popover";
import { cn } from "../../lib/utils";
import { formatNumber } from "../../lib/formatNumber";
import {
  Search,
  Filter,
  CalendarIcon,
  Film,
  PrinterIcon,
  Scissors,
  CheckCircle,
  Package,
  RefreshCw,
  X,
  Download,
} from "lucide-react";
import { format } from "date-fns";
import { ar } from "date-fns/locale";
import * as XLSX from "xlsx";

interface RollData {
  roll_id: number;
  roll_number: string;
  roll_seq: number;
  stage: string;
  weight_kg: string;
  cut_weight_total_kg?: string;
  waste_kg?: string;
  created_at: string;
  printed_at?: string;
  cut_completed_at?: string;
  production_order_id: number;
  production_order_number: string;
  order_id: number;
  order_number: string;
  customer_id: string;
  customer_name: string;
  customer_name_ar?: string;
  item_name?: string;
  item_name_ar?: string;
  size_caption?: string;
  color?: string;
  punching?: string;
  film_machine_name?: string;
  printing_machine_name?: string;
  cutting_machine_name?: string;
  created_by_name?: string;
  printed_by_name?: string;
  cut_by_name?: string;
}

interface RollsTabProps {
  customers?: any[];
  productionOrders?: any[];
}

export default function RollsTab({ customers = [], productionOrders = [] }: RollsTabProps) {
  const [searchTerm, setSearchTerm] = useState("");
  const [stageFilter, setStageFilter] = useState("all");
  const [customerFilter, setCustomerFilter] = useState("all");
  const [productionOrderFilter, setProductionOrderFilter] = useState("all");
  const [startDate, setStartDate] = useState<Date | undefined>();
  const [endDate, setEndDate] = useState<Date | undefined>();
  const [showFilters, setShowFilters] = useState(false);

  // جلب الرولات
  const { data: rolls = [], isLoading, refetch } = useQuery<RollData[]>({
    queryKey: ["/api/rolls/search"],
    queryFn: async () => {
      const response = await fetch("/api/rolls/search?q=");
      if (!response.ok) throw new Error("فشل في جلب الرولات");
      return response.json();
    },
  });

  // الفلترة
  const filteredRolls = useMemo(() => {
    return rolls.filter((roll) => {
      // البحث النصي
      const searchLower = searchTerm.toLowerCase();
      const matchesSearch = !searchTerm || 
        roll.roll_number.toLowerCase().includes(searchLower) ||
        roll.production_order_number.toLowerCase().includes(searchLower) ||
        roll.order_number.toLowerCase().includes(searchLower) ||
        (roll.customer_name_ar || roll.customer_name).toLowerCase().includes(searchLower) ||
        (roll.item_name_ar || roll.item_name || "").toLowerCase().includes(searchLower);

      // فلتر المرحلة
      const matchesStage = stageFilter === "all" || roll.stage === stageFilter;

      // فلتر العميل
      const matchesCustomer = customerFilter === "all" || roll.customer_id === customerFilter;

      // فلتر أمر الإنتاج
      const matchesProductionOrder = productionOrderFilter === "all" || 
        roll.production_order_id === parseInt(productionOrderFilter);

      // فلتر التاريخ
      const rollDate = new Date(roll.created_at);
      const matchesStartDate = !startDate || rollDate >= startDate;
      const matchesEndDate = !endDate || rollDate <= endDate;

      return matchesSearch && matchesStage && matchesCustomer && 
             matchesProductionOrder && matchesStartDate && matchesEndDate;
    });
  }, [rolls, searchTerm, stageFilter, customerFilter, productionOrderFilter, startDate, endDate]);

  // إحصائيات سريعة
  const stats = useMemo(() => {
    const byStage = {
      film: filteredRolls.filter(r => r.stage === "film").length,
      printing: filteredRolls.filter(r => r.stage === "printing").length,
      cutting: filteredRolls.filter(r => r.stage === "cutting").length,
      done: filteredRolls.filter(r => r.stage === "done").length,
      archived: filteredRolls.filter(r => r.stage === "archived").length,
    };
    const totalWeight = filteredRolls.reduce((sum, r) => sum + parseFloat(r.weight_kg || "0"), 0);
    return { byStage, totalWeight, total: filteredRolls.length };
  }, [filteredRolls]);

  // الترجمة
  const getStageNameAr = (stage: string) => {
    const stages: Record<string, string> = {
      film: "فيلم",
      printing: "طباعة",
      cutting: "تقطيع",
      done: "منتهي",
      archived: "مؤرشف",
    };
    return stages[stage] || stage;
  };

  const getStageBadgeVariant = (stage: string): "default" | "secondary" | "destructive" | "outline" => {
    const variants: Record<string, "default" | "secondary" | "destructive" | "outline"> = {
      film: "secondary",
      printing: "default",
      cutting: "outline",
      done: "default",
      archived: "secondary",
    };
    return variants[stage] || "default";
  };

  const getStageBadgeClassName = (stage: string) => {
    if (stage === "done") {
      return "bg-green-100 text-green-800 hover:bg-green-200 dark:bg-green-900 dark:text-green-100";
    }
    return "";
  };

  const getStageIcon = (stage: string) => {
    const icons: Record<string, any> = {
      film: Film,
      printing: PrinterIcon,
      cutting: Scissors,
      done: CheckCircle,
      archived: Package,
    };
    const Icon = icons[stage] || Package;
    return <Icon className="h-4 w-4" />;
  };

  // تصدير إلى Excel
  const exportToExcel = () => {
    if (filteredRolls.length === 0) {
      alert("لا توجد بيانات للتصدير");
      return;
    }

    const data = filteredRolls.map((roll) => ({
      "رقم الرول": roll.roll_number,
      "رقم أمر الإنتاج": roll.production_order_number,
      "رقم الطلب": roll.order_number,
      "العميل": roll.customer_name_ar || roll.customer_name,
      "المنتج": roll.item_name_ar || roll.item_name || "-",
      "المقاس": roll.size_caption || "-",
      "المرحلة": getStageNameAr(roll.stage),
      "الوزن (كجم)": roll.weight_kg,
      "فيلم بواسطة": roll.created_by_name || "-",
      "طبع بواسطة": roll.printed_by_name || "-",
      "قطع بواسطة": roll.cut_by_name || "-",
      "وزن التقطيع": roll.cut_weight_total_kg || "-",
      "الهدر": roll.waste_kg || "-",
      "تاريخ الإنشاء": format(new Date(roll.created_at), "yyyy-MM-dd HH:mm", { locale: ar }),
    }));

    const ws = XLSX.utils.json_to_sheet(data);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "الرولات");
    XLSX.writeFile(wb, `rolls-${format(new Date(), "yyyy-MM-dd")}.xlsx`);
  };

  // مسح الفلاتر
  const clearFilters = () => {
    setSearchTerm("");
    setStageFilter("all");
    setCustomerFilter("all");
    setProductionOrderFilter("all");
    setStartDate(undefined);
    setEndDate(undefined);
  };

  return (
    <div className="space-y-4">
      {/* الإحصائيات السريعة */}
      <div className="grid grid-cols-2 md:grid-cols-6 gap-4">
        <Card>
          <CardHeader className="p-4 pb-2">
            <CardTitle className="text-sm font-medium text-gray-600">الإجمالي</CardTitle>
          </CardHeader>
          <CardContent className="p-4 pt-0">
            <div className="text-2xl font-bold">{formatNumber(stats.total)}</div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="p-4 pb-2">
            <CardTitle className="text-sm font-medium text-gray-600 flex items-center gap-1">
              <Film className="h-4 w-4" />
              فيلم
            </CardTitle>
          </CardHeader>
          <CardContent className="p-4 pt-0">
            <div className="text-2xl font-bold">{formatNumber(stats.byStage.film)}</div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="p-4 pb-2">
            <CardTitle className="text-sm font-medium text-gray-600 flex items-center gap-1">
              <PrinterIcon className="h-4 w-4" />
              طباعة
            </CardTitle>
          </CardHeader>
          <CardContent className="p-4 pt-0">
            <div className="text-2xl font-bold">{formatNumber(stats.byStage.printing)}</div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="p-4 pb-2">
            <CardTitle className="text-sm font-medium text-gray-600 flex items-center gap-1">
              <Scissors className="h-4 w-4" />
              تقطيع
            </CardTitle>
          </CardHeader>
          <CardContent className="p-4 pt-0">
            <div className="text-2xl font-bold">{formatNumber(stats.byStage.cutting)}</div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="p-4 pb-2">
            <CardTitle className="text-sm font-medium text-gray-600 flex items-center gap-1">
              <CheckCircle className="h-4 w-4" />
              منتهي
            </CardTitle>
          </CardHeader>
          <CardContent className="p-4 pt-0">
            <div className="text-2xl font-bold">{formatNumber(stats.byStage.done)}</div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="p-4 pb-2">
            <CardTitle className="text-sm font-medium text-gray-600">إجمالي الوزن</CardTitle>
          </CardHeader>
          <CardContent className="p-4 pt-0">
            <div className="text-xl font-bold">{stats.totalWeight.toFixed(2)} كجم</div>
          </CardContent>
        </Card>
      </div>

      {/* شريط البحث والفلاتر */}
      <Card>
        <CardContent className="p-4">
          <div className="space-y-4">
            {/* صف البحث والأزرار */}
            <div className="flex flex-col md:flex-row gap-3">
              <div className="flex-1 relative">
                <Search className="absolute right-3 top-3 h-4 w-4 text-gray-400" />
                <Input
                  placeholder="ابحث برقم الرول، أمر الإنتاج، الطلب، العميل، أو المنتج..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pr-9"
                  data-testid="input-search-rolls"
                />
              </div>
              <div className="flex gap-2">
                <Button
                  variant="outline"
                  onClick={() => setShowFilters(!showFilters)}
                  data-testid="button-toggle-filters"
                >
                  <Filter className="h-4 w-4 ml-2" />
                  الفلاتر
                </Button>
                <Button
                  variant="outline"
                  onClick={() => refetch()}
                  data-testid="button-refresh-rolls"
                >
                  <RefreshCw className="h-4 w-4 ml-2" />
                  تحديث
                </Button>
                <Button
                  variant="outline"
                  onClick={exportToExcel}
                  disabled={filteredRolls.length === 0}
                  data-testid="button-export-rolls"
                >
                  <Download className="h-4 w-4 ml-2" />
                  تصدير
                </Button>
              </div>
            </div>

            {/* الفلاتر المتقدمة */}
            {showFilters && (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 p-4 bg-gray-50 rounded-lg">
                {/* فلتر المرحلة */}
                <div className="space-y-2">
                  <Label>المرحلة</Label>
                  <Select value={stageFilter} onValueChange={setStageFilter}>
                    <SelectTrigger data-testid="select-stage-filter">
                      <SelectValue placeholder="كل المراحل" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">كل المراحل</SelectItem>
                      <SelectItem value="film">فيلم</SelectItem>
                      <SelectItem value="printing">طباعة</SelectItem>
                      <SelectItem value="cutting">تقطيع</SelectItem>
                      <SelectItem value="done">منتهي</SelectItem>
                      <SelectItem value="archived">مؤرشف</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                {/* فلتر العميل */}
                <div className="space-y-2">
                  <Label>العميل</Label>
                  <Select value={customerFilter} onValueChange={setCustomerFilter}>
                    <SelectTrigger data-testid="select-customer-filter">
                      <SelectValue placeholder="كل العملاء" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">كل العملاء</SelectItem>
                      {customers.map((customer: any) => (
                        <SelectItem key={customer.id} value={customer.id}>
                          {customer.name_ar || customer.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                {/* فلتر أمر الإنتاج */}
                <div className="space-y-2">
                  <Label>أمر الإنتاج</Label>
                  <Select value={productionOrderFilter} onValueChange={setProductionOrderFilter}>
                    <SelectTrigger data-testid="select-production-order-filter">
                      <SelectValue placeholder="كل أوامر الإنتاج" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">كل أوامر الإنتاج</SelectItem>
                      {productionOrders.slice(0, 50).map((po: any) => (
                        <SelectItem key={po.id} value={po.id.toString()}>
                          {po.production_order_number}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                {/* فلتر التاريخ */}
                <div className="space-y-2">
                  <Label>التاريخ</Label>
                  <div className="flex gap-2">
