import { useState, useEffect } from "react";
import { useAuth } from "../../hooks/use-auth";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import {
  Card,
  CardContent,
  CardHeader,
  CardTitle,
  CardDescription,
} from "../ui/card";
import { Button } from "../ui/button";
import { Input } from "../ui/input";
import { Label } from "../ui/label";
import { Switch } from "../ui/switch";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../ui/select";
import { Separator } from "../ui/separator";
import { useToast } from "../../hooks/use-toast";
import { apiRequest } from "../../lib/queryClient";
import {
  User,
  Sun,
  Moon,
  Monitor,
  Save,
  RefreshCw,
  Mail,
  Phone,
  Globe,
} from "lucide-react";

export default function UserProfile() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch user settings
  const { data: userSettingsData } = useQuery({
    queryKey: ["/api/settings/user", user?.id],
    enabled: !!user?.id,
  });

  // User preferences state
  const [userSettings, setUserSettings] = useState({
    displayName: user?.display_name_ar || "",
    email: "",
    phone: "",
    language: "ar",
    theme: "light",
    notifications: {
      email: true,
      sms: false,
      push: true,
      sound: true,
    },
    dashboard: {
      autoRefresh: true,
      refreshInterval: 30,
      compactView: false,
    },
  });

  // Update userSettings when data is fetched
  useEffect(() => {
    if (userSettingsData && Array.isArray(userSettingsData)) {
      const settingsMap = userSettingsData.reduce((acc: any, setting: any) => {
        acc[setting.setting_key] = setting.setting_value;
        return acc;
      }, {});

      setUserSettings((prev) => ({
        ...prev,
        displayName: user?.display_name_ar || settingsMap.displayName || "",
        email: settingsMap.email || "",
        phone: settingsMap.phone || "",
        language: settingsMap.language || "ar",
        theme: settingsMap.theme || "light",
        notifications: {
          email: settingsMap.notificationsEmail === "true",
          sms: settingsMap.notificationsSms === "true",
          push: settingsMap.notificationsPush === "true",
          sound: settingsMap.notificationsSound === "true",
        },
        dashboard: {
          autoRefresh: settingsMap.dashboardAutoRefresh === "true",
          refreshInterval: parseInt(settingsMap.dashboardRefreshInterval || "30"),
          compactView: settingsMap.dashboardCompactView === "true",
        },
      }));
    }
  }, [userSettingsData, user]);

  // Mutation for saving user settings
  const saveUserSettingsMutation = useMutation({
    mutationFn: async (settings: any) => {
      const flattenedSettings = {
        displayName: settings.displayName,
        email: settings.email,
        phone: settings.phone,
        language: settings.language,
        theme: settings.theme,
        notificationsEmail: settings.notifications.email.toString(),
        notificationsSms: settings.notifications.sms.toString(),
        notificationsPush: settings.notifications.push.toString(),
        notificationsSound: settings.notifications.sound.toString(),
        dashboardAutoRefresh: settings.dashboard.autoRefresh.toString(),
        dashboardRefreshInterval: settings.dashboard.refreshInterval.toString(),
        dashboardCompactView: settings.dashboard.compactView.toString(),
      };

      return await apiRequest("/api/settings/user", {
        method: "POST",
        body: JSON.stringify(flattenedSettings),
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({
        queryKey: ["/api/settings/user", user?.id],
      });
      toast({
        title: "تم حفظ الإعدادات بنجاح",
        description: "تم حفظ تفضيلاتك الشخصية",
      });
    },
    onError: () => {
      toast({
        title: "خطأ في حفظ الإعدادات",
        description: "حدث خطأ أثناء حفظ إعداداتك",
        variant: "destructive",
      });
    },
  });

  const handleSaveUserSettings = () => {
    saveUserSettingsMutation.mutate(userSettings);
  };

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <User className="w-5 h-5" />
            الملف الشخصي
          </CardTitle>
          <CardDescription>
            قم بتحديث معلوماتك الشخصية وتفضيلاتك
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Personal Information */}
          <div className="space-y-4">
            <h4 className="text-sm font-medium">المعلومات الشخصية</h4>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="displayName" className="flex items-center gap-2">
                  <User className="w-4 h-4" />
                  الاسم المعروض
                </Label>
                <Input
                  id="displayName"
                  value={userSettings.displayName}
                  onChange={(e) =>
                    setUserSettings((prev) => ({
                      ...prev,
                      displayName: e.target.value,
                    }))
                  }
                  data-testid="input-display-name"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="email" className="flex items-center gap-2">
                  <Mail className="w-4 h-4" />
                  البريد الإلكتروني
                </Label>
                <Input
                  id="email"
                  type="email"
                  value={userSettings.email}
                  onChange={(e) =>
                    setUserSettings((prev) => ({
                      ...prev,
                      email: e.target.value,
                    }))
                  }
                  data-testid="input-email"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="phone" className="flex items-center gap-2">
                  <Phone className="w-4 h-4" />
                  رقم الهاتف
                </Label>
                <Input
                  id="phone"
                  value={userSettings.phone}
                  onChange={(e) =>
                    setUserSettings((prev) => ({
                      ...prev,
                      phone: e.target.value,
                    }))
                  }
                  placeholder="+966 5X XXX XXXX"
                  data-testid="input-phone"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="language" className="flex items-center gap-2">
                  <Globe className="w-4 h-4" />
                  اللغة المفضلة
                </Label>
                <Select
                  value={userSettings.language ?? "ar"}
                  onValueChange={(value) =>
                    setUserSettings((prev) => ({
                      ...prev,
                      language: value,
                    }))
                  }
                >
                  <SelectTrigger data-testid="select-language">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="ar">العربية</SelectItem>
                    <SelectItem value="en">English</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          </div>

          <Separator />

          {/* Appearance Settings */}
          <div className="space-y-4">
            <h4 className="text-sm font-medium">المظهر</h4>
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-2">
                {userSettings.theme === "light" ? (
                  <Sun className="w-4 h-4" />
                ) : (
                  <Moon className="w-4 h-4" />
                )}
                <Label>الوضع الداكن</Label>
              </div>
              <Switch
                checked={userSettings.theme === "dark"}
                onCheckedChange={(checked) =>
                  setUserSettings((prev) => ({
                    ...prev,
                    theme: checked ? "dark" : "light",
                  }))
                }
                data-testid="switch-dark-mode"
              />
            </div>
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-2">
                <Monitor className="w-4 h-4" />
                <Label>العرض المدمج</Label>
              </div>
              <Switch
                checked={userSettings.dashboard.compactView}
                onCheckedChange={(checked) =>
                  setUserSettings((prev) => ({
                    ...prev,
                    dashboard: {
                      ...prev.dashboard,
                      compactView: checked,
                    },
                  }))
                }
                data-testid="switch-compact-view"
              />
            </div>
          </div>

          <Separator />

          {/* Dashboard Settings */}
          <div className="space-y-4">
            <h4 className="text-sm font-medium">إعدادات لوحة التحكم</h4>
            <div className="flex items-center justify-between">
              <div>
                <Label className="text-base">التحديث التلقائي</Label>
                <p className="text-sm text-muted-foreground">
                  تحديث البيانات تلقائياً
                </p>
              </div>
              <Switch
                checked={userSettings.dashboard.autoRefresh}
                onCheckedChange={(checked) =>
                  setUserSettings((prev) => ({
                    ...prev,
                    dashboard: {
                      ...prev.dashboard,
                      autoRefresh: checked,
                    },
                  }))
                }
                data-testid="switch-auto-refresh"
              />
            </div>

            {userSettings.dashboard.autoRefresh && (
              <div className="space-y-2">
                <Label htmlFor="refreshInterval">
                  فترة التحديث (بالثواني)
                </Label>
                <Select
                  value={(
                    userSettings.dashboard.refreshInterval ?? 30
                  ).toString()}
                  onValueChange={(value) =>
                    setUserSettings((prev) => ({
                      ...prev,
                      dashboard: {
                        ...prev.dashboard,
                        refreshInterval: parseInt(value),
                      },
                    }))
                  }
                >
                  <SelectTrigger data-testid="select-refresh-interval">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="15">15 ثانية</SelectItem>
                    <SelectItem value="30">30 ثانية</SelectItem>
                    <SelectItem value="60">دقيقة واحدة</SelectItem>
                    <SelectItem value="300">5 دقائق</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            )}
          </div>

          <div className="flex justify-end">
            <Button
              onClick={handleSaveUserSettings}
              disabled={saveUserSettingsMutation.isPending}
              data-testid="button-save-profile"
            >
              {saveUserSettingsMutation.isPending ? (
                <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
              ) : (
                <Save className="w-4 h-4 mr-2" />
              )}
              حفظ التغييرات
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

server/services/error-learning-enhancer..content:
// ===============================================
// 🔹 Error Learning Enhancer
// ===============================================
// Description: Advanced error learning and pattern recognition
// ===============================================

import { AILearning } from "./ai-learning";

export interface ErrorPattern {
  errorType: string;
  frequency: number;
  lastOccurrence: Date;
  userImpact: "low" | "medium" | "high";
  suggestedFix?: string;
  relatedErrors: string[];
}

export interface LearningRecommendation {
  type: "prevent_error" | "improve_performance" | "enhance_ux";
  priority: "low" | "medium" | "high";
  description: string;
  actionItems: string[];
  expectedImpact: string;
}

export class ErrorLearningEnhancer {
  private static errorHistory: Map<string, ErrorPattern> = new Map();
  private static recommendations: LearningRecommendation[] = [];

  /**
   * تسجيل خطأ والتعلم منه
   */
  static async recordError(
    errorType: string,
    errorMessage: string,
    context: {
      userId?: number;
      action?: string;
      data?: any;
    }
  ): Promise<void> {
    try {
      // تحديث نمط الخطأ
      const pattern = this.errorHistory.get(errorType);

      if (pattern) {
        pattern.frequency++;
        pattern.lastOccurrence = new Date();

        // تحديث مستوى التأثير بناءً على التكرار
        if (pattern.frequency > 10) {
          pattern.userImpact = "high";
        } else if (pattern.frequency > 5) {
          pattern.userImpact = "medium";
        }
      } else {
        this.errorHistory.set(errorType, {
          errorType,
          frequency: 1,
          lastOccurrence: new Date(),
          userImpact: "low",
          relatedErrors: []
        });
      }

      // تسجيل في نظام التعلم العام
      if (context.userId) {
        await AILearning.recordLearningData(
          context.userId,
          context.action || "error_encountered",
          `${errorType}: ${errorMessage}`,
          false,
          0,
          "negative"
        );
      }

      // توليد توصية إذا تكرر الخطأ
      if (pattern && pattern.frequency >= 3) {
        await this.generateErrorRecommendation(errorType, pattern);
      }

      console.log(`⚠️ تم تسجيل خطأ: ${errorType} (التكرار: ${pattern?.frequency || 1})`);
    } catch (error) {
      console.error("Error recording error:", error);
    }
  }

  /**
   * توليد توصية بناءً على نمط الخطأ
   */
  private static async generateErrorRecommendation(
    errorType: string,
    pattern: ErrorPattern
  ): Promise<void> {
    // فحص إذا كان لدينا توصية مسبقة لهذا الخطأ
    const existingRec = this.recommendations.find(
      r => r.description.includes(errorType)
    );

    if (existingRec) {
      // تحديث الأولوية بناءً على التكرار
      if (pattern.frequency > 10) {
        existingRec.priority = "high";
      }
      return;
    }

    // إنشاء توصية جديدة
    const recommendation: LearningRecommendation = {
      type: "prevent_error",
      priority: pattern.userImpact === "high" ? "high" : 
                pattern.userImpact === "medium" ? "medium" : "low",
      description: `تكرر خطأ "${errorType}" ${pattern.frequency} مرة`,
      actionItems: [
        "مراجعة الكود المتعلق بهذا الخطأ",
        "إضافة validationإضافية",
        "تحسين رسائل الخطأ للمستخدم"
      ],
      expectedImpact: `تقليل تكرار هذا الخطأ بنسبة ${Math.min(pattern.frequency * 5, 90)}%`
    };

    this.recommendations.push(recommendation);
    console.log(`💡 تم توليد توصية جديدة لمعالجة: ${errorType}`);
  }

  /**
   * الحصول على أنماط الأخطاء الأكثر شيوعاً
   */
  static getTopErrors(limit: number = 5): ErrorPattern[] {
    return Array.from(this.errorHistory.values())
      .sort((a, b) => b.frequency - a.frequency)
      .slice(0, limit);
  }

  /**
   * الحصول على التوصيات ذات الأولوية العالية
   */
  static getHighPriorityRecommendations(): LearningRecommendation[] {
    return this.recommendations
      .filter(r => r.priority === "high")
      .slice(0, 10);
  }

  /**
   * البحث عن حل لخطأ معين
   */
  static async suggestFix(errorType: string, errorMessage: string): Promise<string> {
    const pattern = this.errorHistory.get(errorType);

    if (pattern && pattern.suggestedFix) {
      return pattern.suggestedFix;
    }

    // استخدام التاريخ للعثور على حلول
    const relatedErrors = Array.from(this.errorHistory.values())
      .filter(p => p.errorType.includes(errorType.split('_')[0]));

    if (relatedErrors.length > 0) {
      return `هذا الخطأ حدث ${pattern?.frequency || 0} مرة سابقاً. جرب:
• التحقق من البيانات المُدخلة
• مراجعة الصلاحيات
• التأكد من اتصال قاعدة البيانات`;
    }

    return "خطأ جديد. يُنصح بفحص السجلات للتفاصيل.";
  }

  /**
   * تحليل أنماط الأخطاء لاكتشاف مشاكل النظام
   */
  static analyzeErrorPatterns(): {
    criticalIssues: string[];
    warningIssues: string[];
    suggestions: string[];
  } {
    const critical: string[] = [];
    const warnings: string[] = [];
    const suggestions: string[] = [];

    this.errorHistory.forEach((pattern, errorType) => {
      if (pattern.userImpact === "high") {
        critical.push(`${errorType}: تكرر ${pattern.frequency} مرة`);
      } else if (pattern.userImpact === "medium") {
        warnings.push(`${errorType}: تكرر ${pattern.frequency} مرة`);
      }

      if (pattern.frequency > 5) {
        suggestions.push(`يُنصح بمعالجة "${errorType}" لتحسين تجربة المستخدم`);
      }
    });

    return {
      criticalIssues: critical,
      warningIssues: warnings,
      suggestions
    };
  }

  /**
   * مسح بيانات الأخطاء القديمة
   */
  static clearOldErrors(daysOld: number = 30): void {
    const cutoffDate = new Date();
    cutoffDate.setDate(cutoffDate.getDate() - daysOld);

    this.errorHistory.forEach((pattern, errorType) => {
      if (pattern.lastOccurrence < cutoffDate) {
        this.errorHistory.delete(errorType);
      }
    });

    console.log(`🧹 تم مسح أخطاء أقدم من ${daysOld} يوم`);
  }

  /**
   * تصدير تقرير الأخطاء
   */
  static generateErrorReport(): {
    totalErrors: number;
    uniqueErrors: number;
    topErrors: ErrorPattern[];
    recommendations: LearningRecommendation[];
    analysis: ReturnType<typeof ErrorLearningEnhancer.analyzeErrorPatterns>;
  } {
    const topErrors = this.getTopErrors(10);
    const analysis = this.analyzeErrorPatterns();

    return {
      totalErrors: Array.from(this.errorHistory.values())
        .reduce((sum, p) => sum + p.frequency, 0),
      uniqueErrors: this.errorHistory.size,
      topErrors,
      recommendations: this.getHighPriorityRecommendations(),
      analysis
    };
  }

  /**
   * التعلم من نجاحات سابقة لمنع أخطاء مستقبلية
   */
  static async learnFromSuccess(
    action: string,
    context: any,
    userId?: number
  ): Promise<void> {
    // تسجيل النجاح
    if (userId) {
      await AILearning.recordLearningData(
        userId,
        action,
        JSON.stringify(context),
        true,
        0,
        "positive"
      );
    }

    // البحث عن أخطاء مماثلة وتحديث الحلول المقترحة
    const relatedErrorTypes = Array.from(this.errorHistory.keys())
      .filter(errorType => errorType.includes(action.split('_')[0]));

    for (const errorType of relatedErrorTypes) {
      const pattern = this.errorHistory.get(errorType);
      if (pattern && !pattern.suggestedFix) {
        pattern.suggestedFix = `جرب الطريقة التي نجحت في: ${action}`;
      }
    }
  }
}

// تنظيف دوري للأخطاء القديمة (كل 24 ساعة)
setInterval(() => {
  ErrorLearningEnhancer.clearOldErrors(30);
}, 24 * 60 * 60 * 1000);

export default ErrorLearningEnhancer;

