  async getOrdersEnhanced(filters: {
    search?: string;
    customer_id?: string;
    status?: string;
    date_from?: string;
    date_to?: string;
    page?: number;
    limit?: number;
  }): Promise<any> {
    return await withDatabaseErrorHandling(async () => {
      let query = db
        .select({
          // Order fields
          id: orders.id,
          order_number: orders.order_number,
          customer_id: orders.customer_id,
