}

.mobile-nav-item.active {
  @apply text-primary;
}

.mobile-nav-item:not(.active) {
  @apply text-gray-600;
}

/* Production workflow enhancements */
.production-tabs {
  @apply border-b border-gray-200;
}

.production-tab {
  @apply border-b-2 border-transparent py-4 px-1 text-sm font-medium transition-colors;
}

.production-tab.active {
  @apply border-primary text-primary;
}

.production-tab:not(.active) {
  @apply text-gray-500 hover:text-gray-700;
}

/* Form enhancements */
.form-input {
  @apply w-full border border-gray-300 rounded-lg px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition-colors;
}

.form-input.rtl {
  @apply text-right;
}

.form-input.rtl::placeholder {
  text-align: right;
}

.form-label {
  @apply block text-sm font-medium text-gray-700 mb-2;
}

.form-select {
  @apply form-input appearance-none;
}

/* Modal enhancements */
.modal-overlay {
  @apply fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center z-50;
}

.modal-content {
  @apply bg-white rounded-lg shadow-xl max-w-md w-full mx-4;
}

.modal-header {
  @apply px-6 py-4 border-b border-gray-200;
}

.modal-body {
  @apply p-6;
}

.modal-footer {
  @apply px-6 py-4 border-t border-gray-200 flex justify-end;
}

.modal-footer > * + * {
  margin-left: 0.75rem;
}

[dir="rtl"] .modal-footer > * + * {
  margin-left: 0;
  margin-right: 0.75rem;
}

/* Accessibility improvements */
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

/* Skip to content link for screen readers */
.skip-to-content {
  @apply sr-only focus:not-sr-only focus:absolute focus:top-4 focus:left-4 focus:z-50 focus:px-4 focus:py-2 focus:bg-primary focus:text-white focus:rounded-lg;
}

/* High contrast mode adjustments */
@media (prefers-contrast: high) {
  .btn-primary,
  .btn-success,
  .btn-warning,
  .btn-danger {
    border: 2px solid currentColor;
  }

  .nav-item-active {
    border: 2px solid currentColor;
  }
}

/* Animation utilities for micro-interactions */
.hover-lift {
  @apply transition-transform duration-200 hover:translate-y-[-2px];
}

.hover-scale {
  @apply transition-transform duration-200 hover:scale-105;
}

.pulse-ring {
  @apply animate-ping absolute inline-flex h-full w-full rounded-full opacity-75;
}

/* Custom gradient backgrounds */
.gradient-primary {
  background: linear-gradient(
    135deg,
    hsl(var(--primary-600)),
    hsl(var(--primary-700))
  );
}

.gradient-success {
  background: linear-gradient(
    135deg,
    hsl(var(--success-500)),
    hsl(var(--success-600))
  );
}

.gradient-warning {
  background: linear-gradient(
    135deg,
    hsl(var(--warning-500)),
    hsl(var(--warning-600))
  );
}

.gradient-danger {
  background: linear-gradient(
    135deg,
    hsl(var(--danger-500)),
    hsl(var(--danger-600))
  );
}

