  async validateStatusTransition(
    tableName: string,
    currentStatus: string,
    newStatus: string,
    entityId: number,
  ): Promise<ValidationResult> {
    console.log(
      `[DataValidator] ğŸ”„ Validating status transition for ${tableName}:`,
