      }

      console.log(
        `[DataValidator] âœ… Valid status transition: ${currentStatus} â†’ ${newStatus}`,
      );
      return { isValid: true, errors, warnings };
    } catch (error) {
      console.error(
        "[DataValidator] Error validating status transition:",
        error,
      );
      errors.push({
